<node CREATED="1430842679681" FOLDED="true" ID="ID_179175344" MODIFIED="1456321592057" TEXT="java.util.zip &#x4e2d;&#x7684;&#x4ee3;&#x7801;">
<node CREATED="1430842335314" ID="ID_850854944" MODIFIED="1430842585352" TEXT="public static void unzip(String zipFile, String location) throws IOException {&#xa;    try {&#xa;        File f = new File(location);&#xa;        if(!f.isDirectory()) {&#xa;            f.mkdirs();&#xa;        }&#xa;        ZipInputStream zin = new ZipInputStream(new FileInputStream(zipFile));&#xa;        try {&#xa;            ZipEntry ze = null;&#xa;            while ((ze = zin.getNextEntry()) != null) {&#xa;                String path = location + ze.getName();&#xa;&#xa;                if (ze.isDirectory()) {&#xa;                    File unzipFile = new File(path);&#xa;                    if(!unzipFile.isDirectory()) {&#xa;                        unzipFile.mkdirs();&#xa;                    }&#xa;                }&#xa;                else {&#xa;                    FileOutputStream fout = new FileOutputStream(path, false);&#xa;                    try {&#xa;                        for (int c = zin.read(); c != -1; c = zin.read()) {&#xa;                            fout.write(c);&#xa;                        }&#xa;                        zin.closeEntry();&#xa;                    }&#xa;                    finally {&#xa;                        fout.close();&#xa;                    }&#xa;                }&#xa;            }&#xa;        }&#xa;        finally {&#xa;            zin.close();&#xa;        }&#xa;    }&#xa;    catch (Exception e) {&#xa;        Log.e(TAG, &quot;Unzip exception&quot;, e);&#xa;    }&#xa;}"/>
</node>
<node CREATED="1430842726063" FOLDED="true" ID="ID_139674346" MODIFIED="1456321592057" TEXT="&#x6539;&#x8fdb; &#x4f7f;&#x7528; buffer">
<node CREATED="1430842795261" ID="ID_1935939854" MODIFIED="1430842801134" TEXT="/**&#xa; * Unzip a zip file.  Will overwrite existing files.&#xa; * &#xa; * @param zipFile Full path of the zip file you&apos;d like to unzip.&#xa; * @param location Full path of the directory you&apos;d like to unzip to (will be created if it doesn&apos;t exist).&#xa; * @throws IOException&#xa; */&#xa;public static void unzip(String zipFile, String location) throws IOException {&#xa;    int size;&#xa;    byte[] buffer = new byte[BUFFER_SIZE];&#xa;&#xa;    try {&#xa;        if ( !location.endsWith(&quot;/&quot;) ) {&#xa;            location += &quot;/&quot;;&#xa;        }&#xa;        File f = new File(location);&#xa;        if(!f.isDirectory()) {&#xa;            f.mkdirs();&#xa;        }&#xa;        ZipInputStream zin = new ZipInputStream(new BufferedInputStream(new FileInputStream(zipFile), BUFFER_SIZE));&#xa;        try {&#xa;            ZipEntry ze = null;&#xa;            while ((ze = zin.getNextEntry()) != null) {&#xa;                String path = location + ze.getName();&#xa;                File unzipFile = new File(path);&#xa;&#xa;                if (ze.isDirectory()) {&#xa;                    if(!unzipFile.isDirectory()) {&#xa;                        unzipFile.mkdirs();&#xa;                    }&#xa;                } else {&#xa;                    // check for and create parent directories if they don&apos;t exist&#xa;                    File parentDir = unzipFile.getParentFile();&#xa;                    if ( null != parentDir ) {&#xa;                        if ( !parentDir.isDirectory() ) {&#xa;                            parentDir.mkdirs();&#xa;                        }&#xa;                    }&#xa;&#xa;                    // unzip the file&#xa;                    FileOutputStream out = new FileOutputStream(unzipFile, false);&#xa;                    BufferedOutputStream fout = new BufferedOutputStream(out, BUFFER_SIZE);&#xa;                    try {&#xa;                        while ( (size = zin.read(buffer, 0, BUFFER_SIZE)) != -1 ) {&#xa;                            fout.write(buffer, 0, size);&#xa;                        }&#xa;&#xa;                        zin.closeEntry();&#xa;                    }&#xa;                    finally {&#xa;                        fout.flush();&#xa;                        fout.close();&#xa;                    }&#xa;                }&#xa;            }&#xa;        }&#xa;        finally {&#xa;            zin.close();&#xa;        }&#xa;    }&#xa;    catch (Exception e) {&#xa;        Log.e(TAG, &quot;Unzip exception&quot;, e);&#xa;    }&#xa;}&#xa;"/>
</node>
</node>
