<node CREATED="1430843349031" ID="ID_357045" MODIFIED="1430843358696" TEXT="package com.blarg.webviewscroller;&#xa;&#xa;import java.io.BufferedInputStream;&#xa;import java.io.BufferedOutputStream;&#xa;import java.io.File;&#xa;import java.io.FileOutputStream;&#xa;import java.io.IOException;&#xa;import java.util.Enumeration;&#xa;import java.util.Observable;&#xa;import java.util.zip.ZipEntry;&#xa;import java.util.zip.ZipFile;&#xa;&#xa;import org.apache.commons.io.IOUtils;&#xa;&#xa;import android.os.AsyncTask;&#xa;import android.util.Log;&#xa;&#xa;public class UnZipper extends Observable {&#xa;&#xa;    private static final String TAG = &quot;UnZip&quot;;&#xa;    private String mFileName, mFilePath, mDestinationPath;&#xa;&#xa;    public UnZipper (String fileName, String filePath, String destinationPath) {&#xa;        mFileName = fileName;&#xa;        mFilePath = filePath;&#xa;        mDestinationPath = destinationPath;&#xa;    }&#xa;&#xa;    public String getFileName () {&#xa;        return mFileName;&#xa;    }&#xa;&#xa;    public String getFilePath() {&#xa;        return mFilePath;&#xa;    }&#xa;&#xa;    public String getDestinationPath () {&#xa;        return mDestinationPath;&#xa;    }&#xa;&#xa;    public void unzip () {&#xa;        String fullPath = mFilePath + &quot;/&quot; + mFileName + &quot;.zip&quot;;&#xa;        Log.d(TAG, &quot;unzipping &quot; + mFileName + &quot; to &quot; + mDestinationPath);&#xa;        new UnZipTask().execute(fullPath, mDestinationPath);&#xa;    }&#xa;&#xa;    private class UnZipTask extends AsyncTask&lt;String, Void, Boolean&gt; {&#xa;&#xa;        @SuppressWarnings(&quot;rawtypes&quot;)&#xa;        @Override&#xa;        protected Boolean doInBackground(String... params) {&#xa;            String filePath = params[0];&#xa;            String destinationPath = params[1];&#xa;&#xa;            File archive = new File(filePath);&#xa;            try {&#xa;                ZipFile zipfile = new ZipFile(archive);&#xa;                for (Enumeration e = zipfile.entries(); e.hasMoreElements();) {&#xa;                    ZipEntry entry = (ZipEntry) e.nextElement();&#xa;                    unzipEntry(zipfile, entry, destinationPath);&#xa;                }&#xa;            } catch (Exception e) {&#xa;                Log.e(TAG, &quot;Error while extracting file &quot; + archive, e);&#xa;                return false;&#xa;            }&#xa;&#xa;            return true;&#xa;        }&#xa;&#xa;        @Override&#xa;        protected void onPostExecute(Boolean result) {&#xa;            setChanged();&#xa;            notifyObservers();&#xa;        }&#xa;&#xa;        private void unzipEntry(ZipFile zipfile, ZipEntry entry,&#xa;                String outputDir) throws IOException {&#xa;&#xa;            if (entry.isDirectory()) {&#xa;                createDir(new File(outputDir, entry.getName()));&#xa;                return;&#xa;            }&#xa;&#xa;            File outputFile = new File(outputDir, entry.getName());&#xa;            if (!outputFile.getParentFile().exists()) {&#xa;                createDir(outputFile.getParentFile());&#xa;            }&#xa;&#xa;            Log.v(TAG, &quot;Extracting: &quot; + entry);&#xa;            BufferedInputStream inputStream = new BufferedInputStream(zipfile.getInputStream(entry));&#xa;            BufferedOutputStream outputStream = new BufferedOutputStream(new FileOutputStream(outputFile));&#xa;&#xa;            try {&#xa;                IOUtils.copy(inputStream, outputStream);&#xa;            } finally {&#xa;                outputStream.close();&#xa;                inputStream.close();&#xa;            }&#xa;        }&#xa;&#xa;        private void createDir(File dir) {&#xa;            if (dir.exists()) {&#xa;                return;&#xa;            }&#xa;            Log.v(TAG, &quot;Creating dir &quot; + dir.getName());&#xa;            if (!dir.mkdirs()) {&#xa;                throw new RuntimeException(&quot;Can not create dir &quot; + dir);&#xa;            }&#xa;        }&#xa;    }&#xa;} "/>
</node>
