<node CREATED="1430843428791" ID="ID_109610679" MODIFIED="1430843440212" TEXT="static Handler myHandler;&#xa;ProgressDialog myProgress;&#xa;&#xa;public void unzipFile(File zipfile) {&#xa;        myProgress = ProgressDialog.show(getContext(), &quot;Extract Zip&quot;,&#xa;                        &quot;Extracting Files...&quot;, true, false);&#xa;        File zipFile = zipfile;&#xa;        String directory = null;&#xa;        directory = zipFile.getParent();&#xa;        directory = directory + &quot;/&quot;;&#xa;        myHandler = new Handler() {&#xa;&#xa;                @Override&#xa;                public void handleMessage(Message msg) {&#xa;                        // process incoming messages here&#xa;                        switch (msg.what) {&#xa;                        case 0:&#xa;                                // update progress bar&#xa;                                myProgress.setMessage(&quot;&quot; + (String) msg.obj);&#xa;                                break;&#xa;                        case 1:&#xa;                                myProgress.cancel();&#xa;                                Toast toast = Toast.makeText(getContext(),&#xa;                                                &quot;Zip extracted successfully&quot;, &#xa;Toast.LENGTH_SHORT);&#xa;                                toast.show();&#xa;                                provider.refresh();&#xa;                                break;&#xa;                        case 2:&#xa;                                myProgress.cancel();&#xa;                                break;&#xa;                        }&#xa;                        super.handleMessage(msg);&#xa;                }&#xa;&#xa;        };&#xa;        Thread workthread = new Thread(new UnZip(zipFile, directory));&#xa;        workthread.start();&#xa;}&#xa;&#xa;public class UnZip implements Runnable {&#xa;&#xa;        File archive;&#xa;        String outputDir;&#xa;&#xa;        public UnZip(File ziparchive, String directory) {&#xa;                archive = ziparchive;&#xa;                outputDir = directory;&#xa;        }&#xa;&#xa;        public void log(String log) {&#xa;                Log.v(&quot;unzip&quot;, log);&#xa;        }&#xa;&#xa;        @SuppressWarnings(&quot;unchecked&quot;)&#xa;        public void run() {&#xa;                Message msg;&#xa;                try {&#xa;                        ZipFile zipfile = new ZipFile(archive);&#xa;                        for (Enumeration e = zipfile.entries(); &#xa;e.hasMoreElements();) {&#xa;                                ZipEntry entry = (ZipEntry) e.nextElement();&#xa;                                msg = new Message();&#xa;                                msg.what = 0;&#xa;                                msg.obj = &quot;Extracting &quot; + entry.getName();&#xa;                                myHandler.sendMessage(msg);&#xa;                                unzipEntry(zipfile, entry, outputDir);&#xa;                        }&#xa;                } catch (Exception e) {&#xa;                        log(&quot;Error while extracting file &quot; + archive);&#xa;                }&#xa;                msg = new Message();&#xa;                msg.what = 1;&#xa;                myHandler.sendMessage(msg);&#xa;        }&#xa;&#xa;        @SuppressWarnings(&quot;unchecked&quot;)&#xa;        public void unzipArchive(File archive, String outputDir) {&#xa;                try {&#xa;                        ZipFile zipfile = new ZipFile(archive);&#xa;                        for (Enumeration e = zipfile.entries(); &#xa;e.hasMoreElements();) {&#xa;                                ZipEntry entry = (ZipEntry) e.nextElement();&#xa;                                unzipEntry(zipfile, entry, outputDir);&#xa;                        }&#xa;                } catch (Exception e) {&#xa;                        log(&quot;Error while extracting file &quot; + archive);&#xa;                }&#xa;        }&#xa;&#xa;        private void unzipEntry(ZipFile zipfile, ZipEntry entry,&#xa;                        String outputDir) throws IOException {&#xa;&#xa;                if (entry.isDirectory()) {&#xa;                        createDir(new File(outputDir, entry.getName()));&#xa;                        return;&#xa;                }&#xa;&#xa;                File outputFile = new File(outputDir, entry.getName());&#xa;                if (!outputFile.getParentFile().exists()) {&#xa;                        createDir(outputFile.getParentFile());&#xa;                }&#xa;&#xa;                log(&quot;Extracting: &quot; + entry);&#xa;                BufferedInputStream inputStream = new &#xa;BufferedInputStream(zipfile&#xa;                                .getInputStream(entry));&#xa;                BufferedOutputStream outputStream = new BufferedOutputStream(&#xa;                                new FileOutputStream(outputFile));&#xa;&#xa;                try {&#xa;                        IOUtils.copy(inputStream, outputStream);&#xa;                } finally {&#xa;                        outputStream.close();&#xa;                        inputStream.close();&#xa;                }&#xa;        }&#xa;&#xa;        private void createDir(File dir) {&#xa;                log(&quot;Creating dir &quot; + dir.getName());&#xa;                if (!dir.mkdirs())&#xa;                        throw new RuntimeException(&quot;Can not create dir &quot; + dir);&#xa;        }&#xa;}"/>
