<node CREATED="1454430403650" ID="ID_1843836366" MODIFIED="1454430408681" TEXT=" ShaderGlow(){&#xa;        super();&#xa;        final String vertexShader =&#xa;//        &quot;#define PROCESSING_LINE_SHADER&quot;+&#xa;        &quot;uniform mat4 uMVPMatrix;&quot;+&#xa;        &quot;uniform vec4 uViewport;      &quot;+&#xa;&#xa;        &quot;attribute vec4 aPosition;      &quot;+&#xa;//        &quot;attribute vec4 color;       &quot;+&#xa;        &quot;attribute vec4 direction;   &quot;+&#xa;&#xa;        &quot;varying vec2 center;        &quot;+&#xa;        &quot;varying vec2 normal;        &quot;+&#xa;        &quot;varying vec4 vertColor;     &quot;+&#xa;&#xa;        &quot;vec3 clipToWindow(vec4 clip, vec4 viewport) {                       &quot;+&#xa;        &quot;    vec3 dclip = clip.xyz / clip.w;                                 &quot;+&#xa;        &quot;    vec2 xypos = (dclip.xy + vec2(1.0, 1.0)) * 0.5 * viewport.zw;   &quot;+&#xa;        &quot;    return vec3(xypos, dclip.z * 0.5 + 0.5);                        &quot;+&#xa;        &quot;} &quot;+&#xa;&#xa;        &quot;void main() {   &quot;+&#xa;        &quot;    vec4 clip0 = uMVPMatrix * aPosition;    &quot;+&#xa;        &quot;    vec4 clip1 = clip0 + uMVPMatrix * vec4(direction.xyz, 0);    &quot;+&#xa;        &quot;    float thickness = direction.w;  &quot;+&#xa;&#xa;        &quot;    vec3 win0 = clipToWindow(clip0, uViewport);  &quot;+&#xa;        &quot;    vec3 win1 = clipToWindow(clip1, uViewport);  &quot;+&#xa;        &quot;    vec2 tangent = win1.xy - win0.xy;           &quot;+&#xa;&#xa;        &quot;    normal = normalize(vec2(-tangent.y, tangent.x));    &quot;+&#xa;        &quot;    vec2 offset = normal * thickness;                   &quot;+&#xa;        &quot;    gl_Position.xy = clip0.xy + offset.xy;              &quot;+&#xa;        &quot;    gl_Position.zw = clip0.zw;                          &quot;+&#xa;        &quot;    vertColor = vec4(1.0, 1.0, 1.0, 1.0);                                  &quot;+&#xa;&#xa;        &quot;    center = (win0.xy + win1.xy) / 2.0;                 &quot;+&#xa;        &quot;} &quot;;&#xa;&#xa;        final String fragmentShader =&#xa;        &quot;precision mediump float;&quot;+&#xa;        &quot;precision mediump int;  &quot;+&#xa;&#xa;        &quot;uniform float uWeight;   &quot;+&#xa;&#xa;        &quot;varying vec2 center;    &quot;+&#xa;        &quot;varying vec2 normal;    &quot;+&#xa;        &quot;varying vec4 vertColor; &quot;+&#xa;&#xa;        &quot;void main() {           &quot;+&#xa;        &quot;    vec2 v = gl_FragCoord.xy - center;  &quot;+&#xa;        &quot;    float alpha = 1.0 - abs(2.0 * dot(normalize(normal), v) / uWeight);  &quot;+&#xa;        &quot;    gl_FragColor = vec4(vertColor.rgb, alpha);  &quot;+&#xa;        &quot;}&quot;;&#xa;"/>
