<node CREATED="1457092079185" ID="ID_1856793984" MODIFIED="1457092079695" TEXT="or &quot;never|collapseActionView&quot;"/>
<node CREATED="1457092190431" FOLDED="true" ID="ID_53689014" MODIFIED="1457520381796" TEXT="&#x8bf4;&#x660e;">
<node CREATED="1457092088332" FOLDED="true" ID="ID_1758724219" MODIFIED="1457520381795" TEXT="collapseActionView flag indicates&#xa;how to display the widget when &#xa;the user is not interacting with it">
<node CREATED="1457092142549" ID="ID_1152135457" MODIFIED="1457092148230" TEXT=" If the widget is on the app bar, the &#xa;app should display the widget as an icon."/>
<node CREATED="1457092158739" ID="ID_119500028" MODIFIED="1457092165433" TEXT="If the widget is in the overflow menu, &#xa;the app should display the widget as&#xa; a menu item."/>
