</node>
<node CREATED="1457092379811" FOLDED="true" ID="ID_1111372035" MODIFIED="1457520381796" TEXT="If the user is not interacting with the widget,">
<node CREATED="1457092388809" ID="ID_1193427057" MODIFIED="1457092393238" TEXT="the app displays the widget as the&#xa; icon specified by android:icon"/>
<node CREATED="1457092417601" ID="ID_1757824421" MODIFIED="1457092421854" TEXT="If there is not enough room in the app bar,&#xa; the app adds the action to the overflow menu."/>
</node>
<node CREATED="1457092439283" FOLDED="true" ID="ID_243588989" MODIFIED="1457520381796" TEXT="When the user taps the icon or menu item">
<node CREATED="1457092447123" ID="ID_1381509222" MODIFIED="1457092451747" TEXT="the widget expands to fill the toolbar,&#xa; allowing the user to interact with it."/>
</node>
</node>
<node CREATED="1457092473921" FOLDED="true" ID="ID_972989689" MODIFIED="1457520381797" TEXT="configure the action">
<node CREATED="1457092510408" ID="ID_638401627" MODIFIED="1457092510851" TEXT=" in your activity&apos;s onCreateOptionsMenu()"/>
</node>
<node CREATED="1457092539355" FOLDED="true" ID="ID_1816788448" MODIFIED="1457520381797" TEXT="get the action view&apos;s object reference">
<node CREATED="1457092544975" ID="ID_1156773617" MODIFIED="1457092545346" TEXT="calling the static getActionView() method"/>
<node CREATED="1457092594843" FOLDED="true" ID="ID_1170058343" MODIFIED="1457520381797" TEXT="&#x4f8b;&#x5b50;&#xff1a;gets the object reference of &#xa;&#x4e0a;&#x9762;&#x4f8b;&#x5b50;&#x4e2d;&#x7684; SearchView">
<node CREATED="1457092633850" ID="ID_1072443220" MODIFIED="1457092637078" TEXT="@Override&#xa;public boolean onCreateOptionsMenu(Menu menu) {&#xa;    getMenuInflater().inflate(R.menu.main_activity_actions, menu);&#xa;&#xa;    MenuItem searchItem = menu.findItem(R.id.action_search);&#xa;    SearchView searchView =&#xa;            (SearchView) MenuItemCompat.getActionView(searchItem);&#xa;&#xa;    // Configure the search info and add any event listeners...&#xa;&#xa;    return super.onCreateOptionsMenu(menu);&#xa;}"/>
</node>
</node>
</node>
<node CREATED="1457092669788" FOLDED="true" ID="ID_811703606" MODIFIED="1457520381800" TEXT="Responding to action view expansion">
<node CREATED="1457092816164" ID="ID_1257651164" MODIFIED="1457092859839" TEXT="If the action&apos;s &lt;item&gt; element has a &#xa;collapseActionView flag, the app displays &#xa;the action view as an icon until the user &#xa;interacts with the action view"/>
<node CREATED="1457092865521" FOLDED="true" ID="ID_1813346314" MODIFIED="1457520381799" TEXT="When the user clicks on the icon">
<node CREATED="1457092875139" FOLDED="true" ID="ID_760143410" MODIFIED="1457520381798" TEXT="built-in handler for &#xa;onOptionsItemSelected() &#xa;expands the action view">
<node CREATED="1457092890670" FOLDED="true" ID="ID_360448696" MODIFIED="1457520381798" TEXT="&#x5982;&#x679c; ubclass overrides the &#xa;onOptionsItemSelected()">
<node CREATED="1457092906766" ID="ID_613936426" MODIFIED="1457092907320" TEXT="must call super.onOptionsItemSelected()"/>
</node>
</node>
</node>
<node CREATED="1457092938110" FOLDED="true" ID="ID_467345637" MODIFIED="1457520381800" TEXT="If you want to do something when&#xa; the action is expanded or collapsed">
<node CREATED="1457092947683" ID="ID_270737359" LINK="http://developer.android.com/reference/android/view/MenuItem.OnActionExpandListener.html" MODIFIED="1457092954669" TEXT=" implements MenuItem.OnActionExpandListener"/>
<node CREATED="1457092970218" FOLDED="true" ID="ID_1250907382" MODIFIED="1457520381799" TEXT="pass a member of that class to &#xa;setOnActionExpandListener()">
<node CREATED="1457092985250" ID="ID_1234325532" LINK="http://developer.android.com/reference/android/view/MenuItem.html#setOnActionExpandListener(android.view.MenuItem.OnActionExpandListener)" MODIFIED="1457092989657" TEXT="setOnActionExpandListener()"/>
</node>
<node CREATED="1457093027900" FOLDED="true" ID="ID_1189487115" MODIFIED="1457520381799" TEXT="&#x4f8b;&#x5b50;&#xff1a;">
<node CREATED="1457093033717" FOLDED="true" ID="ID_832398447" MODIFIED="1457520381799" TEXT=" update the activity based on whether &#xa;an action view is expanded or collapsed">
<node CREATED="1457093053305" ID="ID_984442727" MODIFIED="1457093055790" TEXT="@Override&#xa;public boolean onCreateOptionsMenu(Menu menu) {&#xa;    getMenuInflater().inflate(R.menu.options, menu);&#xa;    // ...&#xa;&#xa;    // Define the listener&#xa;    OnActionExpandListener expandListener = new OnActionExpandListener() {&#xa;        @Override&#xa;        public boolean onMenuItemActionCollapse(MenuItem item) {&#xa;            // Do something when action item collapses&#xa;            return true;  // Return true to collapse action view&#xa;        }&#xa;&#xa;        @Override&#xa;        public boolean onMenuItemActionExpand(MenuItem item) {&#xa;            // Do something when expanded&#xa;            return true;  // Return true to expand action view&#xa;        }&#xa;    };&#xa;&#xa;    // Get the MenuItem for the action item&#xa;    MenuItem actionMenuItem = menu.findItem(R.id.myActionItem);&#xa;&#xa;    // Assign the listener to that action item&#xa;    MenuItemCompat.setOnActionExpandListener(actionMenuItem, expandListener);&#xa;&#xa;    // Any other things you have to do when creating the options menu&#x2026;&#xa;&#xa;    return true;&#xa;}"/>
</node>
</node>
</node>
</node>
</node>
