<node CREATED="1437657444134" FOLDED="true" ID="ID_963767124" MODIFIED="1457520381895" TEXT="&#x5b9a;&#x4e49; interface &#x7684; DialogFragment">
<node CREATED="1437657491401" FOLDED="true" ID="ID_1397793593" MODIFIED="1457520381895" TEXT="&#x4ee3;&#x7801;">
<node CREATED="1437657522948" ID="ID_426281508" MODIFIED="1437657526253" TEXT="public class NoticeDialogFragment extends DialogFragment {&#xa;    &#xa;    /* The activity that creates an instance of this dialog fragment must&#xa;     * implement this interface in order to receive event callbacks.&#xa;     * Each method passes the DialogFragment in case the host needs to query it. */&#xa;    public interface NoticeDialogListener {&#xa;        public void onDialogPositiveClick(DialogFragment dialog);&#xa;        public void onDialogNegativeClick(DialogFragment dialog);&#xa;    }&#xa;    &#xa;    // Use this instance of the interface to deliver action events&#xa;    NoticeDialogListener mListener;&#xa;    &#xa;    // Override the Fragment.onAttach() method to instantiate the NoticeDialogListener&#xa;    @Override&#xa;    public void onAttach(Activity activity) {&#xa;        super.onAttach(activity);&#xa;        // Verify that the host activity implements the callback interface&#xa;        try {&#xa;            // Instantiate the NoticeDialogListener so we can send events to the host&#xa;            mListener = (NoticeDialogListener) activity;&#xa;        } catch (ClassCastException e) {&#xa;            // The activity doesn&apos;t implement the interface, throw exception&#xa;            throw new ClassCastException(activity.toString()&#xa;                    + &quot; must implement NoticeDialogListener&quot;);&#xa;        }&#xa;    }&#xa;    ...&#xa;}"/>
</node>
</node>
</node>
<node CREATED="1437657435172" FOLDED="true" ID="ID_51829193" MODIFIED="1457520381896" TEXT="implement that interface in the &#xa;host component">
<node CREATED="1437657598941" FOLDED="true" ID="ID_1320029626" MODIFIED="1457520381895" TEXT="hosting dialog &#x7684; activity &#x751f;&#x6210;&#xa;dialog fragment &#x7684; consturctor&#xa;&#x6536;&#x5230; dialog &#x7684; events">
<node CREATED="1437657642026" ID="ID_1619734127" MODIFIED="1437657644568" TEXT="public class MainActivity extends FragmentActivity&#xa;                          implements NoticeDialogFragment.NoticeDialogListener{&#xa;    ...&#xa;    &#xa;    public void showNoticeDialog() {&#xa;        // Create an instance of the dialog fragment and show it&#xa;        DialogFragment dialog = new NoticeDialogFragment();&#xa;        dialog.show(getSupportFragmentManager(), &quot;NoticeDialogFragment&quot;);&#xa;    }&#xa;&#xa;    // The dialog fragment receives a reference to this Activity through the&#xa;    // Fragment.onAttach() callback, which it uses to call the following methods&#xa;    // defined by the NoticeDialogFragment.NoticeDialogListener interface&#xa;    @Override&#xa;    public void onDialogPositiveClick(DialogFragment dialog) {&#xa;        // User touched the dialog&apos;s positive button&#xa;        ...&#xa;    }&#xa;&#xa;    @Override&#xa;    public void onDialogNegativeClick(DialogFragment dialog) {&#xa;        // User touched the dialog&apos;s negative button&#xa;        ...&#xa;    }&#xa;}"/>
</node>
</node>
<node CREATED="1437657757930" FOLDED="true" ID="ID_1055966678" MODIFIED="1457520381896" TEXT="&#x2014;the dialog fragment can use the&#xa; interface callback methods to deliver&#xa; click events to the activity">
<node CREATED="1437657776955" ID="ID_1232950333" MODIFIED="1437657782403" TEXT="public class NoticeDialogFragment extends DialogFragment {&#xa;    ...&#xa;&#xa;    @Override&#xa;    public Dialog onCreateDialog(Bundle savedInstanceState) {&#xa;        // Build the dialog and set up the button click handlers&#xa;        AlertDialog.Builder builder = new AlertDialog.Builder(getActivity());&#xa;        builder.setMessage(R.string.dialog_fire_missiles)&#xa;               .setPositiveButton(R.string.fire, new DialogInterface.OnClickListener() {&#xa;                   public void onClick(DialogInterface dialog, int id) {&#xa;                       // Send the positive button event back to the host activity&#xa;                       mListener.onDialogPositiveClick(NoticeDialogFragment.this);&#xa;                   }&#xa;               })&#xa;               .setNegativeButton(R.string.cancel, new DialogInterface.OnClickListener() {&#xa;                   public void onClick(DialogInterface dialog, int id) {&#xa;                       // Send the negative button event back to the host activity&#xa;                       mListener.onDialogNegativeClick(NoticeDialogFragment.this);&#xa;                   }&#xa;               });&#xa;        return builder.create();&#xa;    }&#xa;}"/>
</node>
</node>
</node>
