<node CREATED="1437081575373" ID="ID_1868697136" MODIFIED="1437081575752" TEXT=" used by the parent to impose an exact size on the child"/>
<node CREATED="1437081581170" ID="ID_125760428" MODIFIED="1437081581887" TEXT="child must use this size"/>
<node CREATED="1437081590833" ID="ID_1711906110" MODIFIED="1437081591210" TEXT="guarantee that all of its descendants will fit within this size."/>
</node>
<node CREATED="1437081596018" FOLDED="true" ID="ID_1618017282" MODIFIED="1457520382025" TEXT="AT_MOST">
<node CREATED="1437081603662" ID="ID_1796792166" MODIFIED="1437081628543" TEXT="used by the parent to impose&#xa; a maximum size on the child"/>
<node CREATED="1437081615259" ID="ID_300332299" MODIFIED="1437081623015" TEXT="child must guarantee that it and all of &#xa;its descendants will fit within this size"/>
</node>
</node>
</node>
