<node CREATED="1437662614125" FOLDED="true" ID="ID_982502246" MODIFIED="1457520382090" TEXT="&#x4ee3;&#x7801;">
<node CREATED="1437662619605" ID="ID_1113264468" MODIFIED="1437662626062" TEXT="public static class MyDialogFragment extends DialogFragment {&#xa;    int mNum;&#xa;&#xa;    /**&#xa;     * Create a new instance of MyDialogFragment, providing &quot;num&quot;&#xa;     * as an argument.&#xa;     */&#xa;    static MyDialogFragment newInstance(int num) {&#xa;        MyDialogFragment f = new MyDialogFragment();&#xa;&#xa;        // Supply num input as an argument.&#xa;        Bundle args = new Bundle();&#xa;        args.putInt(&quot;num&quot;, num);&#xa;        f.setArguments(args);&#xa;&#xa;        return f;&#xa;    }&#xa;&#xa;    @Override&#xa;    public void onCreate(Bundle savedInstanceState) {&#xa;        super.onCreate(savedInstanceState);&#xa;        mNum = getArguments().getInt(&quot;num&quot;);&#xa;&#xa;        // Pick a style based on the num.&#xa;        int style = DialogFragment.STYLE_NORMAL, theme = 0;&#xa;        switch ((mNum-1)%6) {&#xa;            case 1: style = DialogFragment.STYLE_NO_TITLE; break;&#xa;            case 2: style = DialogFragment.STYLE_NO_FRAME; break;&#xa;            case 3: style = DialogFragment.STYLE_NO_INPUT; break;&#xa;            case 4: style = DialogFragment.STYLE_NORMAL; break;&#xa;            case 5: style = DialogFragment.STYLE_NORMAL; break;&#xa;            case 6: style = DialogFragment.STYLE_NO_TITLE; break;&#xa;            case 7: style = DialogFragment.STYLE_NO_FRAME; break;&#xa;            case 8: style = DialogFragment.STYLE_NORMAL; break;&#xa;        }&#xa;        switch ((mNum-1)%6) {&#xa;            case 4: theme = android.R.style.Theme_Holo; break;&#xa;            case 5: theme = android.R.style.Theme_Holo_Light_Dialog; break;&#xa;            case 6: theme = android.R.style.Theme_Holo_Light; break;&#xa;            case 7: theme = android.R.style.Theme_Holo_Light_Panel; break;&#xa;            case 8: theme = android.R.style.Theme_Holo_Light; break;&#xa;        }&#xa;        setStyle(style, theme);&#xa;    }&#xa;&#xa;    @Override&#xa;    public View onCreateView(LayoutInflater inflater, ViewGroup container,&#xa;            Bundle savedInstanceState) {&#xa;        View v = inflater.inflate(R.layout.fragment_dialog, container, false);&#xa;        View tv = v.findViewById(R.id.text);&#xa;        ((TextView)tv).setText(&quot;Dialog #&quot; + mNum + &quot;: using style &quot;&#xa;                + getNameForNum(mNum));&#xa;&#xa;        // Watch for button clicks.&#xa;        Button button = (Button)v.findViewById(R.id.show);&#xa;        button.setOnClickListener(new OnClickListener() {&#xa;            public void onClick(View v) {&#xa;                // When button is clicked, call up to owning activity.&#xa;                ((FragmentDialog)getActivity()).showDialog();&#xa;            }&#xa;        });&#xa;&#xa;        return v;&#xa;    }&#xa;}"/>
</node>
