<node CREATED="1437379702278" FOLDED="true" ID="ID_1659903940" LINK="http://android-developers.blogspot.fr/2011/05/introducing-viewpropertyanimator.html" MODIFIED="1457520382321" TEXT="Introducing ViewPropertyAnimator">
<node CREATED="1437379717937" FOLDED="true" ID="ID_6886431" MODIFIED="1457520382318" TEXT="Introducing: ViewPropertyAnimator">
<node CREATED="1437379732614" ID="ID_1469553165" MODIFIED="1437379744858" TEXT="animate several properties in parallel,&#xa;using a single Animator internally"/>
<node CREATED="1437379766570" ID="ID_1935693823" MODIFIED="1437379771137" TEXT="sets them directly on the target View and &#xa;invalidates that object appropriately,"/>
<node CREATED="1437379786707" ID="ID_876535073" MODIFIED="1437379795695" TEXT="more efficient way than a&#xa; normal ObjectAnimator could"/>
<node CREATED="1437379810399" FOLDED="true" ID="ID_93122937" MODIFIED="1457520382318" TEXT="&#x4f8b;&#x5b50;">
<node CREATED="1437379828018" FOLDED="true" ID="ID_1582150045" MODIFIED="1457520382318" TEXT="fading-out view">
<node CREATED="1437379831299" ID="ID_1730257494" MODIFIED="1437379831903" TEXT=" myView.animate().alpha(0);"/>
<node CREATED="1437379852321" FOLDED="true" ID="ID_900201696" MODIFIED="1457520382318" TEXT="combine with other property animations">
<node CREATED="1437379861561" FOLDED="true" ID="ID_860932703" MODIFIED="1457520382317" TEXT="move our view in x and y to (500, 500) as follows">
<node CREATED="1437379865655" ID="ID_1333380748" MODIFIED="1437379866146" TEXT=" myView.animate().x(500).y(500);"/>
</node>
</node>
</node>
<node CREATED="1437379871426" FOLDED="true" ID="ID_1600286015" MODIFIED="1457520382318" TEXT="&#x89e3;&#x91ca;">
<node CREATED="1437379882946" FOLDED="true" ID="ID_1172991397" MODIFIED="1457520382318" TEXT="animate()">
<node CREATED="1437379905030" ID="ID_1470975082" MODIFIED="1437379905494" TEXT="returns an instance of ViewPropertyAnimator"/>
</node>
<node CREATED="1437379918991" FOLDED="true" ID="ID_707712304" MODIFIED="1457520382318" TEXT="Auto-start">
<node CREATED="1437379931005" ID="ID_862691926" MODIFIED="1437380308180" TEXT="&#x58f0;&#x660e;&#x540e;&#xff0c;UI &#x7684;&#x4e0b;&#x4e00;&#x6b21;&#x66f4;&#x65b0;&#x65f6;&#x5f00;&#x59cb;"/>
</node>
</node>
</node>
</node>
<node CREATED="1437380424964" FOLDED="true" ID="ID_1282000443" MODIFIED="1457520382320" TEXT="Performance Anxiety">
<node CREATED="1437380450603" FOLDED="true" ID="ID_207755234" MODIFIED="1457520382319" TEXT="ViewPropertyAnimator uses &#xa;no reflection or JNI techniques">
<node CREATED="1437380480009" FOLDED="true" ID="ID_318148988" MODIFIED="1457520382318" TEXT="alpha() operates directly on the &#xa;underlying &quot;alpha&quot; field of a View">
<node CREATED="1437380500829" ID="ID_972941853" MODIFIED="1437380501320" TEXT="once per animation frame"/>
</node>
</node>
<node CREATED="1437380527157" FOLDED="true" ID="ID_17012504" MODIFIED="1457520382320" TEXT="combine multiple animations&#xa;&#x7684;&#x6027;&#x80fd;&#x63d0;&#x9ad8;">
<node CREATED="1437380570472" FOLDED="true" ID="ID_1826125539" MODIFIED="1457520382319" TEXT="&#x4f7f;&#x7528; ObjectAnimator">
<node CREATED="1437380582231" ID="ID_1495348240" MODIFIED="1437380584644" TEXT="  ObjectAnimator animX = ObjectAnimator.ofFloat(myView, &quot;x&quot;, 50f);&#xa;    ObjectAnimator animY = ObjectAnimator.ofFloat(myView, &quot;y&quot;, 100f);&#xa;    AnimatorSet animSetXY = new AnimatorSet();&#xa;    animSetXY.playTogether(animX, animY);&#xa;    animSetXY.start();"/>
<node CREATED="1437380588412" ID="ID_1648931171" MODIFIED="1437380676521" TEXT="&#x989d;&#x5916;&#x8bbe;&#x7f6e; AnimatorSet"/>
</node>
<node CREATED="1437380679120" FOLDED="true" ID="ID_1364497320" MODIFIED="1457520382319" TEXT="&#x4f7f;&#x7528; PropertyValuesHolder &#x8fdb;&#x884c;&#x6539;&#x5584;">
<node CREATED="1437380693535" ID="ID_1688955117" MODIFIED="1437380702215" TEXT=" PropertyValuesHolder pvhX = PropertyValuesHolder.ofFloat(&quot;x&quot;, 50f);&#xa;    PropertyValuesHolder pvhY = PropertyValuesHolder.ofFloat(&quot;y&quot;, 100f);&#xa;    ObjectAnimator.ofPropertyValuesHolder(myView, pvhX, pvyY).start();"/>
<node CREATED="1437380736141" ID="ID_76968885" MODIFIED="1437380744975" TEXT="&#x907f;&#x514d;&#x591a;&#x4e2a; Animator"/>
</node>
<node CREATED="1437380765536" FOLDED="true" ID="ID_1051440020" MODIFIED="1457520382319" TEXT=" using ViewPropertyAnimator,&#xa;&#x548c;&#x4f7f;&#x7528;ViewPropertyAnimator &#xa;&#x6548;&#x7387;&#x4e00;&#x6837;&#xff0c; &#x4ee3;&#x7801;&#x66f4;&#x7b80;&#x5355;">
<node CREATED="1437380794473" ID="ID_968263950" MODIFIED="1437380794963" TEXT=" myView.animate().x(50f).y(100f);"/>
