<node CREATED="1455527596879" FOLDED="true" ID="ID_1135001091" MODIFIED="1457520382379" TEXT="&#x4ee3;&#x7801;">
<node CREATED="1455527553813" ID="ID_172218075" MODIFIED="1455527556368" TEXT="public class ImageDownloader {&#xa;&#xa;    public void download(String url, ImageView imageView) {&#xa;            BitmapDownloaderTask task = new BitmapDownloaderTask(imageView);&#xa;            task.execute(url);&#xa;        }&#xa;    }&#xa;&#xa;    /* class BitmapDownloaderTask, see below */&#xa;}"/>
</node>
