<node CREATED="1455527581039" FOLDED="true" ID="ID_1113168566" MODIFIED="1457520382383" TEXT="BitmapDownloaderTask">
<node CREATED="1455527726196" ID="ID_1199749823" MODIFIED="1455527729206" TEXT=" AsyncTask which will actually download the image"/>
<node CREATED="1455527753967" ID="ID_892018399" MODIFIED="1455527766789" TEXT=" started using execute, returns immediately"/>
<node CREATED="1455527786084" ID="ID_445203106" MODIFIED="1455527787826" TEXT="called from the UI thread"/>
<node CREATED="1455527805558" FOLDED="true" ID="ID_545432125" MODIFIED="1457520382379" TEXT="&#x4ee3;&#x7801;">
<node CREATED="1455527817866" ID="ID_1297879322" MODIFIED="1455527820959" TEXT="class BitmapDownloaderTask extends AsyncTask&lt;String, Void, Bitmap&gt; {&#xa;    private String url;&#xa;    private final WeakReference&lt;ImageView&gt; imageViewReference;&#xa;&#xa;    public BitmapDownloaderTask(ImageView imageView) {&#xa;        imageViewReference = new WeakReference&lt;ImageView&gt;(imageView);&#xa;    }&#xa;&#xa;    @Override&#xa;    // Actual download method, run in the task thread&#xa;    protected Bitmap doInBackground(String... params) {&#xa;         // params comes from the execute() call: params[0] is the url.&#xa;         return downloadBitmap(params[0]);&#xa;    }&#xa;&#xa;    @Override&#xa;    // Once the image is downloaded, associates it to the imageView&#xa;    protected void onPostExecute(Bitmap bitmap) {&#xa;        if (isCancelled()) {&#xa;            bitmap = null;&#xa;        }&#xa;&#xa;        if (imageViewReference != null) {&#xa;            ImageView imageView = imageViewReference.get();&#xa;            if (imageView != null) {&#xa;                imageView.setImageBitmap(bitmap);&#xa;            }&#xa;        }&#xa;    }&#xa;}"/>
</node>
<node CREATED="1455527825639" FOLDED="true" ID="ID_716442841" MODIFIED="1457520382382" TEXT="&#x5206;&#x6790;">
<node CREATED="1455527829924" FOLDED="true" ID="ID_166592604" MODIFIED="1457520382380" TEXT="doInBackground &#x5728;&#xa;&#x5355;&#x72ec;&#x7684;thread &#x4e0a;&#x6267;&#x884c;">
<node CREATED="1455527919161" FOLDED="true" ID="ID_1278343676" MODIFIED="1457520382379" TEXT="&#x4f7f;&#x7528; downloadBitmap">
<node CREATED="1455527934836" ID="ID_1589783288" MODIFIED="1455527937938" TEXT="static Bitmap downloadBitmap(String url) {&#xa;    final AndroidHttpClient client = AndroidHttpClient.newInstance(&quot;Android&quot;);&#xa;    final HttpGet getRequest = new HttpGet(url);&#xa;&#xa;    try {&#xa;        HttpResponse response = client.execute(getRequest);&#xa;        final int statusCode = response.getStatusLine().getStatusCode();&#xa;        if (statusCode != HttpStatus.SC_OK) { &#xa;            Log.w(&quot;ImageDownloader&quot;, &quot;Error &quot; + statusCode + &quot; while retrieving bitmap from &quot; + url); &#xa;            return null;&#xa;        }&#xa;        &#xa;        final HttpEntity entity = response.getEntity();&#xa;        if (entity != null) {&#xa;            InputStream inputStream = null;&#xa;            try {&#xa;                inputStream = entity.getContent(); &#xa;                final Bitmap bitmap = BitmapFactory.decodeStream(inputStream);&#xa;                return bitmap;&#xa;            } finally {&#xa;                if (inputStream != null) {&#xa;                    inputStream.close();  &#xa;                }&#xa;                entity.consumeContent();&#xa;            }&#xa;        }&#xa;    } catch (Exception e) {&#xa;        // Could provide a more explicit error message for IOException or IllegalStateException&#xa;        getRequest.abort();&#xa;        Log.w(&quot;ImageDownloader&quot;, &quot;Error while retrieving bitmap from &quot; + url, e.toString());&#xa;    } finally {&#xa;        if (client != null) {&#xa;            client.close();&#xa;        }&#xa;    }&#xa;    return null;&#xa;}&#xa;"/>
</node>
</node>
<node CREATED="1455528620967" FOLDED="true" ID="ID_39135064" MODIFIED="1457520382381" TEXT="onPostExecute &#x5728; UI thread &#x4e0a;&#xa;&#x88ab;&#x8c03;&#x7528;&#xff0c;&#x5f53;&#x4efb;&#x52a1;&#x5b8c;&#x6210;&#x65f6;&#xff0c;">
<node CREATED="1455528666208" FOLDED="true" ID="ID_802340976" MODIFIED="1457520382381" TEXT=" resulting Bitmap as a parameter,&#xa;&#x548c;&#x4e00;&#x4e2a; imageView &#x76f8;&#x7ed3;&#x5408;">
<node CREATED="1455528680535" ID="ID_41940834" MODIFIED="1455528765035" TEXT="&#x8be5; imageView &#x5728;&#x8c03;&#x7528; &#xa;download&#x65f6;&#x7ed9;&#x51fa;"/>
<node CREATED="1455528770985" FOLDED="true" ID="ID_731299547" MODIFIED="1457520382380" TEXT=" stored in the &#xa;BitmapDownloaderTask">
<node CREATED="1455528795455" FOLDED="true" ID="ID_952552419" MODIFIED="1457520382380" TEXT=" ImageView is stored as a WeakReference">
<node CREATED="1455528808507" ID="ID_253644105" MODIFIED="1455528818462" TEXT=" a download in progress does not &#xa;prevent a killed activity&apos;s ImageView &#xa;from being garbage collected"/>
<node CREATED="1455528865126" ID="ID_1283584940" MODIFIED="1455528895019" TEXT="we have to check that both the weak&#xa;reference and the imageView are not null &#xa;(i.e. were not collected) before using &#xa;them in onPostExecute."/>
</node>
</node>
</node>
</node>
</node>
<node CREATED="1455528915207" FOLDED="true" ID="ID_1725851743" MODIFIED="1457520382382" TEXT="&#x4f18;&#x70b9;">
<node CREATED="1455528932053" FOLDED="true" ID="ID_624764410" MODIFIED="1457520382382" TEXT="dramatically improved the &#xa;performance of the ListView">
<node CREATED="1455528951322" ID="ID_1745512235" MODIFIED="1455528951827" TEXT=" scrolls smoothly"/>
</node>
</node>
<node CREATED="1455528958692" ID="ID_914970170" LINK="http://android-developers.blogspot.com/2009/05/painless-threading.html" MODIFIED="1455528979679" TEXT="Read Painless threading for &#xa;more details on AsyncTasks."/>
<node CREATED="1455528989289" FOLDED="true" ID="ID_1730983078" MODIFIED="1457520382383" TEXT="&#x7f3a;&#x70b9;">
<node CREATED="1455529112708" FOLDED="true" ID="ID_434627649" MODIFIED="1457520382383" TEXT=" a ListView-specific behavior reveals a problem">
<node CREATED="1455529132229" FOLDED="true" ID="ID_1037024307" MODIFIED="1457520382382" TEXT="memory efficiency reasons">
<node CREATED="1455529142843" ID="ID_762404160" MODIFIED="1455529148068" TEXT="ListView recycles the views that &#xa;are displayed when the user scrolls"/>
</node>
<node CREATED="1455529720619" ID="ID_339346639" MODIFIED="1455529746495" TEXT="flings list &#x7684;&#x65f6;&#x5019;&#xff0c;&#x4f1a;&#xa;&#x8981;&#x4e0b;&#x8f7d;&#x597d;&#x591a;&#x56fe;&#x7247;"/>
<node CREATED="1455529876793" ID="ID_1791117907" MODIFIED="1455529886650" TEXT="Each time it is displayed the ImageView &#xa;correctly triggers an image download task, &#xa;which will eventually change its image"/>
<node CREATED="1455529899148" FOLDED="true" ID="ID_1489705256" MODIFIED="1457520382382" TEXT="problem">
<node CREATED="1455529911782" FOLDED="true" ID="ID_1650165665" MODIFIED="1457520382382" TEXT="ordering">
<node CREATED="1455529921761" ID="ID_1102958225" MODIFIED="1455529931829" TEXT=" there&apos;s no guarantee that the download &#xa;tasks will finish in the order in which they&#xa; were started"/>
<node CREATED="1455530211563" ID="ID_421701952" MODIFIED="1455530224062" TEXT=" image finally displayed in the list &#xa;may come from a previous item"/>
</node>
</node>
</node>
</node>
<node CREATED="1455530356599" ID="ID_1203066266" LINK="#ID_1821587961" MODIFIED="1455530381590" TEXT="&#x89e3;&#x51b3;"/>
</node>
</node>
<node CREATED="1455530350432" FOLDED="true" ID="ID_1821587961" MODIFIED="1457520382388" TEXT="Handling concurrency">
<node CREATED="1455530451990" FOLDED="true" ID="ID_589506700" MODIFIED="1457520382387" TEXT="To solve this issue, we should remember&#xa; the order of the downloads, so that the &#xa;last started one is the one that will &#xa;effectively be displayed.">
<node CREATED="1455530513213" ID="ID_1426924396" LINK="#ID_1465750273" MODIFIED="1455530554894" TEXT=" add this extra information in the &#xa;ImageView using a dedicated &#xa;Drawable subclass"/>
<node CREATED="1455530738757" FOLDED="true" ID="ID_1390025426" MODIFIED="1457520382386" TEXT="download method will now create an &#xa;instance of this class and associate it &#xa;with the imageView">
<node CREATED="1455530820644" FOLDED="true" ID="ID_1539862694" MODIFIED="1457520382383" TEXT="&#x4ee3;&#x7801;">
<node CREATED="1455530826741" ID="ID_1859230355" MODIFIED="1455530829449" TEXT="public void download(String url, ImageView imageView) {&#xa;     if (cancelPotentialDownload(url, imageView)) {&#xa;         BitmapDownloaderTask task = new BitmapDownloaderTask(imageView);&#xa;         DownloadedDrawable downloadedDrawable = new DownloadedDrawable(task);&#xa;         imageView.setImageDrawable(downloadedDrawable);&#xa;         task.execute(url, cookie);&#xa;     }&#xa;}"/>
<node CREATED="1455531232441" FOLDED="true" ID="ID_1893664044" MODIFIED="1457520382383" TEXT="getBitmapDownloaderTask">
<node CREATED="1455531237693" ID="ID_1537951064" MODIFIED="1455531240509" TEXT="private static BitmapDownloaderTask getBitmapDownloaderTask(ImageView imageView) {&#xa;    if (imageView != null) {&#xa;        Drawable drawable = imageView.getDrawable();&#xa;        if (drawable instanceof DownloadedDrawable) {&#xa;            DownloadedDrawable downloadedDrawable = (DownloadedDrawable)drawable;&#xa;            return downloadedDrawable.getBitmapDownloaderTask();&#xa;        }&#xa;    }&#xa;    return null;&#xa;}"/>
</node>
</node>
<node CREATED="1455530831459" FOLDED="true" ID="ID_1280823573" MODIFIED="1457520382385" TEXT="&#x5206;&#x6790;">
<node CREATED="1455530837250" FOLDED="true" ID="ID_420230487" MODIFIED="1457520382384" TEXT="cancelPotentialDownload method">
<node CREATED="1455530851878" ID="ID_612226706" MODIFIED="1455530858326" TEXT="stop the possible download in progress &#xa;on this imageView since a new one is &#xa;about to start"/>
<node CREATED="1455530946398" FOLDED="true" ID="ID_341180406" MODIFIED="1457520382383" TEXT="&#x4ee3;&#x7801;">
<node CREATED="1455530950330" ID="ID_1024605827" MODIFIED="1455530952732" TEXT="private static boolean cancelPotentialDownload(String url, ImageView imageView) {&#xa;    BitmapDownloaderTask bitmapDownloaderTask = getBitmapDownloaderTask(imageView);&#xa;&#xa;    if (bitmapDownloaderTask != null) {&#xa;        String bitmapUrl = bitmapDownloaderTask.url;&#xa;        if ((bitmapUrl == null) || (!bitmapUrl.equals(url))) {&#xa;            bitmapDownloaderTask.cancel(true);&#xa;        } else {&#xa;            // The same URL is already being downloaded.&#xa;            return false;&#xa;        }&#xa;    }&#xa;    return true;&#xa;}"/>
</node>
<node CREATED="1455531042254" FOLDED="true" ID="ID_640986917" MODIFIED="1457520382384" TEXT="&#x5206;&#x6790;">
<node CREATED="1455531054093" FOLDED="true" ID="ID_349865461" MODIFIED="1457520382383" TEXT="cancelPotentialDownload uses the &#xa;cancel method of the AsyncTask &#xa;class to stop the download in progress">
<node CREATED="1455531076928" ID="ID_764637942" MODIFIED="1455531076928" TEXT=""/>
</node>
</node>
</node>
<node CREATED="1455530882368" FOLDED="true" ID="ID_740900649" MODIFIED="1457520382384" TEXT="this is not sufficient to guarantee that&#xa; the newest download is always displayed">
<node CREATED="1455530886784" FOLDED="true" ID="ID_1929237012" MODIFIED="1457520382384" TEXT="&#x539f;&#x56e0;">
<node CREATED="1455530903152" ID="ID_1519207999" MODIFIED="1455530913255" TEXT="the task may be finished, waiting &#xa;in its onPostExecute method, which &#xa;may still may be executed after the &#xa;one of this new download."/>
</node>
</node>
<node CREATED="1455531145522" ID="ID_286761508" MODIFIED="1455531154795" TEXT="when a download is already in progress on&#xa; the same URL in which case we let it continue"/>
<node CREATED="1455531184880" FOLDED="true" ID="ID_1237749157" MODIFIED="1457520382385" TEXT="&#x95ee;&#x9898;">
<node CREATED="1455531190442" FOLDED="true" ID="ID_179483667" MODIFIED="1457520382385" TEXT="if an ImageView is garbage collected, &#xa;its associated download is not stopped">
<node CREATED="1455531201482" FOLDED="true" ID="ID_702474335" MODIFIED="1457520382385" TEXT="&#x89e3;&#x51b3;&#xa;">
<node CREATED="1455531207862" ID="ID_1272500724" MODIFIED="1455531215534" TEXT="A RecyclerListener "/>
</node>
</node>
</node>
</node>
</node>
<node CREATED="1455531279815" FOLDED="true" ID="ID_1126909877" MODIFIED="1457520382386" TEXT="onPostExecute has to be modified so that&#xa; it will bind the Bitmap only if this ImageView i&#xa;s still associated with this download process:">
<node CREATED="1455531289009" FOLDED="true" ID="ID_667213956" MODIFIED="1457520382386" TEXT="&#x4ee3;&#x7801;">
<node CREATED="1455531293566" ID="ID_925229992" MODIFIED="1455531295905" TEXT="if (imageViewReference != null) {&#xa;    ImageView imageView = imageViewReference.get();&#xa;    BitmapDownloaderTask bitmapDownloaderTask = getBitmapDownloaderTask(imageView);&#xa;    // Change bitmap only if this process is still associated with it&#xa;    if (this == bitmapDownloaderTask) {&#xa;        imageView.setImageBitmap(bitmap);&#xa;    }&#xa;}"/>
</node>
</node>
</node>
<node CREATED="1455530544204" FOLDED="true" ID="ID_1816792056" MODIFIED="1457520382388" TEXT="code ">
<node CREATED="1455530546660" FOLDED="true" ID="ID_1465750273" MODIFIED="1457520382388" TEXT="DownloadedDrawable">
<node CREATED="1455530564679" ID="ID_1300318305" MODIFIED="1455530569789" TEXT="temporarily bind to the ImageView&#xa; while the download is in progress"/>
<node CREATED="1455530575253" FOLDED="true" ID="ID_1879884659" MODIFIED="1457520382387" TEXT="&#x4ee3;&#x7801;">
<node CREATED="1455530577833" ID="ID_206338595" MODIFIED="1455530580874" TEXT="static class DownloadedDrawable extends ColorDrawable {&#xa;    private final WeakReference&lt;BitmapDownloaderTask&gt; bitmapDownloaderTaskReference;&#xa;&#xa;    public DownloadedDrawable(BitmapDownloaderTask bitmapDownloaderTask) {&#xa;        super(Color.BLACK);&#xa;        bitmapDownloaderTaskReference =&#xa;            new WeakReference&lt;BitmapDownloaderTask&gt;(bitmapDownloaderTask);&#xa;    }&#xa;&#xa;    public BitmapDownloaderTask getBitmapDownloaderTask() {&#xa;        return bitmapDownloaderTaskReference.get();&#xa;    }&#xa;}"/>
</node>
<node CREATED="1455530602476" FOLDED="true" ID="ID_1871194363" MODIFIED="1457520382388" TEXT="&#x5206;&#x6790;">
<node CREATED="1455530609338" FOLDED="true" ID="ID_1278711233" MODIFIED="1457520382388" TEXT="ColorDrawable">
<node CREATED="1455530619013" FOLDED="true" ID="ID_20656805" MODIFIED="1457520382387" TEXT=" the ImageView displaying a black &#xa;background while its download is &#xa;in progress">
<node CREATED="1455530691380" ID="ID_1603320194" MODIFIED="1455530696690" TEXT="One could use a &#x201c;download in progress&#x201d; &#xa;image instead"/>
</node>
</node>
