<node CREATED="1456838405937" FOLDED="true" ID="ID_528829796" MODIFIED="1457520382429" TEXT="a new &quot;yourapp&quot; namespace has &#xa;been added to the menu element,&#xa; with the showAsAction and actionProviderClass &#xa;attributes being provided from this namespace">
<node CREATED="1456838428020" ID="ID_1117026421" MODIFIED="1456838430632" TEXT="&lt;menu xmlns:yourapp=&quot;http://schemas.android.com/apk/res-auto&quot;&#xa;    xmlns:android=&quot;http://schemas.android.com/apk/res/android&quot;&gt;&#xa;&#xa;    &lt;item&#xa;        android:id=&quot;@+id/menu_share&quot;&#xa;        android:title=&quot;@string/menu_share&quot;&#xa;        yourapp:actionProviderClass=&quot;android.support.v7.widget.ShareActionProvider&quot;&#xa;        yourapp:showAsAction=&quot;always&quot; /&gt;&#xa;    ...&#xa;&lt;/menu&gt;"/>
</node>
