<node CREATED="1456838783170" ID="ID_208965681" MODIFIED="1456838789337" TEXT="populate it by overriding &#xa;onCreateOptionsMenu()"/>
<node CREATED="1456838797916" FOLDED="true" ID="ID_432324085" MODIFIED="1457520382432" TEXT="populating the Menu object">
<node CREATED="1456838923527" ID="ID_877389012" MODIFIED="1456838923969" TEXT="by inflating a menu resource"/>
</node>
</node>
<node CREATED="1456838939649" FOLDED="true" ID="ID_1597976449" MODIFIED="1457520382433" TEXT="to call any MenuItem methods&#xa; introduced in API level 11 or later">
<node CREATED="1456838961448" FOLDED="true" ID="ID_442186923" LINK="https://developer.android.com/reference/android/support/v4/view/MenuItemCompat.html" MODIFIED="1457520382432" TEXT="MenuItemCompat">
<node CREATED="1456838978334" FOLDED="true" ID="ID_1731841274" MODIFIED="1457520382432" TEXT="&#x4f8b;&#x5b50;">
<node CREATED="1456838982085" ID="ID_1431263593" MODIFIED="1456838984378" TEXT="@Override&#xa;public boolean onCreateOptionsMenu(Menu menu) {&#xa;    // Inflate the menu&#xa;    getMenuInflater().inflate(R.menu.main_menu, menu);&#xa;&#xa;    // Find the share item&#xa;    MenuItem shareItem = menu.findItem(R.id.menu_share);&#xa;&#xa;    // Need to use MenuItemCompat to retrieve the Action Provider&#xa;    mActionProvider = (ShareActionProvider)&#xa;        MenuItemCompat.getActionProvider(shareItem);&#xa;&#xa;    return super.onCreateOptionsMenu(menu);&#xa;}"/>
</node>
</node>
</node>
</node>
