<node CREATED="1456839141490" FOLDED="true" ID="ID_867989875" MODIFIED="1457520382434" TEXT="Add ActionMode callbacks">
<node CREATED="1456839193832" FOLDED="true" ID="ID_888475815" MODIFIED="1457520382434" TEXT="To start an action mode from&#xa; your ActionBarActivity ">
<node CREATED="1456839201777" ID="ID_528572914" LINK="https://developer.android.com/reference/android/support/v7/app/ActionBarActivity.html#startSupportActionMode(android.support.v7.view.ActionMode.Callback)" MODIFIED="1456839206292" TEXT="startSupportActionMode()"/>
<node CREATED="1456839227015" FOLDED="true" ID="ID_438806844" MODIFIED="1457520382433" TEXT="&#x4f8b;&#x5b50; shows how to start an action mode, &#xa;inflate a menu resource and react to &#xa;user&#x2019;s selection:">
<node CREATED="1456839571760" ID="ID_1424399992" MODIFIED="1456839574336" TEXT="startSupportActionMode(new ActionMode.Callback() {&#xa;    @Override&#xa;    public boolean onCreateActionMode(ActionMode actionMode, Menu menu) {&#xa;        // Inflate our menu from a resource file&#xa;        actionMode.getMenuInflater().inflate(R.menu.action_mode_main, menu);&#xa;&#xa;        // Return true so that the action mode is shown&#xa;        return true;&#xa;    }&#xa;&#xa;    @Override&#xa;    public boolean onPrepareActionMode(ActionMode actionMode, Menu menu) {&#xa;        // As we do not need to modify the menu before displayed, we return false.&#xa;        return false;&#xa;    }&#xa;&#xa;    @Override&#xa;    public boolean onActionItemClicked(ActionMode actionMode, MenuItem menuItem) {&#xa;        // Similar to menu handling in Activity.onOptionsItemSelected()&#xa;        switch (menuItem.getItemId()) {&#xa;            case R.id.menu_remove:&#xa;                // Some remove functionality&#xa;                return true;&#xa;        }&#xa;&#xa;        return false;&#xa;    }&#xa;&#xa;    @Override&#xa;    public void onDestroyActionMode(ActionMode actionMode) {&#xa;        // Allows you to be notified when the action mode is dismissed&#xa;    }&#xa;});"/>
</node>
</node>
</node>
<node CREATED="1456839709729" FOLDED="true" ID="ID_905756622" MODIFIED="1457520382437" TEXT=" Add support for Up navigation">
<node CREATED="1456839728471" FOLDED="true" ID="ID_243551076" MODIFIED="1457520382436" TEXT="Up navigation support is &#xa;built into ActionBarCompat&#xa;&#x548c; Android 4.1 &#x4e2d;&#x7684;&#x76f8;&#x4f3c;">
<node CREATED="1456839772706" FOLDED="true" ID="ID_383650040" MODIFIED="1457520382435" TEXT="add a reference in each AndroidManifest &#xa;&lt;activity&gt; element to its logical parent activity">
<node CREATED="1456839789221" ID="ID_1908131310" MODIFIED="1456839793306" TEXT="by adding a metadata element &#xa;to the &lt;activity&gt; element"/>
<node CREATED="1456839826725" FOLDED="true" ID="ID_456866784" MODIFIED="1457520382434" TEXT="set the android:parentActivityName &#xa;attribute for explicit Android 4.1 support, &#xa;although this is not strictly necessary for&#xa; ActionBarCompat:">
<node CREATED="1456839845572" ID="ID_1954490435" MODIFIED="1456839847856" TEXT="&lt;activity android:name=&quot;.SampleActivity&quot;&#xa;    android:parentActivityName=&quot;.SampleParentActivity&quot;&gt;&#xa;    &lt;meta-data android:name=&quot;android.support.PARENT_ACTIVITY&quot;&#xa;        android:value=&quot;.SampleParentActivity&quot; /&gt;&#xa;&lt;/activity&gt;&#xa;"/>
</node>
</node>
<node CREATED="1456839875630" FOLDED="true" ID="ID_347226271" MODIFIED="1457520382435" TEXT="In your Activity you should then configure&#xa; the home button to display for up navigation:">
<node CREATED="1456839891658" ID="ID_1361735860" MODIFIED="1456839894364" TEXT="getSupportActionBar().setDisplayHomeAsUpEnabled(true);"/>
</node>
<node CREATED="1456839915762" ID="ID_1324379653" MODIFIED="1456839921713" TEXT="When the user now clicks on the home item,&#xa; the user will navigate up to the parent Activity &#xa;set in your AndroidManifest."/>
</node>
<node CREATED="1456839938774" FOLDED="true" ID="ID_1593462274" MODIFIED="1457520382436" TEXT="customize what happens when a&#xa; user clicks on the home item&#xa;&#x548c; Android 4.1 &#x4e2d;&#x7684;&#x65b9;&#x6cd5;&#x540d;&#x5b57;&#x4e00;&#x6837;&#xa;">
<node CREATED="1456840016132" ID="ID_500099838" MODIFIED="1456840022112" TEXT="onSupportNavigateUp()"/>
