<node CREATED="1456844031832" FOLDED="true" ID="ID_1401015360" MODIFIED="1457520382448" TEXT="AppCompat &#x4e2d;&#x7684; Toolbar&#xa;">
<node CREATED="1456844061948" ID="ID_1584757477" MODIFIED="1456844092953" TEXT="&#x5b9e;&#x73b0;&#x4e86; android framework widget&#xa;&#x4e2d;&#x7684;&#x90e8;&#x5206; features"/>
<node CREATED="1456844099460" ID="ID_1129452099" LINK="https://developer.android.com/reference/android/support/v7/widget/Toolbar.html" MODIFIED="1456844117682" TEXT="&#x7531; android.support.v7.widget.Toolbar &#x5b9e;&#x73b0;"/>
<node CREATED="1456844283610" FOLDED="true" ID="ID_1466140094" MODIFIED="1457520382448" TEXT="two ways to use Toolbar">
<node CREATED="1456844292855" FOLDED="true" ID="ID_810415229" MODIFIED="1457520382446" TEXT="Use a Toolbar as an Action Bar">
<node CREATED="1456844639614" FOLDED="true" ID="ID_1036463968" MODIFIED="1457520382445" TEXT="&#x89e3;&#x91ca;">
<node CREATED="1456844307267" ID="ID_458224846" MODIFIED="1456844607695" TEXT=" to use the existing Action Bar facilities &#xa;&#x6bd4;&#x5982; menu inflation &#xff0c; selection&#xff0c;&#xa;ActionBarDrawerToggle&#xff0c; &#x5e76;&#x4e14;&#x8fd8;&#x60f3;&#x63a7;&#x5236;&#xa;Toolbar&#x7684;&#x5916;&#x8868;"/>
</node>
<node CREATED="1456844649035" FOLDED="true" ID="ID_1170137892" MODIFIED="1457520382446" TEXT="&#x7528;&#x6cd5;">
<node CREATED="1456844666991" FOLDED="true" ID="ID_1403375632" MODIFIED="1457520382445" TEXT="First disable the decor-provided Action Bar">
<node CREATED="1456844675234" FOLDED="true" ID="ID_1428906172" MODIFIED="1457520382445" TEXT="how">
<node CREATED="1456844688694" ID="ID_817730605" MODIFIED="1456844693577" TEXT=" have your theme extend from &#xa;Theme.AppCompat.NoActionBar"/>
</node>
</node>
<node CREATED="1456844702217" FOLDED="true" ID="ID_1073674275" MODIFIED="1457520382445" TEXT="Second, create a Toolbar instance">
<node CREATED="1456844717920" FOLDED="true" ID="ID_1847579472" MODIFIED="1457520382445" TEXT="via your layout XML">
<node CREATED="1456844721805" ID="ID_1482602115" MODIFIED="1456844724605" TEXT="&lt;android.support.v7.widget.Toolbar&#xa;    android:id=&#x201d;@+id/my_awesome_toolbar&#x201d;&#xa;    android:layout_height=&#x201d;wrap_content&#x201d;&#xa;    android:layout_width=&#x201d;match_parent&#x201d;&#xa;    android:minHeight=&#x201d;?attr/actionBarSize&#x201d;&#xa;    android:background=&#x201d;?attr/colorPrimary&#x201d; /&gt;"/>
</node>
</node>
<node CREATED="1456844837036" FOLDED="true" ID="ID_693365147" MODIFIED="1457520382446" TEXT="Then in your Activity or Fragment, set&#xa; the Toolbar to act as your Action Bar">
<node CREATED="1456844846077" ID="ID_1954955773" MODIFIED="1456844849920" TEXT="@Override&#xa;public void onCreate(Bundle savedInstanceState) {&#xa;    super.onCreate(savedInstanceState);&#xa;    setContentView(R.layout.blah);&#xa;&#xa;    Toolbar toolbar = (Toolbar) findViewById(R.id.my_awesome_toolbar);&#xa;    setSupportActionBar(toolbar);&#xa;}"/>
</node>
</node>
</node>
<node CREATED="1456844633719" FOLDED="true" ID="ID_1361694314" MODIFIED="1457520382448" TEXT="Standalone">
<node CREATED="1456844412220" FOLDED="true" ID="ID_1068177489" MODIFIED="1457520382446" TEXT="&#x4f60;&#x7684; app &#x4e0d;&#x652f;&#x6301; ActionBar, &#xa;&#x53ea;&#x6709; Toolbar&#xff0c;">
<node CREATED="1456844559720" FOLDED="true" ID="ID_1223490405" MODIFIED="1457520382446" TEXT="&#x6bd4;&#x5982;">
<node CREATED="1456844567013" ID="ID_314645584" MODIFIED="1456844568445" TEXT="showing multiple toolbars on the screen"/>
<node CREATED="1456844577361" ID="ID_86780884" MODIFIED="1456844577839" TEXT="spanning only part of the width"/>
</node>
</node>
<node CREATED="1456844940392" FOLDED="true" ID="ID_940236644" MODIFIED="1457520382448" TEXT="&#x7528;&#x6cd5;">
<node CREATED="1456844986068" ID="ID_1024068307" MODIFIED="1456845033973" TEXT="Toolbar &#x4e0d;&#x88ab;&#x8bbe;&#x6210; action bar&#xa;&#x6240;&#x4ee5;&#x53ef;&#x4ee5;&#x4f7f;&#x7528; AppCompat theme&#xa;&#x4e0d;&#x9700;&#x8981; disable  &#xa;decor-provided Action Bar"/>
<node CREATED="1456845045684" FOLDED="true" ID="ID_1496836597" MODIFIED="1457520382447" TEXT=" manually populate the Toolbar&#xa; with content/actions">
<node CREATED="1456845065552" FOLDED="true" ID="ID_163246851" MODIFIED="1457520382447" TEXT="&#x4f8b;&#x5b50;&#xff1a; display actions, you &#xa;need to inflate a menu into it:">
<node CREATED="1456845090819" ID="ID_349944388" MODIFIED="1456845093228" TEXT="@Override&#xa;public void onCreate(Bundle savedInstanceState) {&#xa;    super.onCreate(savedInstanceState);&#xa;    setContentView(R.layout.blah);&#xa;&#xa;    Toolbar toolbar = (Toolbar) findViewById(R.id.my_awesome_toolbar);&#xa;&#xa;    // Set an OnMenuItemClickListener to handle menu item clicks&#xa;    toolbar.setOnMenuItemClickListener(new Toolbar.OnMenuItemClickListener() {&#xa;        @Override&#xa;        public boolean onMenuItemClick(MenuItem item) {&#xa;            // Handle the menu item&#xa;            return true;&#xa;        }&#xa;    });&#xa;&#xa;    // Inflate a menu to be displayed in the toolbar&#xa;    toolbar.inflateMenu(R.menu.your_toolbar_menu);&#xa;}"/>
</node>
</node>
</node>
</node>
</node>
<node CREATED="1456845170009" ID="ID_1031994649" LINK="https://developer.android.com/reference/android/support/v7/widget/Toolbar.html" MODIFIED="1456845178411" TEXT="&#x53c2;&#x770b; Toolbar API reference"/>
</node>
<node CREATED="1456845189651" FOLDED="true" ID="ID_1319451107" MODIFIED="1457520382449" TEXT="Styling">
<node CREATED="1456845212466" FOLDED="true" ID="ID_435294565" MODIFIED="1457520382448" TEXT="example : toolbar &#x4f5c;&#x4e3a; action bar&#xa;&#x4f7f;&#x7528;&#x7684;&#x65f6;&#x5019;&#xff0c;&#x9700;&#x8981;&#x4f7f;&#x7528;&#x7684; basic style">
<node CREATED="1456845272650" ID="ID_1414309549" MODIFIED="1456845274771" TEXT="&lt;android.support.v7.widget.Toolbar  &#xa;    android:layout_height=&quot;wrap_content&quot;&#xa;    android:layout_width=&quot;match_parent&quot;&#xa;    android:minHeight=&quot;?attr/actionBarSize&quot;&#xa;    app:theme=&quot;@style/ThemeOverlay.AppCompat.ActionBar&quot; /&gt;"/>
<node CREATED="1456845319738" ID="ID_1475083858" MODIFIED="1456845325128" TEXT="app:theme declaration will make sure &#xa;that your text and items are using solid&#xa; colors (i.e 100% opacity white)"/>
</node>
<node CREATED="1456845328985" FOLDED="true" ID="ID_1923036370" MODIFIED="1457520382449" TEXT="DarkActionBar">
<node CREATED="1456845350152" ID="ID_491224995" MODIFIED="1456845356155" TEXT="style Toolbar instances directly&#xa; using layout attributes"/>
<node CREATED="1456845381040" FOLDED="true" ID="ID_582899329" MODIFIED="1457520382449" TEXT=" To achieve a Toolbar which looks like&#xa; &apos;DarkActionBar&apos; (dark content, light &#xa;overflow menu), provide the theme&#xa; and popupTheme attributes:">
<node CREATED="1456845393078" ID="ID_1278033364" MODIFIED="1456845398861" TEXT="&lt;android.support.v7.widget.Toolbar&#xa;    android:layout_height=&#x201d;wrap_content&#x201d;&#xa;    android:layout_width=&#x201d;match_parent&#x201d;&#xa;    android:minHeight=&#x201d;@dimen/triple_height_toolbar&#x201d;&#xa;    app:theme=&quot;@style/ThemeOverlay.AppCompat.Dark.ActionBar&quot;&#xa;    app:popupTheme=&quot;@style/ThemeOverlay.AppCompat.Light&quot; /&gt;"/>
</node>
</node>
</node>
</node>
<node CREATED="1456845430687" FOLDED="true" ID="ID_1365533197" MODIFIED="1457520382450" TEXT="SearchView Widget">
<node CREATED="1456845494350" FOLDED="true" ID="ID_759947885" MODIFIED="1457520382450" TEXT="AppCompat offers Lollipop&#x2019;s &#xa;updated SearchView API&#xa;We now use the Lollipop style &#xa;structure instead of the old &#xa;searchView* theme attributes.">
<node CREATED="1456845578064" FOLDED="true" ID="ID_1437447295" MODIFIED="1457520382450" TEXT="how you style SearchView:">
<node CREATED="1456845584083" ID="ID_935736821" MODIFIED="1456845586266" TEXT="values/themes.xml:&#xa;&lt;style name=&#x201d;Theme.MyTheme&#x201d; parent=&#x201d;Theme.AppCompat&#x201d;&gt;&#xa;    &lt;item name=&#x201d;searchViewStyle&#x201d;&gt;@style/MySearchViewStyle&lt;/item&gt;&#xa;&lt;/style&gt;&#xa;&lt;style name=&#x201d;MySearchViewStyle&#x201d; parent=&#x201d;Widget.AppCompat.SearchView&#x201d;&gt;&#xa;    &lt;!-- Background for the search query section (e.g. EditText) --&gt;&#xa;    &lt;item name=&quot;queryBackground&quot;&gt;...&lt;/item&gt;&#xa;    &lt;!-- Background for the actions section (e.g. voice, submit) --&gt;&#xa;    &lt;item name=&quot;submitBackground&quot;&gt;...&lt;/item&gt;&#xa;    &lt;!-- Close button icon --&gt;&#xa;    &lt;item name=&quot;closeIcon&quot;&gt;...&lt;/item&gt;&#xa;    &lt;!-- Search button icon --&gt;&#xa;    &lt;item name=&quot;searchIcon&quot;&gt;...&lt;/item&gt;&#xa;    &lt;!-- Go/commit button icon --&gt;&#xa;    &lt;item name=&quot;goIcon&quot;&gt;...&lt;/item&gt;&#xa;    &lt;!-- Voice search button icon --&gt;&#xa;    &lt;item name=&quot;voiceIcon&quot;&gt;...&lt;/item&gt;&#xa;    &lt;!-- Commit icon shown in the query suggestion row --&gt;&#xa;    &lt;item name=&quot;commitIcon&quot;&gt;...&lt;/item&gt;&#xa;    &lt;!-- Layout for query suggestion rows --&gt;&#xa;    &lt;item name=&quot;suggestionRowLayout&quot;&gt;...&lt;/item&gt;&#xa;&lt;/style&gt;"/>
<node CREATED="1456845707048" ID="ID_735104107" MODIFIED="1456845712391" TEXT="&#x4e0d;&#x7528;&#x5168;&#x90e8;&#x8bbe;&#x7f6e;"/>
