<node CREATED="1456997957582" ID="ID_165650908" MODIFIED="1456997957946" TEXT="uploading a video"/>
<node CREATED="1456997962712" ID="ID_415011570" MODIFIED="1456997963152" TEXT="finding friend "/>
</node>
<node CREATED="1456997976893" ID="ID_542870374" MODIFIED="1456997997757" TEXT="Start by requiring minimal investment by the user&#xff0c;&#xa;then ask them for more details as they are needed&#xa; to use the apps features."/>
