<node CREATED="1462960651028" ID="ID_1911617419" MODIFIED="1462960651620" TEXT="garbage collections"/>
<node CREATED="1462960669008" ID="ID_554161743" MODIFIED="1462960673632" TEXT="metadata (UProperty) support for exposing &#xa;variables to the Unreal Editor"/>
