<node CREATED="1462960212705" FOLDED="true" ID="ID_898142275" MODIFIED="1467367526309" TEXT="Actors">
<node CREATED="1462961080892" ID="ID_187353729" MODIFIED="1462961081374" TEXT="An Actor is any object that can be placed into a level"/>
<node CREATED="1462961095774" FOLDED="true" ID="ID_1296237443" MODIFIED="1463732445808" TEXT="Actors are a generic Class that support 3D transformations">
<node CREATED="1462961099996" ID="ID_663365250" MODIFIED="1462961100441" TEXT="translation"/>
<node CREATED="1462961103605" ID="ID_791796103" MODIFIED="1462961103868" TEXT="rotation"/>
<node CREATED="1462961106642" ID="ID_692087247" MODIFIED="1462961106962" TEXT="scale"/>
</node>
<node CREATED="1462961119296" FOLDED="true" ID="ID_757824627" MODIFIED="1463732445809" TEXT="Actors can be created (spawned) and &#xa;destroyed through gameplay code&#xa;">
<node CREATED="1462961136549" ID="ID_1510361781" MODIFIED="1462961137310" TEXT="gameplay code  (C++ or Blueprints). "/>
</node>
<node CREATED="1462961147167" ID="ID_1348382901" MODIFIED="1462961147791" TEXT=" In C++, AActor is the base class of all Actors."/>
<node CREATED="1462961171458" FOLDED="true" ID="ID_17642876" MODIFIED="1463732445812" TEXT="different types of Actors,">
<node CREATED="1462961174575" ID="ID_938425328" MODIFIED="1462961175776" TEXT="StaticMeshActor"/>
<node CREATED="1462961177207" ID="ID_1992127228" MODIFIED="1462961177492" TEXT="CameraActor"/>
<node CREATED="1462961180245" ID="ID_629802975" MODIFIED="1462961180575" TEXT="PlayerStartActor"/>
