</node>
<node CREATED="1462960216395" FOLDED="true" ID="ID_1327296320" MODIFIED="1467367526309" TEXT="Components">
<node CREATED="1462961214234" FOLDED="true" ID="ID_974934908" MODIFIED="1463732445812" TEXT="A Component is a piece of functionality &#xa;that can be added to an Actor">
<node CREATED="1462961250473" ID="ID_1938343765" MODIFIED="1462961254874" TEXT="the Actor will have access to and can use &#xa;functionality provided by the Component."/>
</node>
<node CREATED="1462961231773" ID="ID_1346359271" MODIFIED="1462961232125" TEXT="cannot exist by themselves"/>
<node CREATED="1462961263760" FOLDED="true" ID="ID_273793757" MODIFIED="1463732445815" TEXT="example">
<node CREATED="1462961278836" FOLDED="true" ID="ID_446892906" MODIFIED="1463647132297" TEXT="a Spot Light Component">
<node CREATED="1462961286689" ID="ID_411646058" MODIFIED="1462961288053" TEXT="will allow your Actor to emit light like a spot light"/>
</node>
<node CREATED="1462961300343" FOLDED="true" ID="ID_1359956608" MODIFIED="1463647132298" TEXT="Rotating Movement Component ">
<node CREATED="1462961307390" ID="ID_1963731256" MODIFIED="1462961307936" TEXT="make your Actor spin around"/>
</node>
<node CREATED="1462961315261" FOLDED="true" ID="ID_794519963" MODIFIED="1463647132298" TEXT=" Audio Component">
<node CREATED="1462961320519" ID="ID_1317808141" MODIFIED="1462961321089" TEXT="make your Actor able to play sounds"/>
