<node CREATED="1462960220062" FOLDED="true" ID="ID_453880280" MODIFIED="1467367526310" TEXT="Pawns">
<node CREATED="1462961374521" ID="ID_795076696" MODIFIED="1462961374990" TEXT="a subclass of Actor"/>
<node CREATED="1462961384309" FOLDED="true" ID="ID_1544058768" MODIFIED="1463732445816" TEXT=" serve as an in-game avatar or persona">
<node CREATED="1462961390465" ID="ID_1099085856" MODIFIED="1462961398129" TEXT="example &#xff1a; the characters in a game"/>
</node>
<node CREATED="1462961410208" FOLDED="true" ID="ID_354920297" MODIFIED="1463732445816" TEXT="Pawns can be controlled by">
<node CREATED="1462961432431" ID="ID_1269817733" MODIFIED="1462961433427" TEXT=" a player"/>
<node CREATED="1462961446518" FOLDED="true" ID="ID_504832789" MODIFIED="1463647132309" TEXT=" the game&apos;s AI">
<node CREATED="1462961457982" ID="ID_1952528738" MODIFIED="1462961458414" TEXT="in the form of non-player characters (NPCs)."/>
</node>
</node>
<node CREATED="1462961511648" FOLDED="true" ID="ID_1535702000" MODIFIED="1463732445817" TEXT="Possessed">
<node CREATED="1462961470290" ID="ID_519760368" MODIFIED="1462961494097" TEXT="When a Pawn is controlled by a human or&#xa; AI player, it is considered as Possessed. "/>
</node>
<node CREATED="1462961515169" FOLDED="true" ID="ID_1684978624" MODIFIED="1463732445817" TEXT="Unpossessed">
<node CREATED="1462961501646" ID="ID_636971027" MODIFIED="1462961507722" TEXT="when a Pawn is not controlled by a human &#xa;or AI player it is considered as Unpossessed."/>
</node>
<node CREATED="1462961527162" FOLDED="true" ID="ID_1562321363" MODIFIED="1463732445817" TEXT="&#x53c2;&#x8003;">
<node CREATED="1462961531897" ID="ID_1058420541" LINK="https://docs.unrealengine.com/latest/INT/Gameplay/Framework/Pawn/index.html" MODIFIED="1462961536327" TEXT="Pawn"/>
<node CREATED="1462961540456" ID="ID_1920514937" LINK="https://docs.unrealengine.com/latest/INT/Gameplay/HowTo/PossessPawns/index.html" MODIFIED="1462961545486" TEXT="Possessing Pawns"/>
</node>
</node>
<node CREATED="1462960224439" FOLDED="true" ID="ID_1206803302" MODIFIED="1467367526310" TEXT="Characters">
<node CREATED="1462961559068" ID="ID_1504835095" MODIFIED="1462961582584" TEXT="a subclass of a Pawn Actor that is &#xa;intended to be used as a player "/>
<node CREATED="1462961593022" FOLDED="true" ID="ID_374375608" MODIFIED="1463732445818" TEXT="The Character subclass includes ">
<node CREATED="1462961600638" ID="ID_1449844662" MODIFIED="1462961601096" TEXT="a collision setup"/>
<node CREATED="1462961608275" ID="ID_1340086135" MODIFIED="1462961608617" TEXT="input bindings for bipedal movement"/>
<node CREATED="1462961615498" ID="ID_1754098442" MODIFIED="1462961620543" TEXT="additional code for movement &#xa;controlled by the player"/>
</node>
<node CREATED="1462961627989" FOLDED="true" ID="ID_159187787" MODIFIED="1463732445818" TEXT="&#x53c2;&#x8003;">
<node CREATED="1462961634390" ID="ID_1746309999" LINK="https://docs.unrealengine.com/latest/INT/Gameplay/Framework/Pawn/Character/index.html" MODIFIED="1462961638281" TEXT="Character"/>
<node CREATED="1462961646123" ID="ID_479063975" LINK="https://docs.unrealengine.com/latest/INT/Engine/Animation/CharacterSetupOverview/index.html" MODIFIED="1462961657032" TEXT="Character Setup"/>
<node CREATED="1462961661871" ID="ID_523957652" LINK="https://docs.unrealengine.com/latest/INT/Gameplay/HowTo/CharacterMovement/Blueprints/index.html" MODIFIED="1462961666229" TEXT="How To - Character Movement"/>
</node>
</node>
<node CREATED="1462960227837" FOLDED="true" ID="ID_1790908611" MODIFIED="1467367526311" TEXT="PlayerController">
<node CREATED="1462961690587" ID="ID_37228920" MODIFIED="1462961697689" TEXT="PlayerController Class is used to take player&#xa; input and translate that into interactions in &#xa;the game"/>
<node CREATED="1462961705020" ID="ID_41301659" MODIFIED="1462961706340" TEXT="every game has at least one PlayerController in it."/>
<node CREATED="1462961730843" ID="ID_1401610395" MODIFIED="1462961737684" TEXT=" A PlayerController often possesses a Pawn &#xa;or Character as a representation of the player&#xa; in a game."/>
<node CREATED="1462961751510" FOLDED="true" ID="ID_216541358" MODIFIED="1463732445818" TEXT="The PlayerController is also the primary network &#xa;interaction point for multiplayer games. ">
<node CREATED="1462961769916" FOLDED="true" ID="ID_1589743682" MODIFIED="1463647132323" TEXT="During multiplayer play, the server has one &#xa;instance of a PlayerController for every player ">
<node CREATED="1462961781920" ID="ID_908923718" MODIFIED="1462961788009" TEXT="ince it must be able to make network &#xa;function calls to each player."/>
<node CREATED="1462961843270" ID="ID_747319637" MODIFIED="1462961851170" TEXT=" Each client only has the PlayerController that&#xa; corresponds to their player and can only use&#xa; their PlayerController to communicate with the&#xa; server."/>
</node>
</node>
<node CREATED="1462961854299" ID="ID_1459223036" LINK="https://docs.unrealengine.com/latest/INT/Gameplay/Framework/Controller/PlayerController/index.html" MODIFIED="1462961864285" TEXT="&#x53c2;&#x8003; PlayerController"/>
</node>
<node CREATED="1462960231010" FOLDED="true" ID="ID_1915323955" MODIFIED="1467367526312" TEXT="AIController">
<node CREATED="1462961882356" ID="ID_1268199887" MODIFIED="1462961889299" TEXT="AIController possesses a Pawn to represent&#xa; a non-player character (NPC)"/>
<node CREATED="1462961927110" ID="ID_445567853" MODIFIED="1462961941141" TEXT=" By default, Pawns and Characters will end up&#xa; with a base AIController unless they are specifically&#xa; possessed by a PlayerController or told not to creat&#xa;e an AIController for themselves."/>
<node CREATED="1462961951597" ID="ID_1332738506" LINK="https://docs.unrealengine.com/latest/INT/Gameplay/Framework/Controller/AIController/index.html" MODIFIED="1462961959093" TEXT="&#x53c2;&#x8003; AIController"/>
</node>
<node CREATED="1462960234688" FOLDED="true" ID="ID_1571563781" MODIFIED="1467367526312" TEXT="Brushes">
<node CREATED="1462961987656" FOLDED="true" ID="ID_286354870" MODIFIED="1463732445821" TEXT=" Brush is an Actor that describes a&#xa; 3D volume that is placed in a level&#xa;(referred to as BSPs) and gameplay &#xa;volumes">
<node CREATED="1462962464831" ID="ID_1673849292" MODIFIED="1462962491428" TEXT="BSP Binary space partitioning"/>
</node>
<node CREATED="1462962506685" ID="ID_723918594" MODIFIED="1462962515180" TEXT="ou will use BSP Brushes to prototype or &#xa;block-out your levels for gameplay testing"/>
<node CREATED="1462962533968" FOLDED="true" ID="ID_1991968446" MODIFIED="1463732445824" TEXT="Volumes on the other hand have several &#xa;uses depending upon the effects attached&#xa; to them such as">
<node CREATED="1462962545206" FOLDED="true" ID="ID_85376067" MODIFIED="1463647132338" TEXT="Blocking Volumes">
<node CREATED="1462962553679" ID="ID_324168522" MODIFIED="1462962558652" TEXT="which are invisible and used to prevent &#xa;Actors from passing through them"/>
</node>
<node CREATED="1462962567421" FOLDED="true" ID="ID_1198106356" MODIFIED="1463647132339" TEXT="Pain Causing Volumes">
<node CREATED="1462962585687" ID="ID_319159270" MODIFIED="1462962592336" TEXT="which causes damage over time&#xa; to any Actor that overlaps it"/>
</node>
<node CREATED="1462962575449" FOLDED="true" ID="ID_569202540" MODIFIED="1463647132339" TEXT="Trigger Volumes">
<node CREATED="1462962615737" ID="ID_1811896883" MODIFIED="1462962627291" TEXT="used as a way to cause events &#xa;when an Actor enters or exits them"/>
</node>
</node>
<node CREATED="1462962630295" FOLDED="true" ID="ID_1156899336" MODIFIED="1463732445827" TEXT="&#x53c2;&#x8003;">
<node CREATED="1462962636402" ID="ID_1457845442" LINK="https://docs.unrealengine.com/latest/INT/Engine/Actors/Brushes/index.html" MODIFIED="1462962640406" TEXT="Geometry Brush Actors"/>
<node CREATED="1462962644523" ID="ID_1237609625" LINK="https://docs.unrealengine.com/latest/INT/Resources/ContentExamples/Brushes/index.html" MODIFIED="1462962651971" TEXT="Geometry Editing Content Examples"/>
</node>
</node>
<node CREATED="1462960242699" FOLDED="true" ID="ID_755607116" MODIFIED="1467367526313" TEXT="Levels">
<node CREATED="1462962666363" ID="ID_625049033" MODIFIED="1462962666887" TEXT="A Level is a user defined area of gameplay"/>
<node CREATED="1462962682845" ID="ID_1214421503" MODIFIED="1462962691184" TEXT="Levels are created, viewed, and modified&#xa; mainly by placing, transforming, and &#xa;editing the properties of the Actors it contains"/>
<node CREATED="1462962706799" FOLDED="true" ID="ID_966918586" MODIFIED="1467367522963" TEXT="In the Unreal Editor, each Level is saved&#xa; as a separate .umap file">
<node CREATED="1462962723607" ID="ID_977934073" MODIFIED="1462962726252" TEXT="you will sometimes see them referred to as Maps."/>
</node>
<node CREATED="1462962730037" FOLDED="true" ID="ID_1434166862" MODIFIED="1463732445831" TEXT="&#x53c2;&#x8003;">
<node CREATED="1462962734238" ID="ID_559168911" LINK="https://docs.unrealengine.com/latest/INT/Engine/Levels/index.html" MODIFIED="1462962742585" TEXT="Levels"/>
<node CREATED="1462962746948" ID="ID_763576008" LINK="https://docs.unrealengine.com/latest/INT/Engine/UI/LevelEditor/index.html" MODIFIED="1462962751293" TEXT="Level Editor"/>
<node CREATED="1462962755825" ID="ID_93728721" LINK="https://docs.unrealengine.com/latest/INT/Resources/ContentExamples/LevelDesign/index.html" MODIFIED="1462962761114" TEXT="Level Design Content Examples"/>
</node>
</node>
<node CREATED="1462960246164" FOLDED="true" ID="ID_504925517" MODIFIED="1467367526313" TEXT="World">
<node CREATED="1462962781615" ID="ID_1672612550" MODIFIED="1462962782095" TEXT="A World contains a list of Levels that are loaded."/>
<node CREATED="1462962815254" FOLDED="true" ID="ID_1800542201" MODIFIED="1463732445831" TEXT=" It handles ">
<node CREATED="1462962819371" ID="ID_346690156" MODIFIED="1462962819817" TEXT=" the streaming of Levels"/>
<node CREATED="1462962826840" ID="ID_1016195649" MODIFIED="1462962827226" TEXT="the spawning (creation) of dynamic Actors."/>
</node>
<node CREATED="1462962842141" FOLDED="true" ID="ID_1319600340" MODIFIED="1463732445831" TEXT="Direct interaction with a World is not necessary&#xa;but it does help provide a specific reference &#xa;point within the game structure">
<node CREATED="1462962873691" ID="ID_365504583" MODIFIED="1462962880903" TEXT="ie: mentioning World directly means you &#xa;are not speaking about Levels, Maps or &#xa;the game"/>
</node>
<node CREATED="1462962891566" ID="ID_257294986" LINK="https://docs.unrealengine.com/latest/INT/Engine/LevelStreaming/WorldBrowser/index.html" MODIFIED="1462962903469" TEXT="&#x53c2;&#x8003; World Composition User Guide"/>
</node>
<node CREATED="1462960250057" FOLDED="true" ID="ID_642802798" MODIFIED="1467367526313" TEXT="GameModes">
<node CREATED="1462962927030" ID="ID_1717768385" MODIFIED="1462962933826" TEXT="The GameMode Class is responsible for &#xa;setting the rules of the game that is being&#xa; played"/>
<node CREATED="1462962942592" FOLDED="true" ID="ID_314372644" MODIFIED="1463732445835" TEXT="The rules can include">
<node CREATED="1462962948466" ID="ID_1392696586" MODIFIED="1462962948941" TEXT="how players join the game"/>
<node CREATED="1462962954006" ID="ID_125817" MODIFIED="1462962954291" TEXT=" whether or not a game can be paused"/>
<node CREATED="1462962958450" ID="ID_240355228" MODIFIED="1462962958724" TEXT=" level transitions"/>
<node CREATED="1462962978096" ID="ID_680477271" MODIFIED="1462962978818" TEXT="game-specific behavior such as win conditions."/>
</node>
<node CREATED="1462963010833" ID="ID_635190313" MODIFIED="1462963011377" TEXT="can set the default GameMode in the Project Settings"/>
<node CREATED="1462963028272" ID="ID_823666798" MODIFIED="1462963029074" TEXT="can override it on a per-Level basis."/>
<node CREATED="1462963045015" FOLDED="true" ID="ID_1086245243" MODIFIED="1463732445836" TEXT=" there is always only one GameMode present per-level.">
<node CREATED="1462963062600" ID="ID_519411503" MODIFIED="1462963076631" TEXT=" In a multiplayer game, the GameMode &#xa;only exists on the server and the rules &#xa;are replicated (sent) to each of the&#xa; connected clients."/>
</node>
</node>
<node CREATED="1462960255761" FOLDED="true" ID="ID_1294067253" MODIFIED="1467367526314" TEXT="GameStates">
<node CREATED="1462963122879" FOLDED="true" ID="ID_1599261347" MODIFIED="1463732445836" TEXT=" &apos;The State of the Game&apos; for everyone connected.">
<node CREATED="1462963105576" ID="ID_1778697695" MODIFIED="1462963110529" TEXT="The GameState contains the information that&#xa; you want replicated to every client in a game"/>
</node>
<node CREATED="1462963140645" FOLDED="true" ID="ID_541061835" MODIFIED="1463732445837" TEXT="contains">
<node CREATED="1462963147148" ID="ID_773145456" MODIFIED="1462963147683" TEXT="information about game score"/>
<node CREATED="1462963152794" ID="ID_1603932214" MODIFIED="1462963153225" TEXT="whether a match has started or not"/>
<node CREATED="1462963182912" ID="ID_1751624925" MODIFIED="1462963186753" TEXT=" how many AI to spawn based upon the &#xa;number of players in the world"/>
</node>
<node CREATED="1462969807687" FOLDED="true" ID="ID_1478082110" MODIFIED="1463732445837" TEXT="For mutliplayer games">
<node CREATED="1462969812411" ID="ID_1771847289" MODIFIED="1462969812958" TEXT=" there is one instance of the GameState on each player&apos;s machine "/>
<node CREATED="1462969818296" FOLDED="true" ID="ID_381418502" MODIFIED="1463647132372" TEXT="the server&apos;s instance being the authoritative one">
<node CREATED="1462969827028" ID="ID_1678584955" MODIFIED="1462969827977" TEXT=" the one that clients get their updated information from"/>
</node>
</node>
<node CREATED="1462969834239" ID="ID_343413515" LINK="https://docs.unrealengine.com/latest/INT/Gameplay/Framework/GameState/index.html" MODIFIED="1462969843349" TEXT="&#x53c2;&#x8003; GameState"/>
</node>
<node CREATED="1462960259665" FOLDED="true" ID="ID_585961424" MODIFIED="1467367526314" TEXT="PlayerStates">
<node CREATED="1462969853340" FOLDED="true" ID="ID_111300598" MODIFIED="1463732445838" TEXT=" is the state of a participant in the game">
<node CREATED="1462969861632" ID="ID_1620369524" MODIFIED="1462969862270" TEXT="a human player "/>
<node CREATED="1462969866513" FOLDED="true" ID="ID_757826645" MODIFIED="1463647132374" TEXT=" a bot ">
<node CREATED="1462969871353" ID="ID_940766356" MODIFIED="1462969871787" TEXT="that is simulating a player."/>
</node>
</node>
<node CREATED="1462969974527" ID="ID_437091880" MODIFIED="1462969988384" TEXT="Non-player AI &#x6ca1;&#x6709; PlayerState"/>
<node CREATED="1462970006057" FOLDED="true" ID="ID_345035415" MODIFIED="1463732445838" TEXT="Example data that would be appropriate&#xa; in a PlayerState include ">
<node CREATED="1462970018714" ID="ID_1425935001" MODIFIED="1462970019508" TEXT="player name or score"/>
<node CREATED="1462970024989" ID="ID_1995168647" MODIFIED="1462970025353" TEXT="their current level or health"/>
<node CREATED="1462970052499" ID="ID_1908603330" MODIFIED="1462970058385" TEXT="whether they are currently carrying the &#xa;flag in a Capture the Flag game."/>
</node>
<node CREATED="1462970071015" FOLDED="true" ID="ID_593813710" MODIFIED="1463732445841" TEXT="For multiplayer games, ">
<node CREATED="1462970076240" FOLDED="true" ID="ID_342189863" MODIFIED="1463647132378" TEXT="PlayerStates for all players exist on all machines">
<node CREATED="1462970082025" ID="ID_20918510" MODIFIED="1462970082490" TEXT="unlike PlayerControllers"/>
</node>
<node CREATED="1462970088825" ID="ID_433207526" MODIFIED="1462970093082" TEXT=" can replicate data from the server to&#xa; the client to keep things in sync."/>
</node>
<node CREATED="1462970097742" ID="ID_1704842358" LINK="https://docs.unrealengine.com/latest/INT/Gameplay/Framework/QuickReference/index.html" MODIFIED="1462970106007" TEXT="&#x53c2;&#x8003; Gameplay Framework Quick Reference"/>
</node>
</node>
