<node CREATED="1463566381103" ID="ID_469190420" MODIFIED="1463566381582" TEXT=" Min Roughness property"/>
<node CREATED="1463566508879" FOLDED="true" ID="ID_684348873" MODIFIED="1463578444120" TEXT="&#x5f71;&#x54cd; Lightmass when rendering lightmaps.">
<node CREATED="1463566539660" FOLDED="true" ID="ID_1040942260" MODIFIED="1463578444120" TEXT="Larger radius lights cast softer shadows">
