<node CREATED="1463561631401" FOLDED="true" ID="ID_1315919913" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/LightTypes/index.html" MODIFIED="1466090547094" TEXT="Types of Lights">
<node CREATED="1463581101037" FOLDED="true" ID="ID_1485420502" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/LightTypes/Directional/index.html" MODIFIED="1467367523118" TEXT="Directional Lights">
<node CREATED="1466089981199" FOLDED="true" ID="ID_1161049099" MODIFIED="1466090067120" TEXT="The Directional Light simulates light that is being &#xa;emitted from a source that is infinitely far away">
<node CREATED="1466090021579" ID="ID_1529996266" MODIFIED="1466090022075" TEXT="all shadows cast by this light will be parallel"/>
<node CREATED="1466090044508" ID="ID_1671167691" MODIFIED="1466090045048" TEXT="making this the ideal choice for simulating sunlight"/>
</node>
<node CREATED="1466090056850" FOLDED="true" ID="ID_662484487" MODIFIED="1467367523118" TEXT="The Directional Light when placed can &#xa;be set to one of three Mobility settings:">
<node CREATED="1466090070171" FOLDED="true" ID="ID_1936537712" MODIFIED="1467367523117" TEXT="Static">
<node CREATED="1466090086248" ID="ID_1386403840" MODIFIED="1466090086788" TEXT="cannot be changed in game"/>
<node CREATED="1466090103687" ID="ID_567824864" MODIFIED="1466090109920" TEXT="fastest method for rendering and allows for baked lighting."/>
</node>
<node CREATED="1466090072804" FOLDED="true" ID="ID_1512053212" MODIFIED="1467367523118" TEXT="Stationary">
<node CREATED="1466090134908" FOLDED="true" ID="ID_199421597" MODIFIED="1467367523118" TEXT=" light will only have its shadowing and bounced&#xa; lighting from static geometry baked by Lightmass">
<node CREATED="1466090169650" ID="ID_472534966" MODIFIED="1466090170168" TEXT="all other lighting will be dynamic"/>
</node>
<node CREATED="1466090193401" ID="ID_737863447" MODIFIED="1466090194020" TEXT="allows for the light to change color and intensity in game, "/>
<node CREATED="1466090200117" ID="ID_1660043998" MODIFIED="1466090200601" TEXT="it does not move and allows partial baked lighting."/>
</node>
<node CREATED="1466090075279" FOLDED="true" ID="ID_1738639248" MODIFIED="1467367523118" TEXT="Moveable">
<node CREATED="1466090218468" ID="ID_1125143692" MODIFIED="1466090237883" TEXT=" the light is totally dynamic, &#xa;allows for dynamic shadowing. "/>
<node CREATED="1466090293949" ID="ID_582089498" MODIFIED="1466090294444" TEXT="slowest in terms of rendering "/>
<node CREATED="1466090299198" ID="ID_684508820" MODIFIED="1466090299755" TEXT="allows for the most flexibility during gameplay"/>
</node>
</node>
</node>
<node CREATED="1463581112118" ID="ID_727006571" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/LightTypes/Point/index.html" MODIFIED="1463581127640" TEXT="Point Lights"/>
<node CREATED="1463581133178" ID="ID_621294078" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/LightTypes/SkyLight/index.html" MODIFIED="1463581138696" TEXT="Sky Lights"/>
<node CREATED="1463581143269" ID="ID_1133983252" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/LightTypes/Spot/index.html" MODIFIED="1463581148357" TEXT="Spot Light"/>
</node>
<node CREATED="1463561590182" FOLDED="true" ID="ID_1254106894" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/LightMobility/index.html" MODIFIED="1466094724458" TEXT="Light Mobility">
<icon BUILTIN="button_ok"/>
<node CREATED="1463562520947" ID="ID_1550954239" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/LightMobility/DynamicLights/index.html" MODIFIED="1463562528058" TEXT="Movable Lights"/>
<node CREATED="1463562377207" FOLDED="true" ID="ID_684390441" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/LightMobility/StaticLights/index.html" MODIFIED="1467367523119" TEXT="Static Lights">
<node CREATED="1466093362754" FOLDED="true" ID="ID_1673135551" MODIFIED="1466094318664" TEXT="&#x4ecb;&#x7ecd;">
<node CREATED="1466093369952" ID="ID_35371711" MODIFIED="1466093390552" TEXT="are lights that cannot be changed &#xa;or moved in any way at runtime"/>
<node CREATED="1466093398484" FOLDED="true" ID="ID_700203723" MODIFIED="1467367523119" TEXT=" They are calculated only within Lightmaps">
<node CREATED="1466093407845" ID="ID_201631451" MODIFIED="1466093408514" TEXT="once processed, have no further impact on performance"/>
<node CREATED="1466093503601" FOLDED="true" ID="ID_1561829136" MODIFIED="1467367523119" TEXT="Since Static lights only use Lightmaps, their &#xa;shadows are baked prior to gameplay">
<node CREATED="1466093523031" ID="ID_882828407" MODIFIED="1466093562351" TEXT="they cannot shadow moving (dynamic) objects,"/>
<node CREATED="1466093546849" FOLDED="true" ID="ID_11527397" MODIFIED="1467367523119" TEXT="when lighting objects that are also static,&#xa; they have the ability to produce area&#xa; (contact) shadows.">
<node CREATED="1466093606635" ID="ID_917068953" MODIFIED="1466093609109" TEXT="This is done by adjusting the Source Radius property."/>
<node CREATED="1466094259170" ID="ID_1259351310" MODIFIED="1466094307131" TEXT="surfaces receiving soft shadows will likely &#xa;have to have their Lightmap Resolution set &#xa;appropriately in order for the shadows to &#xa;look good."/>
</node>
</node>
</node>
<node CREATED="1466093438739" FOLDED="true" ID="ID_1846587175" MODIFIED="1466093440979" TEXT="usefulness of static lights is limited.">
<node CREATED="1466093425903" ID="ID_1138953924" MODIFIED="1466093426424" TEXT=" Movable objects cannot integrate with static lights"/>
</node>
<node CREATED="1466093457023" FOLDED="true" ID="ID_399566981" MODIFIED="1466093486432" TEXT="&#x7279;&#x70b9;">
<node CREATED="1466093465106" ID="ID_487096993" MODIFIED="1466093465396" TEXT="medium quality"/>
<node CREATED="1466093469916" ID="ID_177726411" MODIFIED="1466093470403" TEXT=" lowest mutability"/>
<node CREATED="1466093475440" ID="ID_717216315" MODIFIED="1466093475826" TEXT=" lowest performance cost"/>
</node>
</node>
</node>
<node CREATED="1463562460906" FOLDED="true" ID="ID_797350887" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/LightMobility/StationaryLights/index.html" MODIFIED="1467367523120" TEXT="Stationary lights">
<node CREATED="1466094332762" FOLDED="true" ID="ID_852278239" MODIFIED="1467367523120" TEXT="&#x4ecb;&#x7ecd;">
<node CREATED="1466094336991" FOLDED="true" ID="ID_649399696" MODIFIED="1466094418402" TEXT="Stationary Lights are lights that ">
<node CREATED="1466094369226" ID="ID_616640933" MODIFIED="1466094369633" TEXT="are intended to stay in one position"/>
<node CREATED="1466094357750" FOLDED="true" ID="ID_1038091617" MODIFIED="1467367523119" TEXT=" but are able to change in other ways">
<node CREATED="1466094376201" FOLDED="true" ID="ID_753534472" MODIFIED="1467367523119" TEXT="such as ">
<node CREATED="1466094384893" ID="ID_1337593535" MODIFIED="1466094385181" TEXT="brightness"/>
<node CREATED="1466094387933" ID="ID_933264310" MODIFIED="1466094388218" TEXT="color"/>
</node>
</node>
</node>
<node CREATED="1466094416366" ID="ID_1809975456" MODIFIED="1466094416987" TEXT="runtime changes to brightness only affect the direct lighting"/>
<node CREATED="1466094436112" FOLDED="true" ID="ID_440309776" MODIFIED="1466094449207" TEXT="Indirect (bounced) lighting will not change.">
<node CREATED="1466094446125" ID="ID_1287503692" MODIFIED="1466094446891" TEXT=" since it is pre-calculated by Lightmass"/>
</node>
<node CREATED="1466094455497" FOLDED="true" ID="ID_489320503" MODIFIED="1466094494705" TEXT="&#x7279;&#x70b9;">
<node CREATED="1466094463877" ID="ID_267440612" MODIFIED="1466094464274" TEXT=" highest quality,"/>
<node CREATED="1466094469650" ID="ID_876273164" MODIFIED="1466094470136" TEXT="medium mutability,"/>
<node CREATED="1466094474532" ID="ID_157051070" MODIFIED="1466094474996" TEXT=" medium performance cost"/>
</node>
<node CREATED="1466094495547" ID="ID_350995556" MODIFIED="1466094499733" TEXT="All of the indirect lighting and shadowing from &#xa;Stationary Lights is stored within the Lightmap"/>
<node CREATED="1466094512752" ID="ID_1290846074" MODIFIED="1466094513508" TEXT="Direct shadows are stored within the Shadowmap."/>
<node CREATED="1466094532108" FOLDED="true" ID="ID_1969617302" MODIFIED="1467367523120" TEXT="These lights make use of Distance Field Shadows">
<node CREATED="1466094577523" ID="ID_1448714104" MODIFIED="1466094604050" TEXT="meaning that their shadows can remain crisp &#xa;even with fairly low Lightmap Resolution on lit &#xa;objects."/>
</node>
</node>
</node>
</node>
<node CREATED="1463561667210" ID="ID_560102260" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/Shadows/index.html" MODIFIED="1463561673863" TEXT="Shadow Casting"/>
<node CREATED="1463561876966" FOLDED="true" ID="ID_367827151" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/Lightmass/index.html" MODIFIED="1466430959408" TEXT="Lightmass Global Illumination">
<node CREATED="1466093180795" FOLDED="true" ID="ID_1248754889" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/Lightmass/" MODIFIED="1467367523130" TEXT="Lightmass Global Illumination">
<node CREATED="1466093196625" FOLDED="true" ID="ID_655688396" MODIFIED="1466095579372" TEXT="&#x4ecb;&#x7ecd;">
<node CREATED="1466093232486" FOLDED="true" ID="ID_639535387" MODIFIED="1466095352467" TEXT="Lightmass creates lightmaps with&#xa; complex light interactions">
<node CREATED="1466093247682" FOLDED="true" ID="ID_668614856" MODIFIED="1466095099609" TEXT="like">
<node CREATED="1466093255444" ID="ID_764912929" MODIFIED="1466093255826" TEXT="area shadowing "/>
<node CREATED="1466093259607" ID="ID_367240195" MODIFIED="1466093260000" TEXT="diffuse interreflection"/>
</node>
<node CREATED="1466095091719" FOLDED="true" ID="ID_1593471386" LINK="https://en.wikipedia.org/wiki/Lightmap" MODIFIED="1466095289608" TEXT="Lightmap">
<node CREATED="1466095121398" FOLDED="true" ID="ID_54956241" MODIFIED="1466095286391" TEXT="the brightness of surfaces in a virtual scene is &#xa;pre-calculated and stored in texture maps for&#xa; later use. ">
<node CREATED="1466095171384" FOLDED="true" ID="ID_1096440922" MODIFIED="1467367523121" TEXT="pre-calculated and stored &#xff1d; Baking&#xa;">
<node CREATED="1466095145183" FOLDED="true" ID="ID_436599930" LINK="https://en.wikipedia.org/wiki/Glossary_of_computer_graphics#Baking" MODIFIED="1467367523120" TEXT="Baking">
<node CREATED="1466095203147" ID="ID_1794154225" MODIFIED="1466095209051" TEXT="Performing an expensive calculation offline, and &#xa;caching the results in a Texture map or Vertex &#xa;attributes. "/>
<node CREATED="1466095217054" FOLDED="true" ID="ID_1770998508" MODIFIED="1467367523120" TEXT="Typically used for generating">
<node CREATED="1466095220141" ID="ID_1657682896" LINK="https://en.wikipedia.org/wiki/Lightmaps" MODIFIED="1466095224911" TEXT="lightmaps"/>
<node CREATED="1466095229093" ID="ID_1692263695" MODIFIED="1466095229465" TEXT="normal maps"/>
<node CREATED="1466095233435" ID="ID_1963603227" MODIFIED="1466095234010" TEXT="level of detail"/>
</node>
</node>
</node>
</node>
</node>
</node>
<node CREATED="1466093273963" ID="ID_722576557" MODIFIED="1466093317376" TEXT="Lightmass is used to precompute portions of the lighting contribution of lights with &#xa;- stationary and &#xa;- static mobility"/>
<node CREATED="1466095353796" FOLDED="true" ID="ID_722852341" MODIFIED="1466095559706" TEXT="Swarm Agent&#xa;&#x5206;&#x5e03;&#x5f0f;&#x8ba1;&#x7b97;">
<node CREATED="1466095359579" ID="ID_853786385" MODIFIED="1466095365898" TEXT="Communication between the editor and &#xa;Lightmass is handled by the Swarm Agent"/>
<node CREATED="1466095404087" ID="ID_903066687" MODIFIED="1466095404640" TEXT="manages the lighting build locally"/>
<node CREATED="1466095414393" ID="ID_789733524" MODIFIED="1466095415239" TEXT="distribute the lighting build to remote machines"/>
<node CREATED="1466095430448" ID="ID_807163599" MODIFIED="1466095432733" TEXT="opens minimized by default"/>
<node CREATED="1466095508179" ID="ID_750822812" MODIFIED="1466095508789" TEXT="tracks lighting build progress "/>
<node CREATED="1466095523300" ID="ID_515252383" MODIFIED="1466095523809" TEXT="keeps you up to date with which machines are working for you"/>
</node>
</node>
<node CREATED="1466095583701" FOLDED="true" ID="ID_1453632849" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/Lightmass/#featuresforstaticandstationarylights" MODIFIED="1466151342321" TEXT="Features for Static and Stationary lights">
<node CREATED="1466096004519" FOLDED="true" ID="ID_162237685" MODIFIED="1467367523121" TEXT="Diffuse Interreflection">
<node CREATED="1466151223102" ID="ID_371172485" MODIFIED="1466151223692" TEXT=" the most visually important global illumination lighting effect"/>
<node CREATED="1466151247145" FOLDED="true" ID="ID_1196175224" MODIFIED="1467367523121" TEXT="&#x8fc7;&#x7a0b;">
<node CREATED="1466151253285" ID="ID_105869757" MODIFIED="1466151253687" TEXT="Light bounces by default with Lightmass"/>
<node CREATED="1466151265525" ID="ID_1782241776" MODIFIED="1466151273178" TEXT="the BaseColor term of your material controls&#xa; how much light (and what color) bounces in&#xa; all directions."/>
</node>
<node CREATED="1466151283269" ID="ID_1808113578" MODIFIED="1466151283725" TEXT="called Color Bleeding"/>
<node CREATED="1466151300031" FOLDED="true" ID="ID_821227888" MODIFIED="1467367523121" TEXT=" is incoming light reflecting equally in all directions">
<node CREATED="1466151313770" ID="ID_1085430224" MODIFIED="1466151319327" TEXT=" which means that it is not affected by &#xa;the viewing direction or position."/>
</node>
</node>
<node CREATED="1466096013307" FOLDED="true" ID="ID_119915928" MODIFIED="1466151209997" TEXT="Character lighting">
<node CREATED="1466096020969" ID="ID_1678995170" MODIFIED="1466096021904" TEXT="Limitations"/>
</node>
<node CREATED="1466096026954" FOLDED="true" ID="ID_294436185" MODIFIED="1466150170461" TEXT="Ambient Occlusion">
<node CREATED="1466148527632" FOLDED="true" ID="ID_245536866" MODIFIED="1466148569289" TEXT="&#x4ecb;&#x7ecd;">
<node CREATED="1466148466816" FOLDED="true" ID="ID_1131106185" MODIFIED="1466148557937" TEXT="Ambient occlusion is the indirect shadowing&#xa; you would get from a uniformly bright upper&#xa; hemisphere">
<node CREATED="1466148504646" ID="ID_1030503278" MODIFIED="1466148505187" TEXT=" a uniformly bright upper hemisphere, like an overcast sky"/>
</node>
<node CREATED="1466148537760" ID="ID_1700525763" MODIFIED="1466148556483" TEXT="Lightmass supports &#xa;&#xff0d;calculating ambient occlusion, &#xa;&#xff0d;applying it to direct and indirect illumination,&#xa;&#xff0d; baking it into lightmaps."/>
</node>
<node CREATED="1466148446713" FOLDED="true" ID="ID_328466128" MODIFIED="1466150098300" TEXT="Ambient Occlusion Settings">
<node CREATED="1466149195175" FOLDED="true" ID="ID_866986736" MODIFIED="1466149197883" TEXT="Visualize Ambient Occlusion">
<node CREATED="1466148574336" FOLDED="true" ID="ID_654924143" MODIFIED="1467367523122" TEXT="Overrides lightmaps with just the&#xa; occlusion factor when lighting is built.">
<node CREATED="1466149169220" ID="ID_1579630740" MODIFIED="1466149176937" TEXT=" This is useful for seeing exactly what the &#xa;occlusion factor is, and comparing the &#xa;effects of different settings."/>
</node>
</node>
<node CREATED="1466149190112" FOLDED="true" ID="ID_1698148434" MODIFIED="1466150081447" TEXT="Max Occlusion Distance">
<node CREATED="1466149207595" ID="ID_863044982" MODIFIED="1466149225067" TEXT="Maximum distance for an object to &#xa;cause occlusion on another object."/>
<node CREATED="1466149939957" FOLDED="true" ID="ID_1665979879" MODIFIED="1467367523122" TEXT="&#x4f8b;&#x5b50;">
<node CREATED="1466149947821" FOLDED="true" ID="ID_1420138614" MODIFIED="1467367523122" TEXT="MaxOcclusionDistance of 5">
<node CREATED="1466149962255" ID="ID_369602523" MODIFIED="1466149998945" TEXT="Low frequency occlusion is removed,&#xa;only occlusion in corners is left."/>
</node>
</node>
</node>
<node CREATED="1466149231750" FOLDED="true" ID="ID_1687861328" MODIFIED="1466149786188" TEXT="Fully Occluded Samples Fraction">
<node CREATED="1466149288082" ID="ID_397970872" MODIFIED="1466149296553" TEXT="Fraction of samples taken that must be&#xa; occluded in order to reach full occlusion."/>
<node CREATED="1466149326424" FOLDED="true" ID="ID_1794159366" MODIFIED="1467367523122" TEXT=" per-primitive FullyOccludedSamplesFraction">
<node CREATED="1466149334107" ID="ID_557448290" MODIFIED="1466149334595" TEXT="controlling how much occlusion an object causes on other objects"/>
</node>
<node CREATED="1466149630525" FOLDED="true" ID="ID_116518363" MODIFIED="1467367523122" TEXT="&#x4f8b;&#x5b50;">
<node CREATED="1466149638983" FOLDED="true" ID="ID_1484317945" MODIFIED="1467367523122" TEXT="FullyOccludedSamplesFraction of 0.8. ">
<node CREATED="1466149643234" ID="ID_507736080" MODIFIED="1466149651720" TEXT="Occlusion is shifted darker across all ranges, &#xa;any areas that were at 80% occluded or&#xa; above saturate to black."/>
</node>
</node>
</node>
<node CREATED="1466149350668" FOLDED="true" ID="ID_1291077254" MODIFIED="1466149785311" TEXT="Occlusion Exponent">
<node CREATED="1466149354764" ID="ID_234569802" MODIFIED="1466149355207" TEXT="Higher exponents increase contrast."/>
<node CREATED="1466149673061" FOLDED="true" ID="ID_560164788" MODIFIED="1467367523123" TEXT="&#x4f8b;&#x5b50;">
<node CREATED="1466149681285" FOLDED="true" ID="ID_1851156444" MODIFIED="1467367523123" TEXT="OcclusionExponent of 2">
<node CREATED="1466149696407" ID="ID_1282079298" MODIFIED="1466149781079" TEXT="The occlusion transitions from midrange&#xa; to saturated dark much more quickly"/>
</node>
</node>
</node>
<node CREATED="1466150089287" ID="ID_9453538" MODIFIED="1466150096431" TEXT="Ambient occlusion is nearly free as far as &#xa;lighting build time goes when Num Indirect &#xa;Lighting Bounces is greater than 0."/>
</node>
<node CREATED="1466096031725" FOLDED="true" ID="ID_366006452" MODIFIED="1467367523123" TEXT="Limitations">
<node CREATED="1466150110944" FOLDED="true" ID="ID_1978396273" MODIFIED="1467367523123" TEXT="Ambient occlusion requires a fairly high &#xa;lightmap resolution to look good">
<node CREATED="1466150120372" ID="ID_1149096010" MODIFIED="1466150120860" TEXT=" since it changes quickly in corners."/>
<node CREATED="1466150135954" FOLDED="true" ID="ID_1253919424" MODIFIED="1467367523123" TEXT="Preview quality builds do not do a very &#xa;good job at previewing ambient occlusion, ">
<node CREATED="1466150157881" ID="ID_1022577353" MODIFIED="1466150167174" TEXT="since AO requires pretty dense lighting &#xa;samples (just like indirect shadows)."/>
</node>
</node>
</node>
</node>
<node CREATED="1466096038497" FOLDED="true" ID="ID_1851714212" MODIFIED="1466151208050" TEXT="Masked shadows">
<node CREATED="1466150183904" ID="ID_60557285" MODIFIED="1466150194784" TEXT="Lightmass takes the opacity mask of BLEND_Masked &#xa;Materials into account when calculating shadows. "/>
<node CREATED="1466150258641" ID="ID_273739638" MODIFIED="1466150295464" TEXT="The part of the material that gets clipped &#xa;in the editor viewports also does not cause &#xa;any shadowing, &#xa;which allows much more detailed shadowing&#xa; from trees and foliage."/>
</node>
</node>
<node CREATED="1466095593332" FOLDED="true" ID="ID_146603719" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/Lightmass/#featuresforstationarylightsonly" MODIFIED="1466151150785" TEXT="Features for Stationary Lights Only">
<node CREATED="1466150374318" FOLDED="true" ID="ID_282412591" MODIFIED="1466150991669" TEXT="Bent Normal sky occlusion">
<node CREATED="1466150404695" FOLDED="true" ID="ID_1188772896" MODIFIED="1467367523124" TEXT="Lightmass generates directional occlusion&#xa;in the form of a bent normal whenever a &#xa;Sky Light with Stationary Mobility is enabled.">
<node CREATED="1466150556487" FOLDED="true" ID="ID_799504056" MODIFIED="1467367523124" TEXT="bent normal">
<node CREATED="1466150520227" ID="ID_1227177364" MODIFIED="1466150535392" TEXT="The Sky Light shadowing that Lightmass &#xa;bakes stores directional occlusion information&#xa; in what is called a Bent Normal. "/>
<node CREATED="1466150562652" ID="ID_1869470598" MODIFIED="1466150608273" TEXT="This is the direction from the texel &#xa;to the most un-occluded direction"/>
<node CREATED="1466150864234" FOLDED="true" ID="ID_1634662340" MODIFIED="1467367523124" TEXT="Areas that are mostly occluded use &#xa;this direction for sky lighting instead&#xa;of the surface normal, &#xa;">
<node CREATED="1466150910574" ID="ID_1301144305" MODIFIED="1466150911142" TEXT="which improves quality quite a bit in cracks."/>
</node>
</node>
</node>
</node>
<node CREATED="1466150380742" FOLDED="true" ID="ID_1212231644" MODIFIED="1466151149548" TEXT="Distance field shadowmaps">
<node CREATED="1466151009049" FOLDED="true" ID="ID_1239319945" MODIFIED="1467367523125" TEXT="Lightmass computes distance field &#xa;shadowmaps for Stationary Lights ">
<node CREATED="1466151027917" FOLDED="true" ID="ID_1224900883" MODIFIED="1467367523125" TEXT="Distance field shadow maps">
<node CREATED="1466151037334" FOLDED="true" ID="ID_1795360590" MODIFIED="1467367523124" TEXT=" maintain their curvy shape very well">
<node CREATED="1466151042588" ID="ID_822825072" MODIFIED="1466151043132" TEXT=" even under lower resolutions"/>
</node>
<node CREATED="1466151054221" ID="ID_1309983281" MODIFIED="1466151065876" TEXT="however they do not support &#xa;- area shadowing or &#xa;- translucent shadows.  "/>
</node>
</node>
</node>
</node>
<node CREATED="1466095603660" FOLDED="true" ID="ID_619034071" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/Lightmass/#featuresforstaticlightsonly" MODIFIED="1467367523130" TEXT="Features for Static Lights Only">
<node CREATED="1466151156578" FOLDED="true" ID="ID_1978881487" MODIFIED="1466152563197" TEXT="Area lights and shadows">
<node CREATED="1466151680445" FOLDED="true" ID="ID_1276887223" MODIFIED="1467367523126" TEXT="With Lightmass, all lights with Static Mobility &#xa;are area lights by default. ">
<node CREATED="1466152098898" FOLDED="true" ID="ID_812825947" MODIFIED="1467367523126" TEXT="shape">
<node CREATED="1466151704296" FOLDED="true" ID="ID_630019461" MODIFIED="1467367523126" TEXT=" The shape used by Point and Spot Light sources&#xa; is a sphere">
<node CREATED="1466151721700" FOLDED="true" ID="ID_1274578987" MODIFIED="1467367523125" TEXT="radius is set by Light Source Radius &#xa;under Lightmass Settings">
<node CREATED="1466151784714" FOLDED="true" ID="ID_1036184975" MODIFIED="1467367523125" TEXT="light source size is one of the two &#xa;factors controlling shadow softness">
<node CREATED="1466151836334" ID="ID_953870735" MODIFIED="1466151836799" TEXT="larger light sources will create softer shadows"/>
</node>
</node>
</node>
<node CREATED="1466151729519" FOLDED="true" ID="ID_774278469" MODIFIED="1467367523126" TEXT="Directional Light sources use a disk">
<node CREATED="1466151740106" ID="ID_236407039" MODIFIED="1466151740663" TEXT="positioned at the edge of the scene"/>
</node>
</node>
<node CREATED="1466152111960" ID="ID_1385061193" MODIFIED="1466152112690" TEXT="The other factor is distance from the receiving location to the shadow caster."/>
</node>
<node CREATED="1466152164736" FOLDED="true" ID="ID_1853482347" MODIFIED="1467367523126" TEXT="Area shadows get softer as this distance increases">
<node CREATED="1466152170699" ID="ID_78365400" MODIFIED="1466152171289" TEXT="like in real life"/>
</node>
<node CREATED="1466152180937" FOLDED="true" ID="ID_1306789728" MODIFIED="1466152397330" TEXT="example">
<node CREATED="1466152286533" FOLDED="true" ID="ID_172780459" MODIFIED="1466152396160" TEXT="a Static Directional Light with only static lighting">
<node CREATED="1466152292901" ID="ID_1146379695" MODIFIED="1466152293413" TEXT=" the penumbra size is the same everywhere"/>
</node>
<node CREATED="1466152332278" FOLDED="true" ID="ID_1265232416" MODIFIED="1466152395238" TEXT="Lightmass calculated area shadows whose &#xa;sharpness is controlled by both the light &#xa;source size and occluder distance.">
<node CREATED="1466152349413" ID="ID_1532426348" MODIFIED="1466152355860" TEXT="shadow of the pillar is much sharper close&#xa; to where the pillar meets the ground"/>
</node>
</node>
</node>
<node CREATED="1466151163126" FOLDED="true" ID="ID_609909859" MODIFIED="1466152657847" TEXT="Translucent shadows">
<node CREATED="1466152577168" ID="ID_1883301848" MODIFIED="1466152614801" TEXT="Light passing through a translucent material that is applied to a static shadow casting mesh &#xa;will lose some energy, &#xa;resulting in a translucent shadow."/>
</node>
<node CREATED="1466151169628" FOLDED="true" ID="ID_110451233" MODIFIED="1467367523129" TEXT="Translucent shadow color">
<node CREATED="1466152659376" FOLDED="true" ID="ID_1286629822" MODIFIED="1467367523127" TEXT="The amount of light passing through the material is called Transmission">
<node CREATED="1466152751630" FOLDED="true" ID="ID_437784874" MODIFIED="1467367523127" TEXT="ranges between 0 and 1 for each color channel">
<node CREATED="1466152760013" ID="ID_1922442941" MODIFIED="1466152790195" TEXT="0 : fully opaque"/>
<node CREATED="1466152772736" ID="ID_1540627559" MODIFIED="1466152783562" TEXT="1 : the incident light passes through unaffected"/>
</node>
</node>
<node CREATED="1466152816365" FOLDED="true" ID="ID_152359554" MODIFIED="1467367523128" TEXT=" material inputs">
<node CREATED="1466152825580" FOLDED="true" ID="ID_1855802921" MODIFIED="1467367523128" TEXT="There is no material input for Transmission,&#xa;so currently it is derived from the other material inputs as follows:">
<node CREATED="1466152851220" FOLDED="true" ID="ID_362925930" MODIFIED="1467367523127" TEXT="Lit materials">
<node CREATED="1466152882980" FOLDED="true" ID="ID_1104827801" MODIFIED="1467367523127" TEXT="BLEND_Translucent and BLEND_Additive: ">
<node CREATED="1466152902770" ID="ID_223524329" MODIFIED="1466152903684" TEXT="Transmission = Lerp(White, BaseColor, Opacity)"/>
</node>
<node CREATED="1466152887626" FOLDED="true" ID="ID_380891142" MODIFIED="1467367523127" TEXT="BLEND_Modulate: ">
<node CREATED="1466152907495" ID="ID_1667459461" MODIFIED="1466152908061" TEXT="Transmission = BaseColor"/>
</node>
</node>
<node CREATED="1466152855923" FOLDED="true" ID="ID_1342429585" MODIFIED="1467367523128" TEXT="Unlit materials">
<node CREATED="1466152914706" FOLDED="true" ID="ID_1819567532" MODIFIED="1467367523128" TEXT="BLEND_Translucent and BLEND_Additive: ">
<node CREATED="1466152922042" ID="ID_21302608" MODIFIED="1466152922799" TEXT="Transmission = Lerp(White, Emissive, Opacity)"/>
</node>
<node CREATED="1466152931784" FOLDED="true" ID="ID_1375621736" MODIFIED="1467367523128" TEXT="BLEND_Modulate:">
<node CREATED="1466152936060" ID="ID_565992929" MODIFIED="1466152936492" TEXT="Transmission = Emissive"/>
</node>
</node>
</node>
</node>
<node CREATED="1466153099967" FOLDED="true" ID="ID_1975375809" MODIFIED="1466153953748" TEXT="This means that">
<node CREATED="1466153111140" FOLDED="true" ID="ID_1113514975" MODIFIED="1466153223444" TEXT="at an opacity of 0,">
<node CREATED="1466153120523" FOLDED="true" ID="ID_1616575980" MODIFIED="1467367523129" TEXT="the material will not filter out incident lighting">
<node CREATED="1466153139705" ID="ID_916404838" MODIFIED="1466153140722" TEXT=" there will be no translucent shadow."/>
</node>
</node>
<node CREATED="1466153145859" FOLDED="true" ID="ID_1819434601" MODIFIED="1466153224344" TEXT="At an opacity of 1">
<node CREATED="1466153156626" FOLDED="true" ID="ID_1278136159" MODIFIED="1467367523129" TEXT="incident lighting will be filtered by the material&apos;s emissive or Base Color">
<node CREATED="1466153161508" ID="ID_1330774156" MODIFIED="1466153162065" TEXT="(depending on whether it is lit"/>
</node>
</node>
</node>
<node CREATED="1466153181388" FOLDED="true" ID="ID_1849493550" MODIFIED="1466153954873" TEXT="indirect lighting will sometimes wash out the translucent shadows">
<node CREATED="1466153189950" ID="ID_1589996704" MODIFIED="1466153202966" TEXT="make them less saturated than the emissive &#xa;or diffuse of the translucent material."/>
</node>
</node>
<node CREATED="1466151175940" FOLDED="true" ID="ID_1074037794" MODIFIED="1466153764571" TEXT="Translucent shadow sharpness">
<node CREATED="1466153258736" FOLDED="true" ID="ID_616585825" MODIFIED="1466153668739" TEXT="factors controlling translucent shadow sharpness">
<node CREATED="1466153277006" FOLDED="true" ID="ID_1762287033" MODIFIED="1466153436517" TEXT=" a large light source &#x4e0d; sharp">
<node CREATED="1466153318317" ID="ID_686600879" MODIFIED="1466153380219" TEXT="&#x6bd4;&#x5982;directional light with a Light Source Angle of 5"/>
</node>
<node CREATED="1466153389332" FOLDED="true" ID="ID_1979466722" MODIFIED="1467367523130" TEXT="a small light source sharp">
<node CREATED="1466153398928" ID="ID_551988132" MODIFIED="1466153405200" TEXT="&#x6bd4;&#x5982; (Light Source Angle of 0"/>
<node CREATED="1466153430889" FOLDED="true" ID="ID_1408716555" MODIFIED="1466153667164" TEXT=" small light source &#xff0b;">
<node CREATED="1466153535948" FOLDED="true" ID="ID_1122400107" MODIFIED="1467367523129" TEXT=" low lightmap resolution  &#x4e0d; sharp ">
<node CREATED="1466153485411" ID="ID_1527511385" MODIFIED="1466153492510" TEXT="lightmap resolution was too low to capture&#xa;  the sharp translucent shadows"/>
</node>
<node CREATED="1466153539154" FOLDED="true" ID="ID_422377153" MODIFIED="1467367523129" TEXT="material &#x7684; resolution &#x4e0d;&#x591f;">
<node CREATED="1466153566604" ID="ID_1780772542" MODIFIED="1466153650906" TEXT="material &#x7684; resolution controlled by Export &#xa;Resolution Scale in the Material Editor"/>
</node>
</node>
</node>
</node>
<node CREATED="1466153682967" FOLDED="true" ID="ID_731044103" MODIFIED="1467367523130" TEXT="Indirect light is also affected by Translucent Materials.">
<node CREATED="1466153732177" ID="ID_379726356" MODIFIED="1466153760902" TEXT="incoming light &#x7ecf;&#x8fc7; Transmission &#x6539;&#x53d8;&#x989c;&#x8272;, &#xa;and that light then bounces around the&#xa; scene with its color modified."/>
</node>
</node>
<node CREATED="1466151184321" FOLDED="true" ID="ID_1985878831" MODIFIED="1466153889856" TEXT="Limitations">
<node CREATED="1466153773523" FOLDED="true" ID="ID_1248169523" MODIFIED="1466153851853" TEXT="Translucent Materials currently do not scatter &#x6492; light,">
<node CREATED="1466153819087" ID="ID_1649307601" MODIFIED="1466153819645" TEXT="so they will not bleed color onto objects around them."/>
</node>
<node CREATED="1466153843164" FOLDED="true" ID="ID_1498217731" MODIFIED="1467367523130" TEXT="The first diffuse bounce is currently &#xa;not affected by translucent shadows">
<node CREATED="1466153877567" ID="ID_1384663874" MODIFIED="1466153888315" TEXT=" This means that first bounce indirect&#xa; light passing through a translucent &#xa;material will not be filtered by the &#xa;material&apos;s Transmission."/>
</node>
</node>
</node>
<node CREATED="1466095615488" ID="ID_1910510951" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/Lightmass/#gettingthebestqualitywithlightmass" MODIFIED="1466095619839" TEXT="Getting the Best Quality with Lightmass"/>
<node CREATED="1466095625363" ID="ID_661533275" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/Lightmass/#gettingthebestlightingbuildtimes" MODIFIED="1466095631148" TEXT="Getting the Best Lighting Build Times"/>
<node CREATED="1466095634803" ID="ID_851654991" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/Lightmass/#lightmasssettings" MODIFIED="1466095639507" TEXT="Lightmass Settings"/>
</node>
<node CREATED="1463577579260" FOLDED="true" ID="ID_614729631" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/Lightmass/Basics/index.html" MODIFIED="1466095996428" TEXT="Lightmass Basics">
<icon BUILTIN="ksmiletris"/>
<node CREATED="1463577659471" FOLDED="true" ID="ID_22782093" MODIFIED="1467367523131" TEXT="&#x4ecb;&#x7ecd;">
<node CREATED="1463577713192" FOLDED="true" ID="ID_1003973834" MODIFIED="1467367523131" TEXT="Lightmass creates lightmaps with&#xa; complex light interactions ">
<node CREATED="1463577724408" ID="ID_1922857060" MODIFIED="1463577724834" TEXT="area shadowing "/>
<node CREATED="1463577731393" ID="ID_1985513417" MODIFIED="1463577732387" TEXT=" diffuse interreflection"/>
</node>
<node CREATED="1463577754928" ID="ID_1156662279" MODIFIED="1463577768792" TEXT=" is used to precompute portions of the&#xa; lighting contribution of lights with &#xa; stationary and static mobility"/>
</node>
<node CREATED="1463577610613" FOLDED="true" ID="ID_752116700" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/Lightmass/Basics/index.html#lightmassimportancevolume" MODIFIED="1466095896657" TEXT="Lightmass Importance Volume">
<node CREATED="1463577866835" ID="ID_1709625220" MODIFIED="1463577890665" TEXT="actual playable area that needs high quality lighting&#xa;&#x76f8;&#x5bf9;&#x6574;&#x4e2a;&#x5730;&#x56fe;&#x7684;&#x5c3a;&#x5bf8;&#x6765;&#x8bf4;&#xff0c;&#x5f88;&#x5c0f;"/>
<node CREATED="1463577897885" ID="ID_1216220576" MODIFIED="1463577992388" TEXT="Lightmass emits photons based on the size of the level,&#xa;so those background meshes  &#x4f1a;&#x5927;&#x5927;&#x589e;&#x52a0;&#x88ab; emitted &#x7684; &#xa;photons &#x7684;&#x6570;&#x91cf;&#xa;"/>
<node CREATED="1463578066367" FOLDED="true" ID="ID_1124417948" MODIFIED="1466095831221" TEXT="The Lightmass Importance Volume controls &#xa;the area that Lightmass emits photons in, &#xa;allowing you to concentrate it only on the&#xa; area that needs detailed indirect lighting.">
<node CREATED="1463578086639" ID="ID_140438093" MODIFIED="1463578094156" TEXT="Areas outside the importance volume get &#xa;only one bounce of indirect lighting at a&#xa; lower quality."/>
</node>
<node CREATED="1463578132100" FOLDED="true" ID="ID_1998098018" MODIFIED="1466095872613" TEXT="&#x751f;&#x6210; Lightmass importantce Volume&#xa;&#x7684;&#x51e0;&#x79cd;&#x53cd;&#x5c04;&#x65b9;&#x5f0f;">
<node CREATED="1463578117375" ID="ID_1408593494" MODIFIED="1463578118519" TEXT="You can add a Lightmass Importance Volume to a level"/>
<node CREATED="1463578178170" ID="ID_810540159" MODIFIED="1463578178594" TEXT="convert a Brush into a Lightmass Importance Volume"/>
</node>
</node>
<node CREATED="1463577620558" ID="ID_558186611" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/Lightmass/Basics/index.html#building" MODIFIED="1463577635735" TEXT="Building"/>
<node CREATED="1463577642452" FOLDED="true" ID="ID_205682476" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/LightingAndShadows/Lightmass/Basics/index.html#qualitymodes" MODIFIED="1467367523131" TEXT="Quality Modes">
