</node>
<node CREATED="1466090779927" FOLDED="true" ID="ID_1099953105" MODIFIED="1467367523136" TEXT="example">
<node CREATED="1466090782943" ID="ID_936552924" MODIFIED="1466090783537" TEXT="a stained glass window."/>
</node>
</node>
<node CREATED="1466090640924" FOLDED="true" ID="ID_1239479288" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/Materials/HowTo/ColoredTransluscentShadows/#translucentshadowcolor" MODIFIED="1466154182044" TEXT="Translucent Shadow Color">
<node CREATED="1466090807447" ID="ID_745424870" MODIFIED="1466090827582" TEXT="Translucent Shadow Color is the term &#xa;used to describe how shadows will &#xa;pick up colors of objects that they pass&#xa; through also known as Transmission"/>
<node CREATED="1466090961819" FOLDED="true" ID="ID_1461074576" MODIFIED="1467367523136" TEXT="The amount of light that passes through the &#xa;material is determined by">
<node CREATED="1466090993613" ID="ID_337245944" MODIFIED="1466090994030" TEXT=" its opacity value  between 0 and 1 "/>
<node CREATED="1466091001624" ID="ID_1309249599" MODIFIED="1466091002130" TEXT="how much light is being cast onto the material."/>
</node>
<node CREATED="1466091009488" FOLDED="true" ID="ID_160398288" MODIFIED="1467367523137" TEXT="example">
<node CREATED="1466091015935" FOLDED="true" ID="ID_907954340" MODIFIED="1467367523136" TEXT="opacity was set to 0">
<node CREATED="1466091028064" ID="ID_1384418990" MODIFIED="1466091050168" TEXT="the light transmission for a colored shadow &#xa;from the material would be none"/>
</node>
<node CREATED="1466091066507" FOLDED="true" ID="ID_14253498" MODIFIED="1467367523136" TEXT="opacity value were set to 1, ">
<node CREATED="1466091072606" ID="ID_820543107" MODIFIED="1466091073191" TEXT="then it would be fully opaque."/>
</node>
</node>
</node>
<node CREATED="1466090660666" FOLDED="true" ID="ID_153224361" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/Materials/HowTo/ColoredTransluscentShadows/#materialsetup" MODIFIED="1466154661953" TEXT="Material Setup">
