<node CREATED="1466164469571" FOLDED="true" ID="ID_388993286" MODIFIED="1466164476109" TEXT="&#x4ecb;&#x7ecd;">
<node CREATED="1466161328278" ID="ID_1583008387" MODIFIED="1466161333026" TEXT="A Material is an asset that can be applied to &#xa;a mesh to control the visual look of the scene."/>
<node CREATED="1466161387973" ID="ID_1083610178" MODIFIED="1466161393823" TEXT=" when light from the scene hits the surface, &#xa;a Material is used to calculate how that light&#xa; interacts with that surface. "/>
<node CREATED="1466161418957" FOLDED="true" ID="ID_1126201113" MODIFIED="1467367523170" TEXT=" materals &#x4f7f;&#x7528;">
<node CREATED="1466161433144" ID="ID_398253769" MODIFIED="1466161442086" TEXT=" a variety of images (textures)"/>
<node CREATED="1466161447206" ID="ID_771585607" MODIFIED="1466161447628" TEXT="math expressions,"/>
<node CREATED="1466164437845" ID="ID_1349389506" MODIFIED="1466164438557" TEXT="various property settings inherent to the Material itself."/>
</node>
