<node CREATED="1466164521434" ID="ID_608020954" MODIFIED="1466164521787" TEXT="Diffuse Color"/>
<node CREATED="1466164525414" ID="ID_806499021" MODIFIED="1466164525677" TEXT="Specular Power"/>
</node>
<node CREATED="1466164544981" FOLDED="true" ID="ID_790877453" MODIFIED="1467367523171" TEXT=" you instead use properties more easily relatable to the real world">
<node CREATED="1466164599041" FOLDED="true" ID="ID_465518615" MODIFIED="1467367523170" TEXT="These include ">
<node CREATED="1466164602851" ID="ID_1979872518" MODIFIED="1466164603219" TEXT=" Base Color"/>
<node CREATED="1466164605814" ID="ID_23669517" MODIFIED="1466164606166" TEXT="Metallic"/>
