<node CREATED="1465373362060" FOLDED="true" ID="ID_1181817753" MODIFIED="1467367523249" TEXT="&#x4ecb;&#x7ecd;">
<node CREATED="1465373366830" ID="ID_1849585688" MODIFIED="1465373389021" TEXT="Sound Attenuation is the ability of a sound to lower &#xa;in volume as the player moves away from it"/>
<node CREATED="1465373451070" FOLDED="true" ID="ID_724328322" MODIFIED="1465374156412" TEXT="&lt; MinRadius">
<node CREATED="1465373467857" ID="ID_212508237" MODIFIED="1465373468404" TEXT=" volume of the sound is at 100%."/>
</node>
<node CREATED="1465373454794" FOLDED="true" ID="ID_492015148" MODIFIED="1467367523246" TEXT="MinRadius MaxRadius &#x4e4b;&#x95f4;">
<node CREATED="1465373574067" FOLDED="true" ID="ID_571480408" MODIFIED="1467367523245" TEXT="the volume linearly fades between 100% and silence.">
<node CREATED="1465373600473" FOLDED="true" ID="ID_779086504" MODIFIED="1467367523245" TEXT="The rate at which this fade occurs is based on">
<node CREATED="1465373608136" ID="ID_14144393" MODIFIED="1465373608792" TEXT=" the DistanceModel property"/>
<node CREATED="1465373615830" ID="ID_1375361660" MODIFIED="1465373616376" TEXT="the Distance Algorithm setting"/>
<node CREATED="1465373649821" ID="ID_842148149" MODIFIED="1465373654389" TEXT="which provides several types of falloff curves"/>
</node>
</node>
<node CREATED="1465374219958" ID="ID_526689092" MODIFIED="1465374220821" TEXT=" Distance Algorithm can be used to define the falloff "/>
</node>
<node CREATED="1465373518713" FOLDED="true" ID="ID_165483988" MODIFIED="1467367523246" TEXT="&gt; MaxRadius">
<node CREATED="1465373666854" ID="ID_1516208960" MODIFIED="1465373667424" TEXT="silence"/>
</node>
<node CREATED="1465374233394" FOLDED="true" ID="ID_1422801535" MODIFIED="1467367523246" TEXT="Attenuation Shapes can be used to specify &#xa;the shape of the Attenuation Volume itself.">
<node CREATED="1465374253613" ID="ID_1548728362" MODIFIED="1465374254091" TEXT="Sphere"/>
<node CREATED="1465374256617" ID="ID_1611138451" MODIFIED="1465374256998" TEXT="Capsule"/>
<node CREATED="1465374260374" ID="ID_1489122413" MODIFIED="1465374260699" TEXT="Box"/>
