<node CREATED="1465374354985" FOLDED="true" ID="ID_374131643" MODIFIED="1465375151364" TEXT=" available Distance Model Attenuation curves">
<node CREATED="1465374397200" FOLDED="true" ID="ID_1969611579" MODIFIED="1465374831933" TEXT="Attenuation Linear">
<node CREATED="1465374733856" ID="ID_1477881413" MODIFIED="1465374734214" TEXT="1/1 reduction in volume over distance"/>
<node CREATED="1465374452822" FOLDED="true" ID="ID_155511382" MODIFIED="1465374701409" TEXT="Use case:">
