<node CREATED="1465374507111" ID="ID_445502689" MODIFIED="1465374507657" TEXT="general looping ambienc"/>
<node CREATED="1465374511938" ID="ID_758559771" MODIFIED="1465374533518" TEXT="low-detail background sounds that &#xa;do not need tight 3d falloff settings"/>
<node CREATED="1465374583968" ID="ID_444054450" MODIFIED="1465374689570" TEXT="crossfading &#x6de1;&#x5165;&#x6de1;&#x51fa; large radius ambient sounds"/>
</node>
</node>
</node>
<node CREATED="1465374403455" FOLDED="true" ID="ID_812252989" MODIFIED="1465374830943" TEXT="Attenuation Logarithmic">
<node CREATED="1465374709958" ID="ID_1327158806" MODIFIED="1465374710480" TEXT="logarithmic reduction in volume over distance"/>
<node CREATED="1465374721108" FOLDED="true" ID="ID_1521571629" MODIFIED="1467367523249" TEXT="Use case:">
<node CREATED="1465374775146" ID="ID_850618070" MODIFIED="1465374776322" TEXT="sounds that need more exact 3d positionalization."/>
<node CREATED="1465374794747" ID="ID_1755570078" MODIFIED="1465374795528" TEXT="making sounds &apos;pop&apos; at a close distance"/>
<node CREATED="1465374808518" ID="ID_1163503277" MODIFIED="1465374809040" TEXT="incoming missiles and projectiles"/>
