</node>
</node>
<node CREATED="1465374418048" FOLDED="true" ID="ID_760826471" MODIFIED="1465375032102" TEXT="Attenuation Inverse">
<node CREATED="1465374902578" FOLDED="true" ID="ID_1172715620" MODIFIED="1465374929154" TEXT=" extremely steep falloff curve">
<node CREATED="1465374909980" ID="ID_232820675" MODIFIED="1465374920624" TEXT=" using the equation: ( UsedMaxRadius / UsedMinRadius ) * ( 0.02 / ( Distance / UsedMaxRadius ) );"/>
