<icon BUILTIN="ksmiletris"/>
<node CREATED="1465375778711" FOLDED="true" ID="ID_1108344354" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/Overview/index.html#generalvolumeguidelines" MODIFIED="1467024644162" TEXT="General Volume Guidelines">
<node CREATED="1465378019139" FOLDED="true" ID="ID_757346628" MODIFIED="1465378162502" TEXT="example">
<node CREATED="1465378025849" ID="ID_1406164092" MODIFIED="1465378026558" TEXT="a stereo file at 1.0 volume will be 2x louder than a mono file"/>
<node CREATED="1465378039041" ID="ID_1168088741" MODIFIED="1465378039844" TEXT=" (4) mono files would be 4x louder"/>
</node>
<node CREATED="1465378087915" FOLDED="true" ID="ID_1379964865" MODIFIED="1465378161613" TEXT=" volume settings up to around 2.0 will increase &#xa;the perceived loudness of the audio file">
<node CREATED="1465378110298" ID="ID_1001003195" MODIFIED="1465378111008" TEXT="anything above that would not"/>
</node>
<node CREATED="1465378125393" ID="ID_1995817614" MODIFIED="1465378125984" TEXT=" A single cue will never distort"/>
<node CREATED="1465378147052" ID="ID_966639781" MODIFIED="1465378160003" TEXT="you would not want all of your files at maximum volume&#xa; because it will likely overload when multiple cues play s&#xa;imultaneously in-game."/>
<node CREATED="1465378196984" FOLDED="true" ID="ID_1885416518" MODIFIED="1467367523255" TEXT="general guidelines for your volumes">
<node CREATED="1465378199924" FOLDED="true" ID="ID_101088237" MODIFIED="1467367523254" TEXT="Dialog">
<node CREATED="1465378203363" ID="ID_1137573007" MODIFIED="1465378204112" TEXT="1.4"/>
