<node CREATED="1465375790875" FOLDED="true" ID="ID_1605561238" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/Overview/index.html#volumeducking" MODIFIED="1465378472009" TEXT="Volume Ducking">
<node CREATED="1465378256379" FOLDED="true" ID="ID_1219739278" MODIFIED="1465378277567" TEXT="y used to decrease volumes of all other sounds &#xa;besides the one that needs to be heard,">
<node CREATED="1465378269351" ID="ID_552128872" MODIFIED="1465378270121" TEXT="most commonly dialog"/>
</node>
<node CREATED="1465378276080" FOLDED="true" ID="ID_672275657" MODIFIED="1465378468049" TEXT="The process usually consists of:">
<node CREATED="1465378285744" ID="ID_407474222" MODIFIED="1465378293745" TEXT="Identifying a Sound Class that should&#xa; cause ducking (e.g. dialog)."/>
<node CREATED="1465378321556" ID="ID_67038137" MODIFIED="1465378342597" TEXT="When a sound from that Sound Class is triggered,&#xa; other Sound Classes begin to decrease in volume &#xa;(Sound Mix : Fade in Time = 0.3 seconds)."/>
<node CREATED="1465378356682" FOLDED="true" ID="ID_1204218771" MODIFIED="1465378376543" TEXT="Specify the amount other Sound Classes decrease in volume">
<node CREATED="1465378364828" ID="ID_924715009" MODIFIED="1465378365587" TEXT="Sound Mix : Volume Adjuster = 0.4"/>
</node>
<node CREATED="1465378390087" FOLDED="true" ID="ID_1219763749" MODIFIED="1465378454839" TEXT="When a sound from a ducking Sound Class stops, &#xa;other classes increase in volume back to normal &#xa;volume">
<node CREATED="1465378405132" ID="ID_1341807434" MODIFIED="1465378408855" TEXT="Sound Mix : Fade Out Time = 0.3 seconds"/>
</node>
<node CREATED="1465378429126" FOLDED="true" ID="ID_214353631" MODIFIED="1467367523255" TEXT="You may also want Sound Class exceptions to&#xa; the ducking process ">
<node CREATED="1465378443260" ID="ID_809134674" MODIFIED="1465378443952" TEXT="e.g. music)"/>
<node CREATED="1465378453093" ID="ID_1105792337" MODIFIED="1465378453954" TEXT=" a Sound Class called Exceptions that is not affected"/>
</node>
</node>
</node>
<node CREATED="1465375799211" FOLDED="true" ID="ID_53318058" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/Overview/index.html#optimizingsoundmemoryusage" MODIFIED="1467367523256" TEXT="Optimizing Sound Memory Usage">
<node CREATED="1465378489480" FOLDED="true" ID="ID_1572862654" MODIFIED="1467367523256" TEXT="it is best to use the lowest sample rate &#xa;that maintains audio fidelity">
<node CREATED="1465378498390" FOLDED="true" ID="ID_1495122715" MODIFIED="1467367523256" TEXT="example">
<node CREATED="1465378506815" ID="ID_118690202" MODIFIED="1465378507216" TEXT="dialog generally still sounds good at 22.1kHz, "/>
<node CREATED="1465378527722" ID="ID_488561614" MODIFIED="1465378534429" TEXT="commonly played effects with high frequencies&#xa; (such as gunshots) need to be higher &#xa;(e.g. 40.0kHz). "/>
</node>
</node>
</node>
</node>
</node>
<node CREATED="1465316131995" FOLDED="true" ID="ID_933615303" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundActors/index.html" MODIFIED="1467367526325" TEXT="Ambient Sound Actor User Guide">
