<node CREATED="1465379969024" FOLDED="true" ID="ID_1716522634" MODIFIED="1465380100553" TEXT="Radius">
<node CREATED="1465380074493" ID="ID_718194846" MODIFIED="1465380075072" TEXT="The overall size of the volume. "/>
<node CREATED="1465380093894" ID="ID_198688662" MODIFIED="1465380094356" TEXT=" This option will change based on the Attenuation Shape selected."/>
<node CREATED="1465380080367" ID="ID_1426955511" MODIFIED="1465380080810" TEXT="By default, the volume shape is set to Sphere"/>
</node>
<node CREATED="1465379972659" FOLDED="true" ID="ID_273814813" MODIFIED="1467367523259" TEXT="Falloff Distance">
<node CREATED="1465380110260" ID="ID_1921527608" MODIFIED="1465380111739" TEXT="The distance over which falloff occurs."/>
</node>
<node CREATED="1465379976607" FOLDED="true" ID="ID_1303134153" MODIFIED="1467367523259" TEXT="Non-Spatialized Radius">
