<node CREATED="1465383440154" FOLDED="true" ID="ID_277422758" MODIFIED="1465383450179" TEXT="Spatialize">
<node CREATED="1465383446431" ID="ID_834967464" MODIFIED="1465383446864" TEXT="If true, positions the input sound in 3D space."/>
</node>
<node CREATED="1465383450696" FOLDED="true" ID="ID_133751321" MODIFIED="1465387126066" TEXT="Distance Algorithm">
<node CREATED="1465383458523" ID="ID_806479931" MODIFIED="1465383458960" TEXT="The interpolation method to use when attenuating the input sound."/>
</node>
<node CREATED="1465387124276" FOLDED="true" ID="ID_854106559" MODIFIED="1465387151640" TEXT="Attenuation Shape">
<node CREATED="1465387134031" ID="ID_1874800223" MODIFIED="1465387146239" TEXT="The shape of the attenuation area from a drop-down list."/>
</node>
<node CREATED="1465387152191" FOLDED="true" ID="ID_186861737" MODIFIED="1465387163353" TEXT="Radius">
<node CREATED="1465387159189" ID="ID_42792754" MODIFIED="1465387159600" TEXT="The attenuation radius."/>
</node>
<node CREATED="1465387164027" FOLDED="true" ID="ID_1563255545" MODIFIED="1465387199852" TEXT="Falloff Distance">
<node CREATED="1465387169146" ID="ID_993614918" MODIFIED="1465387169614" TEXT="The falloff distance for the attenuation."/>
</node>
<node CREATED="1465387200593" FOLDED="true" ID="ID_1919987931" MODIFIED="1465387212037" TEXT="Non-Spatialized Radius">
<node CREATED="1465387206624" ID="ID_772107634" MODIFIED="1465387207759" TEXT="The distance from the sound&apos;s origin where the sound source is treated as spatialized."/>
</node>
<node CREATED="1465387212632" FOLDED="true" ID="ID_493243245" MODIFIED="1465387224818" TEXT="Attenuate With LPF">
<node CREATED="1465387219705" ID="ID_1571823663" MODIFIED="1465387220165" TEXT="If true, enables attenuation via low pass filter (LPF)."/>
</node>
<node CREATED="1465387225413" FOLDED="true" ID="ID_815685920" MODIFIED="1465387241571" TEXT="LPFRadius Min">
<node CREATED="1465387230117" ID="ID_1227394959" MODIFIED="1465387230913" TEXT="The distance from the sound&apos;s origin where the low pass filter should begin to be applied."/>
</node>
<node CREATED="1465387235742" FOLDED="true" ID="ID_634102763" MODIFIED="1465387287577" TEXT="LPFRadius Max">
<node CREATED="1465387278462" ID="ID_605551571" MODIFIED="1465387285370" TEXT="The distance from the sound&apos;s origin where&#xa; the maximum amount of low pass filter &#xa;should be applied."/>
</node>
<node CREATED="1465387292729" FOLDED="true" ID="ID_873982004" MODIFIED="1465387338702" TEXT="Override Attenuation">
<node CREATED="1465387297882" ID="ID_1947565616" MODIFIED="1465387298440" TEXT="Attenuation&#x9;If true, overrides existing attenuation settings."/>
</node>
<node CREATED="1465387339454" ID="ID_1716784977" MODIFIED="1465387961698" TEXT="Stereo files play evenly on the Left and Right&#xa; channels and attenuate allowing volume fades &#xa;between the Radius Min and Radius Max values,&#xa; but they are not spatialized."/>
<node CREATED="1465388011169" ID="ID_1152289152" MODIFIED="1465388018066" TEXT="Sound Cues that do not contain the Attenuation &#xa;node are not localized and play consistently at&#xa; the Sound Cue volume setting."/>
</node>
<node CREATED="1465382857953" FOLDED="true" ID="ID_857620520" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#branchnode" MODIFIED="1465388074334" TEXT="Branch Node">
<node CREATED="1465388032470" ID="ID_595491418" MODIFIED="1465388040997" TEXT="selects a child node based on the&#xa; value of a Boolean parameter"/>
<node CREATED="1465388056477" FOLDED="true" ID="ID_1689385345" MODIFIED="1467367523302" TEXT="Property">
<node CREATED="1465388051539" FOLDED="true" ID="ID_141058464" MODIFIED="1467367523301" TEXT="Bool Parameter Name">
<node CREATED="1465388068291" ID="ID_969367570" MODIFIED="1465388072994" TEXT="The name of the Boolean parameter used &#xa;to determine which child node is used."/>
</node>
</node>
</node>
<node CREATED="1465382865705" FOLDED="true" ID="ID_155662098" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#concatenatornode" MODIFIED="1465388174086" TEXT="Concatenator Node">
<node CREATED="1465388116153" FOLDED="true" ID="ID_153266943" MODIFIED="1467367523302" TEXT="used to play sounds sequentially">
<node CREATED="1465388126740" ID="ID_737133468" MODIFIED="1465388131783" TEXT="e.g. creating a simple BGM playlist that plays&#xa; through a series of specified tracks"/>
</node>
<node CREATED="1465388161000" FOLDED="true" ID="ID_870381209" MODIFIED="1465388173220" TEXT="Property">
<node CREATED="1465388166431" ID="ID_1513632976" MODIFIED="1465388171779" TEXT="Allows for individual volume settings for each &#xa;sound asset attached to the Concatenator."/>
</node>
</node>
<node CREATED="1465382876923" FOLDED="true" ID="ID_1119295532" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#continuousmodulatornode" MODIFIED="1465388563825" TEXT="Continuous Modulator Node">
<node CREATED="1465388200336" ID="ID_1970646709" MODIFIED="1465388207209" TEXT=" allows gameplay parameters to control volume&#xa; and pitch modulation in real time. "/>
<node CREATED="1465388212148" FOLDED="true" ID="ID_679706963" MODIFIED="1465388465321" TEXT="example">
<node CREATED="1465388220552" ID="ID_1927731639" MODIFIED="1465388225481" TEXT="using the velocity of a vehicle to correspond&#xa; to the pitch of an engine sound. "/>
</node>
<node CREATED="1465388241491" ID="ID_1526755472" MODIFIED="1465388254003" TEXT="This modulator needs to be hooked up&#xa; in code for desired purposes and does&#xa; not function on its own within the Sound&#xa; Cue Editor."/>
<node CREATED="1465388357197" ID="ID_922811954" MODIFIED="1465388461675" TEXT="The pitch and volume of the input sound &#xa;default to a uniform distribution, but you&#xa; can change them in the Sound Cue Editor &#xa;to any other type."/>
<node CREATED="1465388486113" FOLDED="true" ID="ID_1015759681" MODIFIED="1467367523302" TEXT="Pitch Modulation Params">
<node CREATED="1465388506160" FOLDED="true" ID="ID_497233372" MODIFIED="1467367523302" TEXT="Parameter Name">
<node CREATED="1465388510159" ID="ID_670386100" MODIFIED="1465388510647" TEXT="The parameter being used to manipulate pitch."/>
</node>
<node CREATED="1465388529340" FOLDED="true" ID="ID_18870950" MODIFIED="1467367523302" TEXT="Default">
<node CREATED="1465388533401" ID="ID_3989845" MODIFIED="1465388534171" TEXT="The default level of pitch modulation."/>
</node>
<node CREATED="1465388537616" ID="ID_75177292" MODIFIED="1465388538540" TEXT="Min Input"/>
<node CREATED="1465388540871" ID="ID_484970858" MODIFIED="1465388541854" TEXT="Max Input"/>
<node CREATED="1465388544122" ID="ID_1526872604" MODIFIED="1465388545353" TEXT="Min Output"/>
<node CREATED="1465388548467" ID="ID_1446758451" MODIFIED="1465388549482" TEXT="Max Output"/>
<node CREATED="1465388552471" ID="ID_1548369212" MODIFIED="1465388553005" TEXT="Param Mode"/>
</node>
</node>
<node CREATED="1465382886755" FOLDED="true" ID="ID_1366434144" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#crossfadebydistancenode" MODIFIED="1465388614500" TEXT="Crossfade by Distance Node">
<node CREATED="1465388596452" ID="ID_1678081509" MODIFIED="1465388606095" TEXT=" fade between two or more input sounds based&#xa; on the distance from the Sound Cue&apos;s origin. "/>
</node>
<node CREATED="1465382895634" FOLDED="true" ID="ID_1396685998" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#crossfadebyparamnode" MODIFIED="1465388733886" TEXT="Crossfade by Param Node">
<node CREATED="1465388683659" FOLDED="true" ID="ID_592175827" MODIFIED="1467367523303" TEXT="similarly to the Crossfade by Distance node">
<node CREATED="1465388696677" FOLDED="true" ID="ID_1306714655" MODIFIED="1467367523303" TEXT="except">
<node CREATED="1465388719495" ID="ID_655274007" MODIFIED="1465388729407" TEXT="Crossfade by Param node allows you to&#xa;manipulate the value of the parameter&#xa; that controls the crossfade programmatically,&#xa; with code or Blueprints"/>
</node>
</node>
</node>
<node CREATED="1465382914399" FOLDED="true" ID="ID_945933526" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#delaynode" MODIFIED="1465388768618" TEXT="Delay Node">
<node CREATED="1465388745642" ID="ID_697103331" MODIFIED="1465388752719" TEXT="inserts a delay into the audio node chain &#xa;to cause a pause before the input sound &#xa;is passed to the output."/>
</node>
<node CREATED="1465382921725" FOLDED="true" ID="ID_477168914" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#dialogueplayernode" MODIFIED="1465388941942" TEXT="DialoguePlayer Node">
<node CREATED="1465388777900" ID="ID_1380284952" MODIFIED="1465388791232" TEXT="sets up a Dialogue Wave  sound asset to &#xa;be played under specific conditions"/>
<node CREATED="1465388823219" ID="ID_1944322085" MODIFIED="1465388824092" TEXT="Dialogue Waves are expected to be used purely for spoken dialogue"/>
<node CREATED="1465388839206" FOLDED="true" ID="ID_1235311122" MODIFIED="1467367523303" TEXT="DialoguePlayer node requires the user to &#xa;specify">
<node CREATED="1465388905340" ID="ID_1081065714" MODIFIED="1465388905753" TEXT="speaker"/>
<node CREATED="1465388914342" ID="ID_43646462" MODIFIED="1465388914799" TEXT="the targets of the dialogue "/>
</node>
</node>
<node CREATED="1465382931107" FOLDED="true" ID="ID_916415891" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#dopplernode" MODIFIED="1465389023906" TEXT="Doppler Node">
<node CREATED="1465388948432" FOLDED="true" ID="ID_1359526617" MODIFIED="1467367523303" TEXT=" simulates the Doppler effect">
<node CREATED="1465388959346" ID="ID_1308193262" MODIFIED="1465388978851" TEXT=" increase in pitch as you approach them  at speed"/>
<node CREATED="1465388984249" ID="ID_160839351" MODIFIED="1465388984634" TEXT=" decrease as you move away"/>
<node CREATED="1465389015895" FOLDED="true" ID="ID_1479278246" MODIFIED="1467367523303" TEXT="&#x4f8b;&#x5b50;">
<node CREATED="1465389019087" ID="ID_1190758062" MODIFIED="1465389019573" TEXT="a siren from an emergency vehicle as it drives past you."/>
</node>
</node>
</node>
<node CREATED="1465382952800" FOLDED="true" ID="ID_936626015" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#envelopernode" MODIFIED="1465389073985" TEXT="Enveloper Node">
<node CREATED="1465389043627" ID="ID_996052448" MODIFIED="1465389052213" TEXT=" allows you to create an envelope with &#xa;optional looping ability to control the &#xa;volume and pitch of a sound over time&#xa; via distribution curves."/>
</node>
<node CREATED="1465382964456" FOLDED="true" ID="ID_489506433" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#groupcontrolnode" MODIFIED="1465389185551" TEXT="Group Control Node">
<node CREATED="1465389099545" ID="ID_1626929742" MODIFIED="1465389168910" TEXT="determines which Sound Wave to play&#xff0c; &#xa;depending on what sounds in the defined &#xa;groups are already playing and their &#xa;proximity to the listener."/>
</node>
<node CREATED="1465382973120" FOLDED="true" ID="ID_324576567" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#loopingnode" MODIFIED="1465389345842" TEXT="Looping Node">
<node CREATED="1465389186528" ID="ID_1756220609" MODIFIED="1465389193662" TEXT="The Looping node should only be used for&#xa; logical or procedural looping such as &#xa;introducing a delay. "/>
</node>
<node CREATED="1465382991346" FOLDED="true" ID="ID_1538999345" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#maturenode" MODIFIED="1465389449626" TEXT="Mature Node">
<node CREATED="1465389419155" ID="ID_953443652" MODIFIED="1465389426752" TEXT=" label a specific node or Sound Cue as containing &#xa;content that would be labeled &quot;Mature&quot; by the &#xa;ESRB rating system."/>
<node CREATED="1465389436750" ID="ID_1616607035" MODIFIED="1465389447241" TEXT=" You can use this node to filter out Mature-rated&#xa; content in certain versions of your game."/>
</node>
<node CREATED="1465382999785" FOLDED="true" ID="ID_1010071845" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#mixernode" MODIFIED="1465389518966" TEXT="Mixer Node">
<node CREATED="1465389465176" ID="ID_1439267311" MODIFIED="1465389471578" TEXT=" is used to trigger multiple Sound Wave &#xa;nodes simultaneously"/>
</node>
<node CREATED="1465383030534" FOLDED="true" ID="ID_1537736554" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#modulatornode" MODIFIED="1465389713893" TEXT="Modulator Node">
<node CREATED="1465389528438" FOLDED="true" ID="ID_1354034927" MODIFIED="1467367523304" TEXT="add random volume and pitch modulation">
<node CREATED="1465389538879" ID="ID_783311265" MODIFIED="1465389539398" TEXT="Each has a Max and Min value to determine the range of randomization."/>
</node>
<node CREATED="1465389566020" ID="ID_1792624706" MODIFIED="1465389573298" TEXT="You can use the Modulator node to adjust&#xa; Volume + Pitch consistently by setting the &#xa;Min and Max to the same values. "/>
<node CREATED="1465389598818" ID="ID_49818683" MODIFIED="1465389603405" TEXT="adjust relative volumes of multiple Sound&#xa; Wave nodes contained within a Sound Cue."/>
<node CREATED="1465389660132" ID="ID_1855291379" MODIFIED="1465389668966" TEXT="used in conjunction with the Looping node, &#xa;it will randomly select upon Sound Cue &#xa;retrigger not per loop revolution."/>
</node>
<node CREATED="1465383040334" FOLDED="true" ID="ID_1869132941" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#oscillatornode" MODIFIED="1465389756647" TEXT="Oscillator Node">
<node CREATED="1465389723342" ID="ID_1320421734" MODIFIED="1465389724535" TEXT=" add continuous pitch and volume oscillation over time"/>
<node CREATED="1465389745472" ID="ID_1873256541" MODIFIED="1465389750874" TEXT="useful on looping sounds to create a greater &#xa;degree of ongoing movement."/>
</node>
<node CREATED="1465383049504" FOLDED="true" ID="ID_1435679679" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#randomnode" MODIFIED="1465389844012" TEXT="Random Node">
<node CREATED="1465389791185" ID="ID_394474516" MODIFIED="1465389798916" TEXT="randomly trigger a Sound Wave node from within &#xa;a group of possible Sound Wave nodes. "/>
</node>
<node CREATED="1465383060699" FOLDED="true" ID="ID_1542400131" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#soundclassnode" MODIFIED="1465389843089" TEXT="SoundClass Node">
<node CREATED="1465389831323" ID="ID_653069368" MODIFIED="1465389835651" TEXT="remap which Sound Class asset you are&#xa; using with the Sound Wave input node."/>
</node>
<node CREATED="1465383071870" FOLDED="true" ID="ID_1596611622" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#switchnode" MODIFIED="1465389862756" TEXT="Switch Node">
<node CREATED="1465389844977" ID="ID_288433952" MODIFIED="1465389849107" TEXT="e selects an input node based on the &#xa;value of an integer parameter."/>
</node>
<node CREATED="1465383084855" FOLDED="true" ID="ID_1022483419" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#waveparamnode" MODIFIED="1465389884966" TEXT="Wave Param Node">
<node CREATED="1465389863530" ID="ID_330933045" MODIFIED="1465389870833" TEXT=" takes a runtime parameter to determine &#xa;whether to play a different Sound Wave &#xa;than the one feeding into the input pin. "/>
</node>
<node CREATED="1465383093428" FOLDED="true" ID="ID_335997535" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#waveplayernode" MODIFIED="1467367523304" TEXT="Wave Player Node">
<node CREATED="1465389879049" ID="ID_621802428" MODIFIED="1465389881477" TEXT="contains a Sound Wave asset from the Content Browser."/>
</node>
<node CREATED="1465383103712" ID="ID_620294925" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/SoundCues/NodeReference/index.html#examples" MODIFIED="1465383109104" TEXT="Examples"/>
</node>
<node CREATED="1465316239308" FOLDED="true" ID="ID_673217307" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/WAV/index.html" MODIFIED="1467367526327" TEXT="Audio Files">
<icon BUILTIN="smily_bad"/>
<node CREATED="1465391170011" FOLDED="true" ID="ID_699000839" MODIFIED="1467367524953" TEXT="&#x4ecb;&#x7ecd;">
<node CREATED="1465391180216" FOLDED="true" ID="ID_1007385830" MODIFIED="1467367524953" TEXT="audio source files">
<node CREATED="1465391185549" ID="ID_361049495" MODIFIED="1465391186501" TEXT="Either as mono or stereo files,"/>
<node CREATED="1465391325784" ID="ID_477241867" MODIFIED="1465391338857" TEXT="Below are the basic requirements for &#xa;your audio content and a quick import guide."/>
</node>
</node>
<node CREATED="1465391108501" FOLDED="true" ID="ID_1950706950" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/WAV/index.html#import" MODIFIED="1467367524956" TEXT="Import">
<node CREATED="1465391464633" FOLDED="true" ID="ID_1685923242" MODIFIED="1467367524955" TEXT="&#x4ecb;&#x7ecd;">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<node CREATED="1465391471855" FOLDED="true" ID="ID_259519505" MODIFIED="1465391791806" TEXT="Importing with the normal sound factory yields&#xa; a simple sound node wave that can be referenced &#xa;by a sound cue for playback.">
<node CREATED="1465391489768" ID="ID_1580954766" MODIFIED="1465391504586" TEXT="mono or stereo."/>
<node CREATED="1465391504969" FOLDED="true" ID="ID_1487091439" MODIFIED="1465391653000" TEXT="Importing with a special naming convention gives &#xa;the potential of multichannel (e.g. 5.1) sounds.">
<node CREATED="1465391565635" FOLDED="true" ID="ID_52350741" MODIFIED="1467367524953" TEXT="Up to 8 channel sounds can be compressed on all platforms">
<node CREATED="1465391578066" ID="ID_478695559" MODIFIED="1465391578673" TEXT="not all platforms can play them back efficiently."/>
<node CREATED="1465391601727" ID="ID_336235198" MODIFIED="1465391606780" TEXT="The package saving process does all the necessary &#xa;work to convert to the platform&apos;s native format."/>
</node>
<node CREATED="1465391769257" ID="ID_981583387" MODIFIED="1465391776154" TEXT="the number of channels infers which channels &#xa;are played on which speakers (see table below)."/>
</node>
</node>
<node CREATED="1465391793030" FOLDED="true" ID="ID_98231304" MODIFIED="1467367524953" TEXT="Compression settings are tweaked to maintain &#xa;similar quality across platforms. ">
<node CREATED="1465391814092" ID="ID_200528390" MODIFIED="1465391827898" TEXT="After testing many sounds, our musician determined&#xa; an XMA quality of 40 was equivalent to an ogg vorbis&#xa; quality of 0.15."/>
</node>
</node>
<node CREATED="1465391125152" ID="ID_1848635073" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/WAV/index.html#singlefileimport" MODIFIED="1465391129296" TEXT="Single File Import"/>
<node CREATED="1465391134806" ID="ID_71341112" LINK="https://docs.unrealengine.com/latest/INT/Engine/Audio/WAV/index.html#multichannelimport" MODIFIED="1465391139267" TEXT="Multichannel Import"/>
