<node CREATED="1462883851480" FOLDED="true" ID="ID_1572123506" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/index.html" MODIFIED="1467367526442" TEXT="Cascade Particle Systems">
<node CREATED="1462883851480" FOLDED="true" ID="ID_976272870" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/index.html" MODIFIED="1467367526329" TEXT="Cascade Particle Systems">
<node CREATED="1466080132743" ID="ID_1217379609" MODIFIED="1466080133298" TEXT="Unreal&apos;s Particle Systems are edited via Cascade"/>
<node CREATED="1466080209541" ID="ID_857830177" MODIFIED="1466080217773" TEXT="Particle systems are also very closely &#xa;related to the various materials and &#xa;textures applied to each particle. "/>
<node CREATED="1466080244577" ID="ID_1953670063" MODIFIED="1466080248927" TEXT="The primary job of the particle system itself &#xa;is to control the behavior of the particles"/>
<node CREATED="1466080485839" ID="ID_415638763" MODIFIED="1466080490146" TEXT="specific look and feel of the particle system as &#xa;a whole is often controlled by way of materials"/>
</node>
<node CREATED="1466080546166" FOLDED="true" ID="ID_309044522" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/Cascade/index.html" MODIFIED="1467367526329" TEXT="Cascade Particle Editor Reference">
<node CREATED="1466675130486" ID="ID_1307811749" MODIFIED="1466675368874" TEXT="Cascade is a tool for creating modular particle-based&#xa; effects using emitters in the Unreal Engine."/>
<node CREATED="1466675444362" FOLDED="true" ID="ID_538834315" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/Cascade/#openingcascade" MODIFIED="1467367525001" TEXT="Opening Cascade">
