<node CREATED="1466080577453" FOLDED="true" ID="ID_484756695" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/Overview/index.html" MODIFIED="1467367526330" TEXT="Key Particle Concepts">
<icon BUILTIN="ksmiletris"/>
<node CREATED="1466080675505" FOLDED="true" ID="ID_456999062" MODIFIED="1467367525010" TEXT="&#x4ecb;&#x7ecd;">
<node CREATED="1466080717870" ID="ID_204256793" MODIFIED="1466080718296" TEXT=" important concepts"/>
</node>
<node CREATED="1466080732882" FOLDED="true" ID="ID_313677164" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/Overview/index.html#amodularapproachtoparticleeffects" MODIFIED="1466668959253" TEXT="A Modular Approach to Particle Effects">
<node CREATED="1466080834342" FOLDED="true" ID="ID_1418299750" MODIFIED="1466081669363" TEXT="&#x4ecb;&#x7ecd;">
<node CREATED="1466081248304" FOLDED="true" ID="ID_518796289" MODIFIED="1466081425368" TEXT=" In some 3D effects packages, such as Maya, ">
<node CREATED="1466081405332" ID="ID_619801770" MODIFIED="1466081409538" TEXT="a particle system is created with most of the &#xa;necessary properties for behavior already in place. "/>
<node CREATED="1466081413658" ID="ID_1034683918" MODIFIED="1466081423152" TEXT="The user then alters those properties &#xa;in order to achieve the desired result."/>
</node>
<node CREATED="1466080875318" FOLDED="true" ID="ID_1315355199" MODIFIED="1467367525011" TEXT="The primary and overreaching concept of Cascade&#xa; is that of modularly designed particle systems">
<node CREATED="1466081451508" FOLDED="true" ID="ID_68051703" MODIFIED="1466081531330" TEXT="In Cascade, a particle system begins with only">
<node CREATED="1466081467043" ID="ID_544967981" MODIFIED="1466081467765" TEXT=" a few bare bones properties"/>
<node CREATED="1466081475379" ID="ID_1955810131" MODIFIED="1466081475865" TEXT="a few behavioral modules."/>
</node>
<node CREATED="1466081534829" FOLDED="true" ID="ID_1863137223" MODIFIED="1467367525011" TEXT="Each module represents a specific &#xa;aspect of particle behavior,">
<node CREATED="1466081564632" ID="ID_2998132" MODIFIED="1466081565062" TEXT="color"/>
<node CREATED="1466081580011" ID="ID_1193807583" MODIFIED="1466081580487" TEXT="position at birth"/>
<node CREATED="1466081583111" ID="ID_1278090405" MODIFIED="1466081583569" TEXT="movement"/>
<node CREATED="1466081585694" ID="ID_204235453" MODIFIED="1466081586134" TEXT="scaling"/>
</node>
</node>
</node>
<node CREATED="1466081670457" FOLDED="true" ID="ID_955556689" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/Overview/index.html#defaultmodules" MODIFIED="1466082695878" TEXT="Default Modules">
<node CREATED="1466081916940" ID="ID_303861904" MODIFIED="1466081917427" TEXT="exist on a particle emitter by default"/>
<node CREATED="1466081939183" FOLDED="true" ID="ID_1288827562" MODIFIED="1466082668866" TEXT="Whenever a new Sprite Emitter - the key component of any particle system -&#xa; is added to a particle system, it is always&#xa; created with the following default modules:">
<node CREATED="1466081960847" FOLDED="true" ID="ID_1900088068" MODIFIED="1466082004066" TEXT="Required">
<node CREATED="1466081980915" ID="ID_1474331143" MODIFIED="1466081981443" TEXT="material applied to the particles"/>
<node CREATED="1466081988609" ID="ID_1401501826" MODIFIED="1466081989139" TEXT=" how long the emitter should emit particles,"/>
<node CREATED="1466081990119" ID="ID_208133003" MODIFIED="1466081994954" TEXT="etc."/>
</node>
<node CREATED="1466081999017" FOLDED="true" ID="ID_1471607561" MODIFIED="1466082107810" TEXT="Spawn">
<node CREATED="1466082048779" FOLDED="true" ID="ID_925909248" MODIFIED="1466082104592" TEXT="this module controls">
<node CREATED="1466082055860" ID="ID_1146503296" MODIFIED="1466082056306" TEXT="how quickly particles will spawn from the emitter"/>
<node CREATED="1466082063254" ID="ID_1354125471" MODIFIED="1466082092601" TEXT="whether they will spawn in bursts (&#x9694;&#x4e09;&#x5dee;&#x4e94;&#x5730;),"/>
</node>
</node>
<node CREATED="1466082108483" FOLDED="true" ID="ID_619247444" MODIFIED="1466082137951" TEXT="Lifetime">
<node CREATED="1466082116136" ID="ID_733490283" MODIFIED="1466082116904" TEXT="This governs how long each particle will live once it is born."/>
<node CREATED="1466082131258" ID="ID_886298383" MODIFIED="1466082132553" TEXT=" Without this module, the particles will live indefinitely."/>
</node>
<node CREATED="1466082138590" FOLDED="true" ID="ID_56460591" MODIFIED="1466082647129" TEXT="Initial Size">
<node CREATED="1466082641226" ID="ID_313678459" MODIFIED="1466082641787" TEXT="the scale of a particle at the moment of its birth."/>
</node>
<node CREATED="1466082647585" ID="ID_1601110472" MODIFIED="1466082648088" TEXT="Initial Velocity"/>
<node CREATED="1466082654623" FOLDED="true" ID="ID_1078192731" MODIFIED="1467367525011" TEXT="Color Over Life ">
<node CREATED="1466082662431" ID="ID_273453873" MODIFIED="1466082667446" TEXT="how the color of each particle will &#xa;shift throughout its lifespan."/>
</node>
</node>
</node>
<node CREATED="1466081876952" FOLDED="true" ID="ID_1721055206" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/Overview/index.html#modulecategories" MODIFIED="1466083655515" TEXT="Module Categories">
<node CREATED="1466082778022" FOLDED="true" ID="ID_1459039852" MODIFIED="1466082802086" TEXT="Acceleration">
<node CREATED="1466082791624" ID="ID_837709501" MODIFIED="1466082799024" TEXT="how particle acceleration may be affected, &#xa;such as by drag forces."/>
</node>
<node CREATED="1466082802805" ID="ID_1836809590" MODIFIED="1466082803225" TEXT="Attraction"/>
<node CREATED="1466082805943" ID="ID_1110090737" MODIFIED="1466082806555" TEXT="Camera"/>
<node CREATED="1466082810557" ID="ID_1358463916" MODIFIED="1466082810932" TEXT="Collision"/>
<node CREATED="1466082813999" ID="ID_540378276" MODIFIED="1466082814374" TEXT="Color"/>
<node CREATED="1466082817329" FOLDED="true" ID="ID_901110392" MODIFIED="1466082927701" TEXT="Event">
<node CREATED="1466082846291" ID="ID_623620214" MODIFIED="1466082885532" TEXT="control the triggering of particle events, which &#xa;can in turn cause a variety of in-game responses."/>
</node>
<node CREATED="1466082928397" ID="ID_407124986" MODIFIED="1466082928739" TEXT="Kill"/>
<node CREATED="1466082936298" ID="ID_1657352397" MODIFIED="1466082936840" TEXT="Lifetime"/>
<node CREATED="1466082939571" ID="ID_857000511" MODIFIED="1466082940181" TEXT="Light"/>
<node CREATED="1466082942722" FOLDED="true" ID="ID_884076614" MODIFIED="1466083502043" TEXT="Location">
<node CREATED="1466082955064" ID="ID_702229793" MODIFIED="1466082959731" TEXT="Modules controlling where particles will be born &#xa;relative to the location of the emitter Actor."/>
</node>
<node CREATED="1466082950417" ID="ID_1286284587" MODIFIED="1466082950982" TEXT="Material"/>
<node CREATED="1466083506353" ID="ID_1267847780" MODIFIED="1466083506873" TEXT="Orbit"/>
<node CREATED="1466083522633" ID="ID_1270447040" MODIFIED="1466083523919" TEXT="Orientation"/>
<node CREATED="1466083527786" FOLDED="true" ID="ID_602263422" MODIFIED="1466083564080" TEXT="Parameter">
<node CREATED="1466083553393" ID="ID_1085886689" MODIFIED="1466083560895" TEXT="Modules which can be parameterized,&#xa; or controlled via external sources such&#xa; as Blueprints and Matinee."/>
</node>
<node CREATED="1466083531094" ID="ID_882830604" MODIFIED="1466083531952" TEXT="Rotation"/>
<node CREATED="1466083569086" ID="ID_1739496470" MODIFIED="1466083569529" TEXT="RotationRate"/>
<node CREATED="1466083572911" ID="ID_1898395404" MODIFIED="1466083573467" TEXT="Size"/>
<node CREATED="1466083577423" ID="ID_1611673349" MODIFIED="1466083577820" TEXT="Spawn"/>
<node CREATED="1466083580888" ID="ID_942698446" MODIFIED="1466083581263" TEXT="SubUV"/>
<node CREATED="1466083585467" ID="ID_357490920" MODIFIED="1466083586011" TEXT="Velocity"/>
<node CREATED="1466083599386" ID="ID_810766942" LINK="#ID_62940270" MODIFIED="1466083634298" TEXT="&#x53c2;&#x8003; Particle Module Reference"/>
</node>
<node CREATED="1466081885345" FOLDED="true" ID="ID_1812153205" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/Overview/index.html#initialvsstopoverlife" MODIFIED="1466083856286" TEXT="Initial vs. Over Life">
<node CREATED="1466083690191" FOLDED="true" ID="ID_359093060" MODIFIED="1466083784630" TEXT="Initial modules ">
<node CREATED="1466083701723" ID="ID_1654568985" MODIFIED="1466083712882" TEXT="are used to control some aspect of particles&#xa;the moment they are spawned into the world."/>
</node>
<node CREATED="1466083782760" FOLDED="true" ID="ID_1163166685" MODIFIED="1467367525011" TEXT="Over Life or Per Life modules">
<node CREATED="1466083793890" ID="ID_133615520" MODIFIED="1466083794443" TEXT="exist to allow some aspect of a particle to change throughout its life."/>
</node>
<node CREATED="1466083811947" FOLDED="true" ID="ID_742239700" MODIFIED="1467367525012" TEXT="example">
<node CREATED="1466083821352" ID="ID_400183686" MODIFIED="1466083826493" TEXT="an Initial Color module will allow you to set the&#xa; color of a module at the moment a particle is born"/>
<node CREATED="1466083839613" ID="ID_359707758" MODIFIED="1466083847656" TEXT="a Color Over Life property allows the particle color&#xa; to gradually change between the moment of birth &#xa;and the moment of death."/>
</node>
</node>
<node CREATED="1466081894897" FOLDED="true" ID="ID_1341223849" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/Overview/index.html#moduletimecalculation" MODIFIED="1467367525012" TEXT="Module Time Calculation">
<node CREATED="1466083931510" FOLDED="true" ID="ID_1838969365" MODIFIED="1467367525012" TEXT="a property to a distribution type that changes over time">
<node CREATED="1466083936257" ID="ID_1838944778" MODIFIED="1466083936687" TEXT="some modules use &apos;relative time&apos; "/>
<node CREATED="1466083942929" FOLDED="true" ID="ID_1828395588" MODIFIED="1467367525012" TEXT="some use &apos;absolute time&apos; ">
<node CREATED="1466083951896" ID="ID_1674850067" MODIFIED="1466083952517" TEXT="Absolute time is basically the containing emitter time."/>
</node>
</node>
</node>
</node>
<node CREATED="1466080768907" ID="ID_968060365" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/Overview/index.html#emitters,particlesystems,andemitteractors" MODIFIED="1466080775263" TEXT="Emitters, Particle Systems, and Emitter Actors"/>
<node CREATED="1466080780561" ID="ID_61597967" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/Overview/index.html#particlecalculation" MODIFIED="1466080785512" TEXT="Particle Calculation"/>
<node CREATED="1466080789516" ID="ID_115578845" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/Overview/index.html#emittertypes" MODIFIED="1466080793884" TEXT="Emitter Types"/>
<node CREATED="1466080797729" FOLDED="true" ID="ID_1746301637" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/Overview/index.html#parameters" MODIFIED="1466674647010" TEXT="Parameters">
<node CREATED="1466669025829" FOLDED="true" ID="ID_610614780" MODIFIED="1466669112924" TEXT=" certain parts of a particle system&apos;s behavior will &#xa;need to be controlled or changed at runtime. ">
<node CREATED="1466669035594" FOLDED="true" ID="ID_237205938" MODIFIED="1467367525012" TEXT="example">
<node CREATED="1466669048385" ID="ID_1164612527" MODIFIED="1466669097458" TEXT="you may be creating a magic effect that changes&#xa; color based on the amount of magical energy &#xa;consumed when the spell (&#x5492;&#x8bed;) is cast.&#xa;&#xa;parameters will need to be added to the particle system."/>
</node>
</node>
<node CREATED="1466669113764" FOLDED="true" ID="ID_1408471639" MODIFIED="1466669243638" TEXT="A parameter is a type of property that can&#xa; send or receive data to/from other systems ">
<node CREATED="1466669131888" FOLDED="true" ID="ID_531000346" MODIFIED="1467367525013" TEXT=" other systems such as">
<node CREATED="1466669134536" ID="ID_1580009452" MODIFIED="1466669134982" TEXT="Blueprints"/>
<node CREATED="1466669137004" ID="ID_1946451858" MODIFIED="1466669137333" TEXT="Matinee"/>
<node CREATED="1466669139374" ID="ID_1966991561" MODIFIED="1466669139797" TEXT="material"/>
<node CREATED="1466669142782" ID="ID_1654075583" MODIFIED="1466669148477" TEXT="etc"/>
</node>
<node CREATED="1466669187929" FOLDED="true" ID="ID_1992831306" MODIFIED="1467367525013" TEXT="&#x4f8b;&#x5982;">
<node CREATED="1466669203997" ID="ID_381969454" MODIFIED="1466669213479" TEXT="setting up a parameter to control spawn rate &#xa;for a fire effect could allow for the player to&#xa; have the ability to increase or decrease the &#xa;amount of flame being emitted at runtime."/>
</node>
</node>
<node CREATED="1466669244140" ID="ID_1388424572" MODIFIED="1466669260347" TEXT="Conversely, there are parameter modules which&#xa; can be added to a particle system that can, in turn,&#xa; drive other things in the level such as a color &#xa;used in a given material."/>
<node CREATED="1466669286749" FOLDED="true" ID="ID_1231548146" MODIFIED="1467367525013" TEXT="In Cascade, parameters are generally established &#xa;by way of Distributions,  which are ways to handle &#xa;data within a property">
<node CREATED="1466669307596" ID="ID_1437290368" LINK="#ID_1723598216" MODIFIED="1466669326504" TEXT="&#x53c2;&#x8003; Distributions"/>
<node CREATED="1466669348635" ID="ID_780686145" LINK="#ID_409482320" MODIFIED="1466669389341" TEXT="&#x53c2;&#x8003; distribution page"/>
</node>
</node>
<node CREATED="1466080805661" FOLDED="true" ID="ID_733116250" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/Overview/index.html#litparticles" MODIFIED="1466674636008" TEXT="Lit Particles">
<node CREATED="1466670374430" ID="ID_917248778" MODIFIED="1466670382318" TEXT="Particle systems can be set up to receive &#xa;light, but require a special setting."/>
<node CREATED="1466670723104" ID="ID_413555406" MODIFIED="1466670729528" TEXT="Following those steps should allow your&#xa; particles to show up in game as lit"/>
<node CREATED="1466670389236" FOLDED="true" ID="ID_784084623" MODIFIED="1466674634354" TEXT="To set up lit particles:">
<node CREATED="1466670398146" FOLDED="true" ID="ID_1714166430" MODIFIED="1466670810507" TEXT="Make sure the material is using a &#xa;shading model other than unlit">
<node CREATED="1466670419093" ID="ID_1944908574" MODIFIED="1466670420190" TEXT="Using DefaultLit will allow you access to normal mapping, specularity, etc."/>
</node>
<node CREATED="1466670540988" FOLDED="true" ID="ID_650190585" MODIFIED="1466670809562" TEXT="select the flag for each separate LOD called bLit">
<node CREATED="1466670606645" FOLDED="true" ID="ID_1335193087" MODIFIED="1467367525013" TEXT="In the LODSettings in Cascade">
<node CREATED="1466670549091" FOLDED="true" ID="ID_1637436499" MODIFIED="1467367525013" TEXT="found under the LOD properties">
<node CREATED="1466670625273" ID="ID_453685898" MODIFIED="1466670631371" TEXT="which you can see when all modules &#xa;and emitters are deselected)"/>
</node>
</node>
<node CREATED="1466670619412" ID="ID_1714091695" MODIFIED="1466670619833" TEXT=" This flag can only be updated in Cascade."/>
</node>
</node>
</node>
<node CREATED="1466080815620" FOLDED="true" ID="ID_927071554" LINK="https://docs.unrealengine.com/latest/INT/Engine/Rendering/ParticleSystems/Overview/index.html#levelsofdetail_lods_" MODIFIED="1466674631508" TEXT="Levels of Detail (LODs)">
<node CREATED="1466671207667" FOLDED="true" ID="ID_211266496" MODIFIED="1466671411459" TEXT=" it is important to consider the value of calculating &#xa;particles from which the player is too far away to&#xa; see or adequately appreciate.">
<node CREATED="1466671220548" FOLDED="true" ID="ID_1034566969" MODIFIED="1467367525014" TEXT="&#x4f8b;&#x5982;">
<node CREATED="1466671234516" FOLDED="true" ID="ID_1827183126" MODIFIED="1467367525014" TEXT="a particle system of a camp fire">
<node CREATED="1466671250426" FOLDED="true" ID="ID_1426517648" MODIFIED="1467367525014" TEXT="When viewed up close">
<node CREATED="1466671258135" ID="ID_223999524" MODIFIED="1466671356832" TEXT="one might see embers and sparks&#xa; drifting up into the smoke. "/>
</node>
<node CREATED="1466671363627" FOLDED="true" ID="ID_1970551726" MODIFIED="1467367525014" TEXT=" viewed from several hundred meters away">
<node CREATED="1466671380288" ID="ID_1599799555" MODIFIED="1466671391470" TEXT="those embers would be so small that a monitor&#xa; or screen would not even be able to render them&#xa;&#x6240;&#x4ee5;&#x4e0d;&#x663e;&#x793a;"/>
</node>
</node>
</node>
</node>
<node CREATED="1466671431001" FOLDED="true" ID="ID_427989486" MODIFIED="1466671927015" TEXT="The LOD system allows you to set up custom&#xa; distance ranges at which your particle system&#xa; will automatically simplify">
<node CREATED="1466671466461" ID="ID_507533321" MODIFIED="1466671466863" TEXT=" Each range constitutes another LOD"/>
<node CREATED="1466671482988" FOLDED="true" ID="ID_1893483224" MODIFIED="1466671824471" TEXT=" Simplification takes place in the form of">
<node CREATED="1466671490700" ID="ID_1241027467" MODIFIED="1466671490983" TEXT="lowered property values"/>
<node CREATED="1466671495340" ID="ID_548698722" MODIFIED="1466671495685" TEXT="deactivated modules"/>
<node CREATED="1466671505285" ID="ID_1090718546" MODIFIED="1466671505786" TEXT="even deactivated emitters"/>
</node>
