<node CREATED="1463995603972" FOLDED="true" ID="ID_824800497" MODIFIED="1466073147987" TEXT="LightSwitchCodeOnly.h">
<node CREATED="1464000576452" FOLDED="true" ID="ID_41450809" MODIFIED="1466073144014" TEXT="code">
<node CREATED="1464000589490" ID="ID_1735959402" MODIFIED="1464000592861" TEXT="// Copyright 1998-2015 Epic Games, Inc. All Rights Reserved.&#xa;&#xa;    #pragma once&#xa;&#xa;    #include &quot;GameFramework/Actor.h&quot;&#xa;    #include &quot;LightSwitchCodeOnly.generated.h&quot;&#xa;&#xa;    /**&#xa;     * &#xa;     */&#xa;    UCLASS()&#xa;    class [PROJECTNAME]_API ALightSwitchCodeOnly : public AActor&#xa;    {&#xa;        GENERATED_BODY()&#xa;        public:&#xa;        /** point light component */&#xa;        UPROPERTY(VisibleAnywhere, Category = &quot;Switch Components&quot;)&#xa;        class UPointLightComponent* PointLight1;&#xa;&#xa;        /** sphere component */&#xa;        UPROPERTY(VisibleAnywhere, Category = &quot;Switch Components&quot;)&#xa;        class USphereComponent* Sphere1;&#xa;&#xa;        ALightSwitchCodeOnly();&#xa;&#xa;        /** called when something enters the sphere component */&#xa;        UFUNCTION()&#xa;        void OnOverlapBegin(class AActor* OtherActor, class UPrimitiveComponent* OtherComp, int32 OtherBodyIndex, bool bFromSweep, const FHitResult&amp; SweepResult);&#xa;&#xa;        /** called when something leaves the sphere component */&#xa;        UFUNCTION()&#xa;        void OnOverlapEnd(class AActor* OtherActor, class UPrimitiveComponent* OtherComp, int32 OtherBodyIndex);&#xa;&#xa;        /** Toggles the light component&apos;s visibility*/&#xa;        UFUNCTION()&#xa;        void ToggleLight();&#xa;&#xa;        /** the desired intensity for the light */&#xa;        UPROPERTY(VisibleAnywhere, Category = &quot;Switch Variables&quot;)&#xa;        float DesiredIntensity;&#xa;&#xa;    };"/>
</node>
<node CREATED="1464000558315" FOLDED="true" ID="ID_636933986" MODIFIED="1464000609405" TEXT="&#x7c7b;&#x58f0;&#x660e;">
<node CREATED="1463999751028" FOLDED="true" ID="ID_1702447865" MODIFIED="1467367525100" TEXT="code">
<node CREATED="1463999723110" ID="ID_387413226" MODIFIED="1463999729305" TEXT="UCLASS()&#xa;class [PROJECTNAME]_API ALightSwitchCodeOnly : public AActor&#xa;{&#xa;GENERATED_BODY()&#xa;&#xa;};"/>
</node>
<node CREATED="1463999761132" FOLDED="true" ID="ID_57649164" MODIFIED="1463999938191" TEXT="&#x8bf4;&#x660e;">
<node CREATED="1463999768291" FOLDED="true" ID="ID_471334060" MODIFIED="1463999831785" TEXT="UCLASS() macro">
<node CREATED="1463999783683" ID="ID_1061413738" MODIFIED="1463999789988" TEXT="Class declarations created with the C++ Class&#xa; Wizard are automatically preceded by the&#xa; UCLASS() macro"/>
<node CREATED="1463999810947" ID="ID_749941129" MODIFIED="1463999811911" TEXT=" makes the engine aware of the class"/>
<node CREATED="1463999823579" ID="ID_269317979" MODIFIED="1463999829128" TEXT="can also be used with keyword specifiers&#xa; to set the class&apos;s behavior within the engine."/>
</node>
<node CREATED="1463999854821" ID="ID_1097250867" MODIFIED="1463999877442" TEXT="variable and/or function declarations&#xa;can be preceded with UPROPERTY() &#xa;and UFUNCTION() macros"/>
<node CREATED="1463999891961" ID="ID_1722515308" MODIFIED="1463999896100" TEXT=" Components are set up with UPROPERTY() macros as well"/>
</node>
</node>
<node CREATED="1463999938740" FOLDED="true" ID="ID_1066084051" MODIFIED="1464000107937" TEXT="Declare the PointLightComponent and the SphereComponent">
<node CREATED="1463999981382" FOLDED="true" ID="ID_1736750919" MODIFIED="1464000026175" TEXT="code">
<node CREATED="1463999951071" ID="ID_1645490934" MODIFIED="1463999959778" TEXT="public:&#xa;/** point light component */&#xa;UPROPERTY(VisibleAnywhere, Category = &quot;Switch Components&quot;)&#xa;class UPointLightComponent* PointLight1;&#xa;&#xa;/** sphere component */&#xa;UPROPERTY(VisibleAnywhere, Category = &quot;Switch Components&quot;)&#xa;class USphereComponent* Sphere1;&#xa;"/>
</node>
<node CREATED="1463999986598" FOLDED="true" ID="ID_1396951489" MODIFIED="1467367525101" TEXT="&#x8bf4;&#x660e;">
<node CREATED="1464000000782" FOLDED="true" ID="ID_226097346" MODIFIED="1467367525101" TEXT="UPROPERTY(VisibleAnywhere&#x3002;&#x3002;&#x3002;">
<node CREATED="1463999990791" ID="ID_1691465901" MODIFIED="1464000021616" TEXT="The PointLightComponent and the SphereComponent&#xa; are made VisibleAnywhere"/>
<node CREATED="1464000048111" ID="ID_1624723776" MODIFIED="1464000093753" TEXT="This means that their properties will be visible &#xa;in the Details tab of a LightSwitchCodeOnly Actor"/>
</node>
</node>
</node>
<node CREATED="1464000108590" FOLDED="true" ID="ID_1389183016" MODIFIED="1464000147723" TEXT="Declare your constructor,">
<node CREATED="1464000126378" ID="ID_349561631" MODIFIED="1464000127496" TEXT="ALightSwitchCodeOnly()"/>
</node>
<node CREATED="1464000148422" FOLDED="true" ID="ID_420509989" MODIFIED="1466073147985" TEXT="Declare OnOverlapBegin and OnOverlapEnd">
<node CREATED="1464000160363" ID="ID_351006541" MODIFIED="1464000166962" TEXT="will be called when another Actor enters &#xa;or leaves the SphereComponent."/>
<node CREATED="1464000179959" ID="ID_799139080" MODIFIED="1464000180990" TEXT=" these have different signatures."/>
<node CREATED="1464000209132" FOLDED="true" ID="ID_182568137" MODIFIED="1467367525101" TEXT="code">
<node CREATED="1464000188658" ID="ID_770267901" MODIFIED="1464000201397" TEXT="/** called when something enters the sphere component */&#xa;UFUNCTION()&#xa;void OnOverlapBegin(class AActor* OtherActor, class UPrimitiveComponent* OtherComp, int32 OtherBodyIndex, bool bFromSweep, const FHitResult&amp; SweepResult);&#xa;&#xa;/** called when something leaves the sphere component */&#xa;UFUNCTION()&#xa;void OnOverlapEnd(class AActor* OtherActor, class UPrimitiveComponent* OtherComp, int32 OtherBodyIndex);"/>
</node>
</node>
<node CREATED="1464000240097" FOLDED="true" ID="ID_866340340" MODIFIED="1464000379217" TEXT="Declare ToggleLight, &#xa;a function that toggles the visibility &#xa;of the PointLightComponent">
<node CREATED="1464000256569" ID="ID_413895186" MODIFIED="1464000259412" TEXT="UFUNCTION()&#xa;void ToggleLight();"/>
</node>
<node CREATED="1464000375206" FOLDED="true" ID="ID_875407230" MODIFIED="1464000507720" TEXT="Declare the DesiredIntensity variable ">
<node CREATED="1464000393510" FOLDED="true" ID="ID_1371857017" MODIFIED="1467367525101" TEXT="code ">
<node CREATED="1464000396784" ID="ID_368252254" MODIFIED="1464000401399" TEXT="/** the desired intensity for the light */&#xa;UPROPERTY(VisibleAnywhere, Category=&quot;Switch Variables&quot;)&#xa;float DesiredIntensity;"/>
<node CREATED="1464000420662" FOLDED="true" ID="ID_1641014250" MODIFIED="1467367525101" TEXT="make it visible anywhere with the&#xa; VisibleAnywhere specifier.">
<node CREATED="1464000454030" ID="ID_61652039" MODIFIED="1464000459197" TEXT="It will show up in the category Switch Variables &#xa;in the Details tab of a LightSwitchCodeOnly Actor."/>
</node>
</node>
</node>
</node>
<node CREATED="1463995617268" FOLDED="true" ID="ID_1935602798" MODIFIED="1466073145546" TEXT="LightSwitchCodeOnly.cpp">
<node CREATED="1464000642770" FOLDED="true" ID="ID_948796891" MODIFIED="1466073144756" TEXT="code">
<node CREATED="1464001243670" ID="ID_763107454" MODIFIED="1464001247510" TEXT="// Copyright 1998-2015 Epic Games, Inc. All Rights Reserved.&#xa;&#xa;#include &quot;BasicClasses.h&quot;&#xa;#include &quot;LightSwitchCodeOnly.h&quot;&#xa;&#xa;ALightSwitchCodeOnly::ALightSwitchCodeOnly()&#xa;{&#xa;    DesiredIntensity = 3000.0f;&#xa;&#xa;    PointLight1 = CreateDefaultSubobject&lt;UPointLightComponent&gt;(TEXT(&quot;PointLight1&quot;));&#xa;    PointLight1-&gt;Intensity = DesiredIntensity;&#xa;    PointLight1-&gt;bVisible = true;&#xa;    RootComponent = PointLight1;&#xa;&#xa;    Sphere1 = CreateDefaultSubobject&lt;USphereComponent&gt;(TEXT(&quot;Sphere1&quot;));&#xa;    Sphere1-&gt;InitSphereRadius(250.0f);&#xa;    Sphere1-&gt;AttachParent = RootComponent;&#xa;&#xa;    Sphere1-&gt;OnComponentBeginOverlap.AddDynamic(this, &amp;ALightSwitchCodeOnly::OnOverlapBegin);       // set up a notification for when this component overlaps something&#xa;    Sphere1-&gt;OnComponentEndOverlap.AddDynamic(this, &amp;ALightSwitchCodeOnly::OnOverlapEnd);       // set up a notification for when this component overlaps something&#xa;&#xa;}&#xa;&#xa;void ALightSwitchCodeOnly::OnOverlapBegin(class AActor* OtherActor, class UPrimitiveComponent* OtherComp, int32 OtherBodyIndex, bool bFromSweep, const FHitResult&amp; SweepResult)&#xa;{&#xa;    if (OtherActor &amp;&amp; (OtherActor != this) &amp;&amp; OtherComp)&#xa;    {&#xa;        ToggleLight();&#xa;    }&#xa;}&#xa;&#xa;void ALightSwitchCodeOnly::OnOverlapEnd(class AActor* OtherActor, class UPrimitiveComponent* OtherComp, int32 OtherBodyIndex)&#xa;{&#xa;    if (OtherActor &amp;&amp; (OtherActor != this) &amp;&amp; OtherComp)&#xa;    {&#xa;        ToggleLight();&#xa;    }&#xa;}&#xa;&#xa;void ALightSwitchCodeOnly::ToggleLight()&#xa;{&#xa;    PointLight1-&gt;ToggleVisibility();&#xa;}"/>
</node>
<node CREATED="1464001356229" ID="ID_862349296" MODIFIED="1464001382692" TEXT="BasicClasses.h is referring to the name of&#xa; the project that the class has been set up in."/>
