<node CREATED="1464796198338" ID="ID_1043739638" MODIFIED="1464796199700" TEXT="Degrees"/>
</node>
<node CREATED="1464796203475" FOLDED="true" ID="ID_1371359304" MODIFIED="1467367525191" TEXT="OuterConeAngle">
<node CREATED="1464796198338" ID="ID_129535487" MODIFIED="1464796199700" TEXT="Degrees"/>
</node>
</node>
</node>
</node>
</node>
<node CREATED="1463652073002" FOLDED="true" ID="ID_42411009" LINK="https://docs.unrealengine.com/latest/INT/API/Runtime/Engine/Components/UDirectionalLightComponent/index.html" MODIFIED="1467367525194" TEXT="UDirectionalLightComponent">
<node CREATED="1463652093185" FOLDED="true" ID="ID_850304985" MODIFIED="1463732444906" TEXT="Remarks">
<node CREATED="1463652098063" FOLDED="true" ID="ID_32182777" MODIFIED="1463732444906" TEXT="A light component that has parallel rays">
<node CREATED="1463652108818" FOLDED="true" ID="ID_1259221603" MODIFIED="1463732444906" TEXT="Will provide a uniform lighting across any affected surface ">
<node CREATED="1463652114065" ID="ID_1857742728" MODIFIED="1463652114592" TEXT="(eg. The Sun)"/>
</node>
<node CREATED="1463652120791" ID="ID_290437444" MODIFIED="1463652123355" TEXT="affect all objects in the defined light-mass importance volume."/>
</node>
</node>
<node CREATED="1464785029316" ID="ID_292521733" MODIFIED="1464785032591" TEXT="&#x51fd;&#x6570;"/>
<node CREATED="1464785045157" FOLDED="true" ID="ID_1244990695" MODIFIED="1467367525194" TEXT="variables">
<node CREATED="1464795234139" FOLDED="true" ID="ID_44644872" MODIFIED="1467367525192" TEXT="bEnableLightShaftOcclusion">
<node CREATED="1464795251002" ID="ID_285482608" MODIFIED="1464795259373" TEXT="Whether to occlude fog and atmosphere inscattering&#xa; with screenspace blurred occlusion from this light."/>
</node>
<node CREATED="1464795268282" FOLDED="true" ID="ID_163566561" MODIFIED="1467367525192" TEXT="OcclusionMaskDarkness">
<node CREATED="1464795277258" ID="ID_81274798" MODIFIED="1464795304683" TEXT="Controls how dark the occlusion masking is, &#xa;a value of 1 results in no darkening term."/>
</node>
<node CREATED="1464795313018" FOLDED="true" ID="ID_732782781" MODIFIED="1467367525193" TEXT="OcclusionDepthRange">
<node CREATED="1464795321850" ID="ID_45653368" MODIFIED="1464795338475" TEXT="Everything closer to the camera than this &#xa;distance will occlude light shafts"/>
</node>
<node CREATED="1464795351722" FOLDED="true" ID="ID_1404505513" MODIFIED="1467367525193" TEXT="LightShaftOverrideDirection">
<node CREATED="1464795361162" ID="ID_456372994" MODIFIED="1464795368682" TEXT="Can be used to make light shafts come from&#xa; somewhere other than the light&apos;s actual direction. "/>
<node CREATED="1464795383131" ID="ID_476666252" MODIFIED="1464795384539" TEXT="This will only be used when non-zero. "/>
<node CREATED="1464795389233" ID="ID_691008778" MODIFIED="1464795390411" TEXT=" It does not have to be normalized."/>
</node>
<node CREATED="1464795404697" FOLDED="true" ID="ID_485711670" MODIFIED="1467367525193" TEXT="DynamicShadowDistanceMovableLight">
<node CREATED="1464795422282" ID="ID_1206435423" MODIFIED="1464795428714" TEXT="How far Cascaded Shadow Map dynamic shadows&#xa; will cover for a movable light"/>
<node CREATED="1464795437769" ID="ID_416751237" MODIFIED="1464795439114" TEXT=" measured from the camera."/>
<node CREATED="1464795446058" ID="ID_1133035610" MODIFIED="1464795447377" TEXT=" A value of 0 disables the dynamic shadow."/>
</node>
<node CREATED="1464795453688" FOLDED="true" ID="ID_1017073660" MODIFIED="1464795494272" TEXT="DynamicShadowDistanceStationaryLight">
<node CREATED="1464795467401" ID="ID_986662716" MODIFIED="1464795472465" TEXT="How far Cascaded Shadow Map dynamic &#xa;shadows will cover for a stationary light"/>
<node CREATED="1464795477385" ID="ID_1307064574" MODIFIED="1464795479259" TEXT="measured from the camera"/>
<node CREATED="1464795484216" ID="ID_841547242" MODIFIED="1464795484725" TEXT=" A value of 0 disables the dynamic shadow."/>
</node>
<node CREATED="1464795495049" FOLDED="true" ID="ID_1628761606" MODIFIED="1464795552927" TEXT="DynamicShadowCascades">
<font NAME="SansSerif" SIZE="12"/>
<node CREATED="1464795513097" ID="ID_1109038078" MODIFIED="1464795522167" TEXT="Number of cascades to split the view frustum&#xa; into for the whole scene dynamic shadow. "/>
<node CREATED="1464795531032" ID="ID_1745674842" MODIFIED="1464795541543" TEXT=" More cascades result in better shadow&#xa; resolution, but adds significant rendering cost"/>
</node>
<node CREATED="1464785054417" FOLDED="true" ID="ID_1850587562" MODIFIED="1464795587950" TEXT="CascadeDistributionExponent">
<node CREATED="1464786434425" FOLDED="true" ID="ID_1385031764" MODIFIED="1467367525193" TEXT="Controls whether  the cascades are distributed ">
<node CREATED="1464786453260" FOLDED="true" ID="ID_417001608" MODIFIED="1464786473601" TEXT="closer to the camera">
<node CREATED="1464786440950" ID="ID_1448624372" MODIFIED="1464786441664" TEXT="larger exponent"/>
</node>
<node CREATED="1464786460528" FOLDED="true" ID="ID_1147258277" MODIFIED="1467367525193" TEXT="further from the camera">
<node CREATED="1464786482264" ID="ID_1847975725" MODIFIED="1464786482786" TEXT="smaller exponent"/>
</node>
</node>
<node CREATED="1464786492650" ID="ID_111418237" MODIFIED="1464786510404" TEXT="An exponent of 1 means that cascade&#xa; transitions will happen at a distance &#xa;proportional to their resolution."/>
</node>
<node CREATED="1464795593144" FOLDED="true" ID="ID_386184297" MODIFIED="1464795706084" TEXT="CascadeTransitionFraction">
<node CREATED="1464795599865" ID="ID_368643571" MODIFIED="1464795600774" TEXT="Proportion of the fade region between cascades."/>
<node CREATED="1464795628072" ID="ID_1776197978" MODIFIED="1464795638109" TEXT="Pixels within the fade region of two cascades &#xa;have their shadows blended to avoid hard&#xa; transitions between quality levels."/>
<node CREATED="1464795647943" ID="ID_1328478310" MODIFIED="1464795649207" TEXT="A value of zero eliminates the fade region, creating hard transitions."/>
<node CREATED="1464795665591" ID="ID_815950205" MODIFIED="1464795673275" TEXT="Higher values increase the size of the fade region, &#xa;creating a more gradual transition between cascades."/>
<node CREATED="1464795681957" ID="ID_1204342843" MODIFIED="1464795683022" TEXT="The value is expressed as a percentage proportion (i.e. 0.1 = 10% overlap)."/>
<node CREATED="1464795688261" ID="ID_1506768877" MODIFIED="1464795689685" TEXT="Ideal values are the smallest possible which still hide the transition."/>
<node CREATED="1464795700567" ID="ID_815047052" MODIFIED="1464795702095" TEXT="An increased fade region size causes an increase in shadow rendering cost."/>
</node>
<node CREATED="1464795707015" FOLDED="true" ID="ID_1521301441" MODIFIED="1464795754539" TEXT="ShadowDistanceFadeoutFraction">
<node CREATED="1464795720646" ID="ID_1176263447" MODIFIED="1464795727059" TEXT="Controls the size of the fade out region at the&#xa; far extent of the dynamic shadow&apos;s influence.  "/>
<node CREATED="1464795736249" MODIFIED="1464795736249" TEXT="This is specified as a fraction of DynamicShadowDistance."/>
</node>
<node CREATED="1464786516794" FOLDED="true" ID="ID_427123392" MODIFIED="1464795763634" TEXT="bUseInsetShadowsForMovableObjects">
<node CREATED="1464786523894" FOLDED="true" ID="ID_141292299" MODIFIED="1467367525194" TEXT="Stationary lights only">
<node CREATED="1464786594686" FOLDED="true" ID="ID_1118590829" MODIFIED="1464786625527" TEXT="Whether to use per-object inset shadows for movable components">
<node CREATED="1464786609628" ID="ID_470785260" MODIFIED="1464786610385" TEXT="even though cascaded shadow maps are enabled"/>
</node>
<node CREATED="1464786625807" FOLDED="true" ID="ID_1227377745" MODIFIED="1464786660597" TEXT="This allows dynamic objects to have a shadow&#xa; even when they are outside of the cascaded &#xa;shadow map,">
<node CREATED="1464786645373" ID="ID_385378607" MODIFIED="1464786650739" TEXT="which is important when &#xa;DynamicShadowDistanceStationaryLight is small"/>
</node>
<node CREATED="1464786661057" ID="ID_571120429" MODIFIED="1464786670170" TEXT=" If DynamicShadowDistanceStationaryLight is &#xa;large (currently &gt; 8000), this will be forced off"/>
<node CREATED="1464786677303" ID="ID_300061096" MODIFIED="1464786686743" TEXT=" Disabling this can reduce shadowing cost&#xa; significantly with many movable objects."/>
</node>
</node>
<node CREATED="1464795768855" FOLDED="true" ID="ID_397021507" MODIFIED="1464795826217" TEXT="FarShadowCascadeCount">
<node CREATED="1464795778758" ID="ID_395788427" MODIFIED="1464795780573" TEXT="0: no DistantShadowCascades"/>
<node CREATED="1464795786407" ID="ID_293655029" MODIFIED="1464795819377" TEXT="otherwise the count of cascades between &#xa;WholeSceneDynamicShadowRadius and &#xa;DistantShadowDistance that are covered &#xa;by distant shadow cascades"/>
</node>
<node CREATED="1464795827030" FOLDED="true" ID="ID_1444054816" MODIFIED="1464795896536" TEXT="FarShadowDistance">
<node CREATED="1464795840230" ID="ID_593218823" MODIFIED="1464795858328" TEXT="Distance at which the far shadow cascade should end.&#xa; Far shadows will cover the range between&#xa; &apos;Dynamic Shadow Distance&apos; and this distance. "/>
</node>
<node CREATED="1464795868357" FOLDED="true" ID="ID_894272382" MODIFIED="1464795897775" TEXT="DistanceFieldShadowDistance">
<node CREATED="1464795876165" ID="ID_612912499" MODIFIED="1464795889391" TEXT="Distance at which the ray traced shadow cascade should end.  &#xa;Distance field shadows will cover the range between&#xa; &apos;Dynamic Shadow Distance&apos; this distance. "/>
</node>
<node CREATED="1464795906859" FOLDED="true" ID="ID_120334805" MODIFIED="1464795945302" TEXT="LightSourceAngle">
<node CREATED="1464795913269" ID="ID_718722683" MODIFIED="1464795914561" TEXT=" Light source angle in degrees"/>
<node CREATED="1464795918517" ID="ID_1250367585" MODIFIED="1464795918929" TEXT=" used for dynamic shadowing methods. "/>
<node CREATED="1464795929335" ID="ID_1222741271" MODIFIED="1464795938526" TEXT="Currently only Ray Traced Distance Field shadows&#xa; and Capsule shadows support area shadows, and&#xa; therefore make use of LightSourceAngle."/>
</node>
<node CREATED="1464795946149" FOLDED="true" ID="ID_1836770315" MODIFIED="1464795985197" TEXT="TraceDistance">
<node CREATED="1464795955236" ID="ID_410632222" MODIFIED="1464795956929" TEXT="Determines how far shadows can be cast"/>
<node CREATED="1464795960388" ID="ID_1858262607" MODIFIED="1464795960768" TEXT=" in world units."/>
<node CREATED="1464795968421" ID="ID_419719363" MODIFIED="1464795968865" TEXT="Larger values increase the shadowing cost."/>
</node>
<node CREATED="1464795986036" FOLDED="true" ID="ID_673298530" MODIFIED="1464796000421" TEXT="LightmassSettings">
