<node CREATED="1463655188509" ID="ID_1021804760" LINK="#ID_436558459" MODIFIED="1463655627382" TEXT="StaticMeshActor is an instance of a UStaticMesh in the world"/>
<node CREATED="1463655211721" FOLDED="true" ID="ID_1711002345" MODIFIED="1463732444915" TEXT="Static meshes are geometry that do not animate or otherwise deform">
<node CREATED="1463655230184" ID="ID_1636326937" MODIFIED="1463655234819" TEXT="are more efficient to render than other types of geometry"/>
</node>
<node CREATED="1463655244821" ID="ID_818515051" MODIFIED="1463655252345" TEXT="Static meshes dragged into the level from &#xa;the Content Browser are automatically &#xa;converted to StaticMeshActors."/>
