<node CREATED="1462877706167" ID="ID_1266628473" MODIFIED="1462877708338" TEXT="// Copyright 1998-2015 Epic Games, Inc. All Rights Reserved.&#xa;&#xa;#pragma once&#xa;&#xa;#include &quot;GameFramework/Actor.h&quot;&#xa;#include &quot;FloatingActor.generated.h&quot;&#xa;&#xa;UCLASS()&#xa;class QUICKSTART_API AFloatingActor : public AActor&#xa;{&#xa;    GENERATED_BODY()&#xa;&#xa;public: &#xa;    // Sets default values for this actor&apos;s properties&#xa;    AFloatingActor();&#xa;&#xa;    // Called when the game starts or when spawned&#xa;    virtual void BeginPlay() override;&#xa;&#xa;    // Called every frame&#xa;    virtual void Tick( float DeltaSeconds ) override;&#xa;&#xa;    float RunningTime;&#xa;};"/>
</node>
<node CREATED="1462877815326" ID="ID_1936079716" MODIFIED="1462877816083" TEXT="float RunningTime;"/>
