<node CREATED="1462957432332" FOLDED="true" ID="ID_442453789" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/index.html" MODIFIED="1467367526455" TEXT="Gameplay Programming">
<node CREATED="1463739886815" FOLDED="true" ID="ID_1861685524" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/index.html" MODIFIED="1467367526358" TEXT="Gameplay Programming">
<node CREATED="1463740078831" FOLDED="true" ID="ID_596321538" MODIFIED="1467367525342" TEXT="UObjects and Actors">
<node CREATED="1463740090282" FOLDED="true" ID="ID_277626257" LINK="#ID_223817242" MODIFIED="1464040356506" TEXT="Actors">
<node CREATED="1463740099328" ID="ID_342580048" MODIFIED="1463740100328" TEXT=" instances of classes that derive from the AActor class"/>
<node CREATED="1463740114304" ID="ID_1832777594" MODIFIED="1463740115221" TEXT="can be placed into the world"/>
<node CREATED="1463740312506" ID="ID_26324207" MODIFIED="1463740313286" TEXT="can be thought of as whole items or entities"/>
<node CREATED="1463740318276" FOLDED="true" ID="ID_1078320088" LINK="#ID_1799710925" MODIFIED="1467367525341" TEXT="make use of Components">
<node CREATED="1463740387146" ID="ID_381004424" MODIFIED="1463740391196" TEXT="define certain aspects of their functionality or &#xa;hold values for a collection of properties"/>
<node CREATED="1463740410764" FOLDED="true" ID="ID_177340642" MODIFIED="1467367525341" TEXT="Take a car as an example. ">
<node CREATED="1463740415445" ID="ID_946008731" MODIFIED="1463740416054" TEXT="The car as a whole is an Actor"/>
<node CREATED="1463740424227" ID="ID_1026546275" MODIFIED="1463740427538" TEXT="the parts of the car, like the wheels and doors, &#xa;would all be Components of that Actor."/>
</node>
</node>
</node>
<node CREATED="1463740152569" FOLDED="true" ID="ID_966440537" LINK="#ID_695372018" MODIFIED="1464040359215" TEXT="Objects">
<node CREATED="1463740164236" FOLDED="true" ID="ID_521071296" MODIFIED="1463740251875" TEXT="instances of classes that inherit from the UObject class">
<node CREATED="1463740242040" FOLDED="true" ID="ID_1949257388" MODIFIED="1467367525341" TEXT="UObject">
<node CREATED="1463740173868" ID="ID_1167155276" MODIFIED="1463740239870" TEXT="UObject: base class of all objects "/>
<node CREATED="1463740235221" ID="ID_7879568" MODIFIED="1463740235731" TEXT=" including Actors"/>
</node>
</node>
<node CREATED="1463740273937" ID="ID_333149341" MODIFIED="1463740278550" TEXT="term Objects is used to refer to instances of &#xa;classes that do not inherit from the AActor class"/>
</node>
<node CREATED="1463740437258" FOLDED="true" ID="ID_1886518574" MODIFIED="1463740954149" TEXT="Gameplay Framework Classes">
<node CREATED="1463740448218" FOLDED="true" ID="ID_351341046" MODIFIED="1463740501189" TEXT="The basic gameplay classes include functionality for">
<node CREATED="1463740454731" FOLDED="true" ID="ID_460818512" MODIFIED="1467367525342" TEXT=" representing ">
<node CREATED="1463740467839" ID="ID_617138627" MODIFIED="1463740468416" TEXT="players"/>
<node CREATED="1463740461067" ID="ID_1520399023" MODIFIED="1463740462161" TEXT="allies"/>
<node CREATED="1463740473791" ID="ID_292839296" MODIFIED="1463740474616" TEXT="enemies"/>
</node>
<node CREATED="1463740480755" ID="ID_977526618" MODIFIED="1463740481535" TEXT="controlling these avatars with player input or AI logic"/>
<node CREATED="1463740498026" ID="ID_1256000099" MODIFIED="1463740500343" TEXT="creating heads-up displays and cameras for players"/>
</node>
<node CREATED="1463740518806" FOLDED="true" ID="ID_1757904888" MODIFIED="1467367525342" TEXT="gameplay classes such as GameMode, GameState, &#xa;and PlayerState ">
<node CREATED="1463740533196" ID="ID_1139006176" MODIFIED="1463740533785" TEXT="set the rules of the game, "/>
<node CREATED="1463740540523" ID="ID_390230875" MODIFIED="1463740541189" TEXT="rack how the game and the players are progressing"/>
</node>
<node CREATED="1463740580452" ID="ID_952252430" MODIFIED="1463740581212" TEXT="Representing Players, Friends, and Foes in the World"/>
<node CREATED="1463740599106" ID="ID_897049630" MODIFIED="1463740599645" TEXT="Controlling Pawns With Player Input or AI Logic"/>
<node CREATED="1463740608591" ID="ID_1959370959" MODIFIED="1463740608949" TEXT="Displaying Information to Players"/>
<node CREATED="1463740616601" ID="ID_1413940397" MODIFIED="1463740616982" TEXT="Setting and Tracking the Rules of the Game"/>
</node>
</node>
<node CREATED="1463740622711" FOLDED="true" ID="ID_1266743401" MODIFIED="1463740943078" TEXT="Framework Class Relationships">
<node CREATED="1463740671856" FOLDED="true" ID="ID_68801207" MODIFIED="1467367525343" TEXT=" illustrates how these core gameplay &#xa;classes relate to each other">
<node CREATED="1463740694129" FOLDED="true" ID="ID_46039745" MODIFIED="1467367525343" TEXT="A game is made up of">
<node CREATED="1463740700453" ID="ID_1723287771" MODIFIED="1463740701192" TEXT=" a GameMode"/>
<node CREATED="1463740703809" ID="ID_1279589163" MODIFIED="1463740704955" TEXT="GameState"/>
<node CREATED="1463740721159" FOLDED="true" ID="ID_493321484" MODIFIED="1463740870451" TEXT="PlayerControllers">
<node CREATED="1463740734254" FOLDED="true" ID="ID_884112034" MODIFIED="1467367525343" TEXT="Human players">
<node CREATED="1463740739925" FOLDED="true" ID="ID_36161965" MODIFIED="1467367525343" TEXT="Human players joining the game are &#xa;associated with PlayerControllers">
<node CREATED="1463740768559" FOLDED="true" ID="ID_1205278440" MODIFIED="1467367525342" TEXT=" allow players to possess pawns in the game">
<node CREATED="1463740777965" ID="ID_1430716828" MODIFIED="1463740778678" TEXT="so they can have physical representations in the level."/>
</node>
</node>
<node CREATED="1463740797284" FOLDED="true" ID="ID_1640255585" MODIFIED="1467367525343" TEXT="PlayerControllers give players ">
<node CREATED="1463740809819" ID="ID_604428355" MODIFIED="1463740810835" TEXT="input controls"/>
<node CREATED="1463740828100" ID="ID_1547176700" MODIFIED="1463740828869" TEXT=" a heads-up display,"/>
<node CREATED="1463740838924" ID="ID_224725063" MODIFIED="1463740839975" TEXT="PlayerCameraManager for handling camera views."/>
</node>
</node>
</node>
</node>
</node>
<node CREATED="1463740887226" ID="ID_1274872908" LINK="#ID_1845266539" MODIFIED="1463740931009" TEXT="&#x53c2;&#x8003; Gameplay Framework"/>
</node>
</node>
<node CREATED="1462957928046" FOLDED="true" ID="ID_223817242" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/index.html" MODIFIED="1467367526359" TEXT="Actors">
