<node CREATED="1467018200963" FOLDED="true" ID="ID_1490633826" MODIFIED="1467367525349" TEXT="&#x6b65;&#x9aa4;">
<node CREATED="1467018212099" ID="ID_481856519" MODIFIED="1467018212522" TEXT="Actors in a package/level are loaded from disk."/>
<node CREATED="1467018215260" FOLDED="true" ID="ID_201023105" MODIFIED="1467018879230" TEXT="PostLoad">
<node CREATED="1467018236692" ID="ID_1661715994" MODIFIED="1467018240564" TEXT=" is called by serialized Actor &#xa;after they have finished loading from disk. "/>
<node CREATED="1467018252330" ID="ID_1903256124" MODIFIED="1467018257406" TEXT=" Any custom versioning and &#xa;fixup behavior should go here."/>
