<node CREATED="1467017308546" FOLDED="true" ID="ID_822835852" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/ActorLifecycle/#playineditor" MODIFIED="1467018865133" TEXT="Play in Editor">
<node CREATED="1467018504385" ID="ID_1105575476" MODIFIED="1467018546129" TEXT="The Play in Editor path is mostly &#xa;the same as Load from Disk"/>
<node CREATED="1467018526345" FOLDED="true" ID="ID_1512567431" MODIFIED="1467367525349" TEXT="however">
<node CREATED="1467018530991" ID="ID_1138629157" MODIFIED="1467018531429" TEXT=" the Actors are never loaded from disk,"/>
<node CREATED="1467018536250" ID="ID_234890826" MODIFIED="1467018536807" TEXT=" they are copied from the Editor.  "/>
</node>
<node CREATED="1467018542527" FOLDED="true" ID="ID_1820219512" MODIFIED="1467367525350" TEXT="&#x6b65;&#x9aa4;">
<node CREATED="1467018566947" ID="ID_1843153714" MODIFIED="1467018567484" TEXT="Actors in the Editor are duplicated into a new World "/>
<node CREATED="1467018598019" ID="ID_1509820772" LINK="#ID_1490633826" MODIFIED="1467018619805" TEXT="&#x540c; Load from Disk"/>
</node>
</node>
<node CREATED="1467017316207" FOLDED="true" ID="ID_1898733989" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/ActorLifecycle/#spawning" MODIFIED="1467018921505" TEXT="Spawning">
<node CREATED="1467018636006" FOLDED="true" ID="ID_311093852" MODIFIED="1467367525351" TEXT="When spawning (instancing) an Actor">
<node CREATED="1467018641272" FOLDED="true" ID="ID_39123340" MODIFIED="1467367525351" TEXT="&#x6b65;&#x9aa4;">
<node CREATED="1467018648232" ID="ID_1393378162" MODIFIED="1467018648613" TEXT="SpawnActor called"/>
<node CREATED="1467018651598" ID="ID_1752355294" MODIFIED="1467018651977" TEXT="PostSpawnInitialize"/>
<node CREATED="1467018658009" FOLDED="true" ID="ID_989526047" MODIFIED="1467018738003" TEXT="PostActorCreated">
<node CREATED="1467018662971" ID="ID_731849883" MODIFIED="1467018663507" TEXT="called for spawned Actors after its creation"/>
<node CREATED="1467018671161" ID="ID_91240671" MODIFIED="1467018671597" TEXT="constructor like behavior should go here"/>
<node CREATED="1467018681455" ID="ID_478751832" MODIFIED="1467018682024" TEXT="PostActorCreated is mutually exclusive with PostLoad."/>
</node>
<node CREATED="1467018685291" FOLDED="true" ID="ID_1891891518" MODIFIED="1467367525350" TEXT="ExecuteConstruction">
<node CREATED="1467018745042" FOLDED="true" ID="ID_696208844" MODIFIED="1467018823750" TEXT="OnConstruction">
<node CREATED="1467018788086" ID="ID_442289958" MODIFIED="1467018788441" TEXT="The construction of the Actor"/>
<node CREATED="1467018808922" FOLDED="true" ID="ID_1994810843" MODIFIED="1467367525350" TEXT=" this is where ">
<node CREATED="1467018815200" ID="ID_185644706" MODIFIED="1467018816241" TEXT="Blueprint Actors have their components created"/>
<node CREATED="1467018819913" ID="ID_185145557" MODIFIED="1467018820280" TEXT=" blueprint variables are initialized"/>
</node>
</node>
</node>
<node CREATED="1467018688507" FOLDED="true" ID="ID_1756961515" MODIFIED="1467018898249" TEXT="PostActorConstruction">
<node CREATED="1467018845193" FOLDED="true" ID="ID_280600238" MODIFIED="1467367525350" TEXT="PreInitializeComponents">
<node CREATED="1467018446524" ID="ID_917660355" MODIFIED="1467018455144" TEXT=" Called before InitializeComponent is &#xa;called on the Actor&apos;s components"/>
</node>
<node CREATED="1467018848062" FOLDED="true" ID="ID_1495804526" MODIFIED="1467367525350" TEXT="InitializeComponent">
<node CREATED="1467018463793" ID="ID_852450798" MODIFIED="1467018467677" TEXT="Helper function for the creation of each &#xa;component defined on the Actor"/>
</node>
<node CREATED="1467018851435" FOLDED="true" ID="ID_989604853" MODIFIED="1467367525351" TEXT="PostInitializeComponents">
<node CREATED="1467018472164" ID="ID_774944050" MODIFIED="1467018482977" TEXT="Called after the Actor&apos;s components &#xa;have been initialized"/>
</node>
</node>
<node CREATED="1467018708279" ID="ID_1688316938" MODIFIED="1467018917069" TEXT="OnActorSpawned  broadcast on UWorld"/>
<node CREATED="1467018716149" ID="ID_1581114948" MODIFIED="1467018716479" TEXT="BeginPlay is called."/>
</node>
</node>
</node>
<node CREATED="1467017323599" FOLDED="true" ID="ID_335963850" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/ActorLifecycle/#deferredspawn" MODIFIED="1467019129760" TEXT="Deferred Spawn">
<node CREATED="1467018940248" ID="ID_1730420683" MODIFIED="1467018946494" TEXT="An Actor can be Deferred Spawned by having &#xa;any properties set to &quot;Expose on Spawn.&quot;"/>
<node CREATED="1467018997614" FOLDED="true" ID="ID_1528227962" MODIFIED="1467367525352" TEXT="&#x6b65;&#x9aa4;">
<node CREATED="1467018978568" FOLDED="true" ID="ID_27667163" MODIFIED="1467019004318" TEXT="SpawnActorDeferred">
<node CREATED="1467018986995" ID="ID_774830413" MODIFIED="1467018987462" TEXT="meant to spawn procedural Actors"/>
<node CREATED="1467018995838" ID="ID_1651177652" MODIFIED="1467018996395" TEXT="allows additional setup before Blueprint construction script"/>
</node>
<node CREATED="1467019009923" FOLDED="true" ID="ID_1391681071" MODIFIED="1467367525351" TEXT="Everything in SpawnActor occurs&#xff0c; but &#xa;after PostActorCreated the following occurs:">
<node CREATED="1467019055304" ID="ID_858000797" MODIFIED="1467019094111" TEXT="Do setup / call various &quot;initialization functions&quot;&#xa; with a valid but incomplete Actor instance"/>
<node CREATED="1467019098238" FOLDED="true" ID="ID_298948047" MODIFIED="1467367525351" TEXT="FinishSpawningActor">
<node CREATED="1467019110411" ID="ID_75618868" MODIFIED="1467019110767" TEXT="called to Finalize the Actor"/>
<node CREATED="1467019114866" ID="ID_1305817877" MODIFIED="1467019115324" TEXT="picks up at ExecuteConstruction in the Spawn Actor line."/>
</node>
</node>
</node>
</node>
<node CREATED="1467017331339" FOLDED="true" ID="ID_1306913631" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/ActorLifecycle/#comingtotheendoflife" MODIFIED="1467019657578" TEXT="Coming to the End of Life">
<node CREATED="1467019148224" ID="ID_1460661070" MODIFIED="1467019156314" TEXT="actors end their existence is always the same."/>
<node CREATED="1467017349238" FOLDED="true" ID="ID_1867753162" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/ActorLifecycle/#duringgameplay" MODIFIED="1467019655947" TEXT="During Gameplay">
<node CREATED="1467019174248" ID="ID_171047007" MODIFIED="1467019182989" TEXT="These are completely optional, as many &#xa;Actors will not actually die during play."/>
<node CREATED="1467019192642" FOLDED="true" ID="ID_1725499459" MODIFIED="1467019250482" TEXT="Destroy">
<node CREATED="1467019213489" ID="ID_1511567446" MODIFIED="1467019221410" TEXT=" is called manually by game any time an Actor &#xa;is meant to be removed, but gameplay is still &#xa;occurring. "/>
<node CREATED="1467019232414" ID="ID_110583214" MODIFIED="1467019248006" TEXT="The Actor is marked pending kill and&#xa; removed from Level&apos;s array of Actors."/>
</node>
<node CREATED="1467019195714" FOLDED="true" ID="ID_345391980" MODIFIED="1467019563200" TEXT="EndPlay">
<node CREATED="1467019270473" FOLDED="true" ID="ID_834390268" MODIFIED="1467019481453" TEXT="Called in several places to guarantee &#xa;the life of the Actor is coming to an end. ">
<node CREATED="1467019386217" ID="ID_1516628692" MODIFIED="1467019394907" TEXT="During play, Destroy will fire this,"/>
<node CREATED="1467019400031" ID="ID_816384492" MODIFIED="1467019420711" TEXT="Level Transitions, if a streaming level&#xa; containing the Actor is unloaded"/>
</node>
<node CREATED="1467019430588" FOLDED="true" ID="ID_350180996" MODIFIED="1467019516780" TEXT=" All the places EndPlay is called from:">
<node CREATED="1467019441839" ID="ID_237305339" MODIFIED="1467019442507" TEXT="Explicit call to Destroy"/>
<node CREATED="1467019446598" ID="ID_697416348" MODIFIED="1467019447053" TEXT="Play in Editor Ended"/>
<node CREATED="1467019453236" ID="ID_587249395" MODIFIED="1467019453668" TEXT="Level Transition (seamless travel or load map)"/>
<node CREATED="1467019459650" ID="ID_950125512" MODIFIED="1467019460126" TEXT="A streaming level containing the Actor is unloaded "/>
<node CREATED="1467019468142" ID="ID_1917371308" MODIFIED="1467019468542" TEXT="The lifetime of the Actor has expired"/>
<node CREATED="1467019473847" ID="ID_728633852" MODIFIED="1467019474380" TEXT="Application shut down (All Actors are Destroyed)"/>
</node>
<node CREATED="1467019497214" ID="ID_1058841593" MODIFIED="1467019515000" TEXT="Regardless of how this happens, &#xa;the Actor will be marked RF_PendingKill &#xa;so during the next garbage collection &#xa;cycle it will be deallocated. "/>
<node CREATED="1467019540393" ID="ID_1845219618" MODIFIED="1467019545838" TEXT=" rather than checking for pending kill manually,&#xa; consider using an FWeakObjectPtr as it is cleaner."/>
</node>
<node CREATED="1467019199539" FOLDED="true" ID="ID_160108676" MODIFIED="1467019654856" TEXT="OnDestroy">
<node CREATED="1467019570779" ID="ID_1146796604" MODIFIED="1467019571155" TEXT=" This is a legacy response to Destroy"/>
<node CREATED="1467019634806" ID="ID_1946674204" MODIFIED="1467019652290" TEXT="You should probably move anything here to EndPlay &#xa;as it is called by level transition and &#xa;other game cleanup functions."/>
</node>
</node>
</node>
<node CREATED="1467017339980" FOLDED="true" ID="ID_1071932718" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/ActorLifecycle/#garbagecollection" MODIFIED="1467023770647" TEXT="Garbage Collection">
<node CREATED="1467019688673" FOLDED="true" ID="ID_211649556" MODIFIED="1467020870685" TEXT="The following functions are called &#xa;on the object during its destruction:">
<node CREATED="1467019697066" FOLDED="true" ID="ID_1040002600" MODIFIED="1467020801967" TEXT="BeginDestroy">
<node CREATED="1467019729253" FOLDED="true" ID="ID_829763134" MODIFIED="1467367525352" TEXT="This is the object&apos;s chance to ">
<node CREATED="1467019733393" ID="ID_1638080046" MODIFIED="1467019734354" TEXT="free up memory"/>
<node CREATED="1467019737725" FOLDED="true" ID="ID_630519680" MODIFIED="1467367525352" TEXT="handle other multithreaded resources">
<node CREATED="1467019747761" ID="ID_62880515" MODIFIED="1467019748631" TEXT="ie: graphics thread proxy objects"/>
</node>
</node>
</node>
<node CREATED="1467019700137" ID="ID_1243598375" MODIFIED="1467019700525" TEXT="IsReadyForFinishDestroy"/>
<node CREATED="1467019703399" FOLDED="true" ID="ID_1602190714" MODIFIED="1467367525352" TEXT="FinishDestroy">
<node CREATED="1467020802810" ID="ID_578357730" MODIFIED="1467020803287" TEXT="Finally, the object is really going to be destroyed"/>
<node CREATED="1467020816321" ID="ID_1364700835" MODIFIED="1467020816708" TEXT="this is another chance to free up internal data structures"/>
<node CREATED="1467020840724" ID="ID_1905644179" MODIFIED="1467020841222" TEXT=" This is the last call before memory is freed."/>
</node>
</node>
<node CREATED="1467020875183" FOLDED="true" ID="ID_1273027377" MODIFIED="1467367525353" TEXT="Advanced Garbage Collection">
<node CREATED="1467021421053" FOLDED="true" ID="ID_573423468" MODIFIED="1467022944001" TEXT="The garbage collection process in Unreal Engine&#xa; 4 builds clusters of objects that are all destroyed&#xa; together. ">
<node CREATED="1467021441282" ID="ID_823486788" MODIFIED="1467022037329" TEXT="Clustering reduces the total time and overall &#xa;memory churn associated with garbage collection&#xa;compared to deleting objects indivudally."/>
<node CREATED="1467022793046" FOLDED="true" ID="ID_1014504509" MODIFIED="1467367525352" TEXT="As an object loads, it may create subobjects.">
<node CREATED="1467022845783" ID="ID_947301493" MODIFIED="1467022935359" TEXT="By combining the object and its subobjects &#xa;into a single cluster for the garbage collector, &#xa;the engine can delay freeing the resources &#xa;used by the cluster until the entire object is&#xa;ready to be freed, and can then free all of &#xa;the resources at once."/>
</node>
</node>
<node CREATED="1467022989777" FOLDED="true" ID="ID_1875054192" MODIFIED="1467367525353" TEXT="there are some specific cases in which the &quot;clustering&quot; &#xa;behavior of the garbage collector can be altered to &#xa;improve efficiency in the following ways:">
<node CREATED="1467023014765" FOLDED="true" ID="ID_1208276090" MODIFIED="1467023134335" TEXT="Clustering">
<node CREATED="1467023025440" FOLDED="true" ID="ID_973556654" MODIFIED="1467023116492" TEXT="Turn clustering off.">
<node CREATED="1467023034610" ID="ID_1259994516" MODIFIED="1467023065312" TEXT="In Project Settings &gt; Garbage Collection section &gt; &#xa;Create Garbage Collector UObject Clusters option&#xa; can be set to false. "/>
</node>
<node CREATED="1467023084268" ID="ID_1499215438" MODIFIED="1467023129417" TEXT="For most projects, this will result &#xa;in less efficient garbage collection"/>
<node CREATED="1467023102979" ID="ID_1313610245" MODIFIED="1467023114072" TEXT="it is recommended that this only be done &#xa;in cases where performance testing reveals &#xa;that it is definitely beneficial."/>
</node>
<node CREATED="1467023019523" FOLDED="true" ID="ID_1777171714" MODIFIED="1467367525353" TEXT="Cluster Merging ">
<node CREATED="1467023151176" FOLDED="true" ID="ID_758634610" MODIFIED="1467023345179" TEXT=" If clustering is set to true &#xa; to activate cluster merging">
<node CREATED="1467023164148" ID="ID_377492107" MODIFIED="1467023183678" TEXT="Project Settings &gt; the Garbage Collection section&#xa;&gt; Merge GC Clusters option"/>
</node>
<node CREATED="1467023218227" FOLDED="true" ID="ID_636554089" MODIFIED="1467023345854" TEXT="is not suited for every project. ">
<node CREATED="1467023210870" ID="ID_1890425293" MODIFIED="1467023211302" TEXT=" This behavior is turned off by default,"/>
</node>
<node CREATED="1467023239492" FOLDED="true" ID="ID_74998229" MODIFIED="1467023343278" TEXT="During the process of building an object&apos;s cluster,">
<node CREATED="1467023244633" ID="ID_1792605044" MODIFIED="1467023245075" TEXT="the object will be examined"/>
<node CREATED="1467023249831" ID="ID_1935190904" MODIFIED="1467023250532" TEXT="references to other objects may be found within it"/>
</node>
<node CREATED="1467023268697" FOLDED="true" ID="ID_362551897" MODIFIED="1467023369671" TEXT="Without cluster merging (the default behavior), ">
<node CREATED="1467023301988" ID="ID_1873241778" MODIFIED="1467023326783" TEXT="those references are noted,&#xa;but the object being loaded, &#xa;and its subobjects, remain &#xa;in their original cluster. "/>
</node>
<node CREATED="1467023340655" FOLDED="true" ID="ID_1899451262" MODIFIED="1467023723733" TEXT="With cluster merging, ">
<node CREATED="1467023354324" ID="ID_1850022318" MODIFIED="1467023394724" TEXT="the clusters of the object being loaded and&#xa; the referenced object will be combined."/>
<node CREATED="1467023539081" FOLDED="true" ID="ID_581548350" MODIFIED="1467367525352" TEXT="This behavior is often not desired in games &#xa;that stream content, such as open-world games, &#xa;as many clusters may merge together and form &#xa;bigger, more diverse groups of objects. ">
<node CREATED="1467023573775" ID="ID_913165287" MODIFIED="1467023587714" TEXT="Since objects in clusters will not be destroyed&#xa; indivudally, waiting until every object in the&#xa; group is set for destruction, this can result &#xa;in large clusters of objects that are kept in&#xa; memory due to a small number of those &#xa;objects being in constant use."/>
</node>
<node CREATED="1467023612792" ID="ID_1400649625" MODIFIED="1467023716149" TEXT="However, enabling cluster merging and&#xa;manually adding clusters, in code, for &#xa;special cases like assets with large numbers&#xa;of referenced objects that are not shared &#xa;by other objects, &#xa;can improve performance &#xa;by unifying cleanup of the subobjects and &#xa;decreasing the individual number of entries &#xa;and dependencies the garbage collector &#xa;must maintain and check during the game. "/>
</node>
<node CREATED="1467023419520" FOLDED="true" ID="ID_392088877" MODIFIED="1467023725393" TEXT="example">
<node CREATED="1467023429680" FOLDED="true" ID="ID_1554763532" MODIFIED="1467367525353" TEXT=", a particle system asset may reference a material asset, ">
<node CREATED="1467023434018" FOLDED="true" ID="ID_1375871846" MODIFIED="1467023495237" TEXT=" if cluster merging is turned off,">
<node CREATED="1467023451629" ID="ID_1452886186" MODIFIED="1467023488530" TEXT="the material and the particle system will &#xa;remain in separate clusters for garbage &#xa;collection purposes. "/>
</node>
<node CREATED="1467023493773" FOLDED="true" ID="ID_1850292787" MODIFIED="1467367525353" TEXT="With cluster merging turned on,">
<node CREATED="1467023510165" ID="ID_1728620533" MODIFIED="1467023516803" TEXT=" the particle asset cluster will be merged &#xa;with the material cluster due to the particle&#xa; system referencing the material."/>
</node>
</node>
</node>
</node>
</node>
</node>
</node>
</node>
<node CREATED="1462957969539" FOLDED="true" ID="ID_708517638" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html" MODIFIED="1467017140456" TEXT="Components">
<node CREATED="1464080878322" FOLDED="true" ID="ID_1795613976" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html" MODIFIED="1467367525354" TEXT="Components">
<node CREATED="1464080922482" ID="ID_1825953916" MODIFIED="1464080928637" TEXT="Components are a special type of Object &#xa;designed to be used as sub-objects within &#xa;Actors"/>
<node CREATED="1464080973405" FOLDED="true" ID="ID_1195156424" MODIFIED="1465824227159" TEXT="used in situations">
<node CREATED="1464080983520" ID="ID_1979918794" MODIFIED="1464080990315" TEXT="where it is necessary to have easily swappable&#xa; parts in order to change the behavior or functionality&#xa; of some particular aspect of the owning Actor. "/>
</node>
<node CREATED="1464081000700" FOLDED="true" ID="ID_128712211" MODIFIED="1464083530754" TEXT="For instance">
<node CREATED="1464081020322" ID="ID_823144522" MODIFIED="1464082216149" TEXT="a car is controlled and moves very differently &#xa;from an aircraft, which is controlled and moves&#xa; differently from a boat, etc.&#xa;yet all of these are vehicles that share other commonalities.&#xa; By using a Component to handle the controls and movement,&#xa; the same vehicle can easily be made to behave&#xa; like any one of these specific types."/>
</node>
<node CREATED="1464083718725" FOLDED="true" ID="ID_961239240" MODIFIED="1465824233482" TEXT="Contrary to the default behavior of sub-objects in general, &#xa;Components created as sub-objects within an Actor&#xa; are instanced,">
<node CREATED="1464083747145" FOLDED="true" ID="ID_1824893527" MODIFIED="1467367525354" TEXT="meaning each Actor instance of a particular&#xa; class gets its own unique instances of the &#xa;Components. ">
<node CREATED="1464083901534" FOLDED="true" ID="ID_1932696994" MODIFIED="1464084143592" TEXT="A simple way to visualize this is to &#xa;imagine the vehicle described above. ">
<node CREATED="1464083916363" FOLDED="true" ID="ID_1685456010" MODIFIED="1467367525354" TEXT="A Car class might use Components &#xa;to represent the wheels of the car.">
<node CREATED="1464083969468" ID="ID_1706208836" MODIFIED="1464083975499" TEXT="Four Wheel Components would be created &#xa;as sub-objects in the default properties of &#xa;the class and assigned to a Wheels[] array."/>
<node CREATED="1464083990665" ID="ID_288600724" MODIFIED="1464084005595" TEXT="When a new Car instance is created, &#xa;new instances of the wheel Components &#xa;are created specifically for that Car. "/>
<node CREATED="1464084064135" FOLDED="true" ID="ID_1221867118" MODIFIED="1467367525354" TEXT=" If this were not the case, when one Car in the&#xa; world moved, the wheels of all Cars would turn; ">
<node CREATED="1464084095019" ID="ID_674139891" MODIFIED="1464084095529" TEXT="which is clearly not the desired behavior."/>
</node>
</node>
</node>
<node CREATED="1464084112178" ID="ID_1884187093" MODIFIED="1464084148934" TEXT="Instancing of Components by default simplifies &#xa;the process of quickly adding unique sub-objects&#xa;to Actors."/>
</node>
</node>
<node CREATED="1464084471044" FOLDED="true" ID="ID_190504787" MODIFIED="1465824234314" TEXT="Without Component instancing, all Component &#xa;variables would need to be declared using the &#xa;Instanced property specifier.">
<node CREATED="1464084518321" ID="ID_1610323155" LINK="#ID_1607534233" MODIFIED="1464084590065" TEXT="Instanced"/>
</node>
</node>
<node CREATED="1464080834667" FOLDED="true" ID="ID_712856080" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#actorcomponents" MODIFIED="1467367525358" TEXT="Actor Components">
<node CREATED="1464085679234" FOLDED="true" ID="ID_940137517" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#actorcomponents" MODIFIED="1464085695269" TEXT="Actor Components">
<node CREATED="1464085336717" ID="ID_932106012" MODIFIED="1464085352209" TEXT="ActorComponent is the base class for components&#xa; that define reusable behavior that can be added&#xa; to different types of Actors."/>
<node CREATED="1464085366622" FOLDED="true" ID="ID_100269211" MODIFIED="1464085567090" TEXT=".These are generally used to ">
<node CREATED="1464085423823" FOLDED="true" ID="ID_364608364" MODIFIED="1464085439688" TEXT="associate geometry  with an Actor,">
<node CREATED="1464085387178" FOLDED="true" ID="ID_1804358980" MODIFIED="1467367525354" TEXT="in the form of ">
<node CREATED="1464085395346" ID="ID_144382268" MODIFIED="1464085395812" TEXT="collision geometry "/>
</node>
<node CREATED="1464085402615" ID="ID_515371936" MODIFIED="1464085403193" TEXT="or rendered meshes"/>
</node>
<node CREATED="1464085438134" ID="ID_859847966" MODIFIED="1464085438656" TEXT="control how the Actor moves through the world"/>
<node CREATED="1464085444862" ID="ID_460493581" MODIFIED="1464085445418" TEXT=" play sounds associated with the Actor"/>
<node CREATED="1464085459849" ID="ID_1080476419" MODIFIED="1464085474430" TEXT="provide the ability for the Actor to cast light&#xa; and shadows onto the world"/>
</node>
<node CREATED="1464085666453" FOLDED="true" ID="ID_131862329" MODIFIED="1467367525355" TEXT="types">
<node CREATED="1464085568591" FOLDED="true" ID="ID_766162225" MODIFIED="1467367525355" TEXT="SceneComponents">
<node CREATED="1464085590690" ID="ID_786659382" MODIFIED="1464085591130" TEXT="ActorComponents that have a transform"/>
</node>
<node CREATED="1464085606276" FOLDED="true" ID="ID_1000331768" MODIFIED="1467367525355" TEXT="PrimitiveComponents">
<node CREATED="1464085615407" ID="ID_465013538" MODIFIED="1464085615939" TEXT="that can be rendered"/>
</node>
</node>
</node>
<node CREATED="1464087138056" FOLDED="true" ID="ID_137061189" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#registeringcomponents" MODIFIED="1464092297485" TEXT="Registering Components">
<node CREATED="1464085700161" FOLDED="true" ID="ID_532893323" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#registeringcomponents" MODIFIED="1464087118784" TEXT="Registering Components">
<node CREATED="1464085758994" FOLDED="true" ID="ID_189511291" MODIFIED="1464087112045" TEXT="In order for ActorComponents to be updated &#xa;each frame and affect the scene, they must &#xa;register with the scene">
<node CREATED="1464085782711" FOLDED="true" ID="ID_300600403" MODIFIED="1467367525356" TEXT="Registration is performed by calling &#xa;UActorComponent::RegisterComponent().">
<node CREATED="1464086249993" FOLDED="true" ID="ID_1830425011" MODIFIED="1467367525356" TEXT="This function calls &#xa;UActorComponent::RegisterComponentWithScene() &#xa;to make sure that">
<node CREATED="1464086285512" ID="ID_250088325" MODIFIED="1464086290531" TEXT=" the ActorComponent is contained in the &#xa;owning  Actor&apos;s Components[] array"/>
<node CREATED="1464086330607" ID="ID_538830228" MODIFIED="1464086331106" TEXT="associate it with the scene"/>
<node CREATED="1464086341307" FOLDED="true" ID="ID_1344734930" MODIFIED="1467367525355" TEXT="create the render proxy and physics&#xa; state for the ActorComponent.">
<node CREATED="1464085723879" ID="ID_1494982270" LINK="#ID_389210031" MODIFIED="1464086378514" TEXT="Render State"/>
<node CREATED="1464085731541" ID="ID_81318666" LINK="#ID_1581398575" MODIFIED="1464086383364" TEXT="Physics State"/>
</node>
</node>
</node>
</node>
<node CREATED="1464087013056" ID="ID_1036474900" MODIFIED="1464087059230" TEXT="ActorComponents are automatically registered&#xa; when the owning Actor is spawned, , if they are &#xa;created as sub-objects and added to the &#xa;Components[] array in the default properties of&#xa; the Actor. "/>
<node CREATED="1464087094582" ID="ID_1399150367" MODIFIED="1464087098497" TEXT="They can be registered at any time during play by &#xa;calling UActorComponent::RegisterComponent()"/>
<node CREATED="1464087104122" ID="ID_1707445044" MODIFIED="1464087110109" TEXT="registering a Component is a somewhat heavy &#xa;operation, so it should only be performed when&#xa; necessary."/>
</node>
<node CREATED="1464087156126" FOLDED="true" ID="ID_1607720084" MODIFIED="1464092294864" TEXT="Register Events">
<node CREATED="1464087164980" FOLDED="true" ID="ID_1903564340" MODIFIED="1467367525356" TEXT="When a Component is registered, &#xa;the events below are fired off.">
<node CREATED="1464087173314" FOLDED="true" ID="ID_479656086" MODIFIED="1464087752691" TEXT="UActorComponent::OnRegister()">
<node CREATED="1464087179696" ID="ID_722556294" MODIFIED="1464087743103" TEXT="Event to allow for additional initialization when&#xa; registering a Component, if necessary."/>
</node>
<node CREATED="1464087756641" FOLDED="true" ID="ID_1189591144" MODIFIED="1464092285986" TEXT="UActorComponent::CreateRenderState()">
<node CREATED="1464092279223" ID="ID_926053842" MODIFIED="1464092279667" TEXT="Initializes the render state for the Component."/>
</node>
<node CREATED="1464092274756" FOLDED="true" ID="ID_1413511182" MODIFIED="1464092286909" TEXT="UActorComponent::CreatePhysicsState()">
<node CREATED="1464092283049" ID="ID_929586970" MODIFIED="1464092283513" TEXT="Initializes the physics state for the Component."/>
</node>
</node>
</node>
</node>
<node CREATED="1464085708848" FOLDED="true" ID="ID_1661695758" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#unregisteringcomponents" MODIFIED="1464092501513" TEXT="UnRegistering Components">
<node CREATED="1464092337076" FOLDED="true" ID="ID_1636552857" MODIFIED="1464092414587" TEXT="ActorComponents can be unregistered &#xa;as well to avoid them being updated,&#xa; simulated, or rendered.">
<node CREATED="1464092351174" ID="ID_613429264" MODIFIED="1464092358233" TEXT="calling UActorComponent::UnregisterComponent()."/>
</node>
<node CREATED="1464092412943" FOLDED="true" ID="ID_1731616771" MODIFIED="1464092500928" TEXT="UnRegister Events">
<node CREATED="1464092446854" FOLDED="true" ID="ID_1239974053" MODIFIED="1467367525357" TEXT="The events below are fired off &#xa;when a Component is unregistered.">
<node CREATED="1464092463247" FOLDED="true" ID="ID_817261488" MODIFIED="1467367525356" TEXT="UActorComponent::OnUnRegister()">
<node CREATED="1464092478053" ID="ID_1147216983" MODIFIED="1464092484736" TEXT="Event to allow for additional actions when &#xa;unregistering a Component, if necessary."/>
</node>
<node CREATED="1464092467004" FOLDED="true" ID="ID_605994490" MODIFIED="1467367525357" TEXT="UActorComponent::DestroyRenderState()">
<node CREATED="1464092488945" ID="ID_1642197152" MODIFIED="1464092489521" TEXT="Uninitializes the render state for the Component."/>
</node>
<node CREATED="1464092471989" FOLDED="true" ID="ID_819074582" MODIFIED="1467367525357" TEXT="UActorComponent::DestroyPhysicsState()">
<node CREATED="1464092494637" ID="ID_1617926738" MODIFIED="1464092496741" TEXT="Uninitializes the physics state for the Component."/>
</node>
</node>
</node>
</node>
<node CREATED="1464085716577" FOLDED="true" ID="ID_1363178179" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#updating" MODIFIED="1467367525358" TEXT="Updating">
<node CREATED="1464092562302" FOLDED="true" ID="ID_416835665" MODIFIED="1467367525357" TEXT="ActorComponents have the ability to be updated&#xa; each frame via their TickComponent() function. ">
<node CREATED="1464092663257" ID="ID_570576696" MODIFIED="1464092669041" TEXT="This allows Components to perform&#xa; type-specific, per-frame calculations."/>
<node CREATED="1464092680418" FOLDED="true" ID="ID_503621803" MODIFIED="1467367525357" TEXT="For example">
<node CREATED="1464092689642" ID="ID_368648964" MODIFIED="1464092693883" TEXT="SkeletalMeshComponents use TickComponent() &#xa;to update animations and skeletal controllers"/>
<node CREATED="1464092700421" ID="ID_1509210185" MODIFIED="1464092719750" TEXT="ParticleSystemComponents use it to update&#xa; the emitters of the system and check for&#xa; particle events to handle."/>
</node>
</node>
<node CREATED="1464092737718" FOLDED="true" ID="ID_909835865" MODIFIED="1467367525357" TEXT="In order for a Component to be updated, ">
<node CREATED="1464092744198" ID="ID_542587058" MODIFIED="1464092744697" TEXT=" it must be registered"/>
<node CREATED="1464092750476" ID="ID_525246383" MODIFIED="1464092751055" TEXT="set to tick (bComponentNeverTicks=false)"/>
<node CREATED="1464092757959" ID="ID_1267153023" MODIFIED="1464092758458" TEXT=" have its tick function setup."/>
</node>
</node>
<node CREATED="1464085723879" FOLDED="true" ID="ID_389210031" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#renderstate" MODIFIED="1464093022474" TEXT="Render State">
<node CREATED="1464092809759" ID="ID_1363707911" MODIFIED="1464092815137" TEXT="An ActorComponent must have a render state&#xa; created in order to be rendered."/>
<node CREATED="1464092870312" FOLDED="true" ID="ID_377558442" MODIFIED="1467367525358" TEXT="The render state of an ActorComponent also &#xa;lets the engine know whether something has &#xa;changed about the Component that requires&#xa; its render data to be updated.">
<node CREATED="1464092980585" ID="ID_923675552" MODIFIED="1464092985344" TEXT="When such a change occurs, the &#xa;render state is marked dirty."/>
<node CREATED="1464092997990" ID="ID_671100247" MODIFIED="1464093016116" TEXT="Then, at the end of the current frame, &#xa;all dirty Components have their render &#xa;data updated."/>
</node>
</node>
<node CREATED="1464085731541" FOLDED="true" ID="ID_1581398575" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#physicsstate" MODIFIED="1464093154709" TEXT="Physics State">
<node CREATED="1464093094682" ID="ID_1676272185" MODIFIED="1464093101602" TEXT="In order to be simulated using the physics &#xa;engine, an ActorComponent must have a &#xa;physics state created. "/>
<node CREATED="1464093125612" FOLDED="true" ID="ID_803802774" MODIFIED="1467367525358" TEXT="physics updates happen immediately when changes occur.">
<node CREATED="1464093116645" ID="ID_844099365" MODIFIED="1464093118422" TEXT=" the physics state is never marked dirty "/>
<node CREATED="1464093147371" ID="ID_1996751792" MODIFIED="1464093151523" TEXT="his is important as it keeps issues like &#xa;frame-behind artifacts from happening."/>
</node>
</node>
</node>
<node CREATED="1464080841642" FOLDED="true" ID="ID_1035353114" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#scenecomponents" MODIFIED="1464095164058" TEXT="Scene Components">
<node CREATED="1464093266464" FOLDED="true" ID="ID_511222237" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#scenecomponents" MODIFIED="1464093275164" TEXT="Scene Components">
<node CREATED="1464093167972" FOLDED="true" ID="ID_1562835065" MODIFIED="1467367525359" TEXT="SceneComponents are an extension of &#xa;ActorComponents that have a transform">
<node CREATED="1464093176590" FOLDED="true" ID="ID_897163166" MODIFIED="1467367525358" TEXT="transform">
<node CREATED="1464093179100" ID="ID_1990981412" MODIFIED="1464093179498" TEXT="location"/>
<node CREATED="1464093181620" ID="ID_1123470055" MODIFIED="1464093181911" TEXT="rotation"/>
<node CREATED="1464093184399" ID="ID_130112143" MODIFIED="1464093184780" TEXT="scale"/>
</node>
</node>
<node CREATED="1464093247507" ID="ID_109130781" MODIFIED="1464093256552" TEXT="The addition of a transform means SceneComponents&#xa; can also be attached to one another."/>
</node>
<node CREATED="1464093278886" FOLDED="true" ID="ID_924717419" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#attachment" MODIFIED="1464094444212" TEXT="Attachment">
<node CREATED="1464093355111" ID="ID_1359920364" MODIFIED="1464093362999" TEXT="all attachment is handled on the Component level &#xa;and only SceneComponents can be attached to one&#xa; another. "/>
<node CREATED="1464093376085" FOLDED="true" ID="ID_1682403346" MODIFIED="1464094019144" TEXT="SceneComponents have an AttachParent &#xa;property that points to the Component it &#xa;is attached to.">
<node CREATED="1464093568198" ID="ID_1820818245" MODIFIED="1464093573688" TEXT="This is mainly used when an Actor contains&#xa; multiple Components to keep them all attached, "/>
<node CREATED="1464093635142" ID="ID_1159548817" MODIFIED="1464093679482" TEXT="it can also be used to specify a Component &#xa;belonging to another Actor to attach a Component &#xa;in one Actor to a Component in another Actor."/>
</node>
<node CREATED="1464093870909" FOLDED="true" ID="ID_1966527389" MODIFIED="1467367525359" TEXT="in order to attach one Actor to another,&#xa;">
<node CREATED="1464093946652" FOLDED="true" ID="ID_1823897299" MODIFIED="1464093959299" TEXT=" each of the Actors involved must contain &#xa;at least one   SceneComponent">
<node CREATED="1464093921224" ID="ID_1970376989" MODIFIED="1464093921723" TEXT="as these are what will actually be attached "/>
</node>
<node CREATED="1464093963933" FOLDED="true" ID="ID_503308539" MODIFIED="1467367525359" TEXT="the SceneComponent of the Actor that is being &#xa;attached must be the root Component">
<node CREATED="1464094054021" FOLDED="true" ID="ID_898636252" MODIFIED="1467367525359" TEXT="reason">
<node CREATED="1464094414867" ID="ID_63610939" MODIFIED="1464094440238" TEXT="each Component can only have one parent &#xa;so if the attached Component is not the root Component&#xa; of the Actor being attached, that Component and any of &#xa;its child Components are the only ones that will be attached. &#xa;The root Component would remain unattached and unaffected,&#xa; as would the Actor as a whole since it relies on the root&#xa; Component and its transform."/>
</node>
</node>
</node>
</node>
<node CREATED="1464093286424" FOLDED="true" ID="ID_319666250" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#componenttransforms" MODIFIED="1464095161526" TEXT="Component Transforms">
<node CREATED="1464094521954" FOLDED="true" ID="ID_1934772384" MODIFIED="1464095143786" TEXT="Each SceneComponent has&#xa;">
<node CREATED="1464094637268" FOLDED="true" ID="ID_1698123348" MODIFIED="1464094987213" TEXT=" its own FTransform&#xa;generally for internal use, that describes its &#xa;world-relative location, rotation, and scale">
<node CREATED="1464094498991" FOLDED="true" ID="ID_622905960" MODIFIED="1464094643480" TEXT="The FTransform struct contains ">
<node CREATED="1464094503639" ID="ID_1821255702" MODIFIED="1464094504047" TEXT="a Translation vector"/>
<node CREATED="1464094507677" ID="ID_162721009" MODIFIED="1464094508047" TEXT=" a Rotation quaternion"/>
<node CREATED="1464094512752" ID="ID_1495195359" MODIFIED="1464094512997" TEXT="a Scale3D vector."/>
</node>
</node>
<node CREATED="1464094675713" FOLDED="true" ID="ID_1151962978" MODIFIED="1467367525359" TEXT="RelativeLocation vector, &#xa;RelativeRotation rotator, and &#xa;RelativeScale3D vector &#xa;used which can act as either relative to &#xa;their parent Component or relative to the world.">
<node CREATED="1464094978887" ID="ID_1471445683" MODIFIED="1464094984838" TEXT="These are generally what are used when &#xa;getting or setting the transform for a &#xa;Component."/>
<node CREATED="1464095040879" FOLDED="true" ID="ID_1770152595" MODIFIED="1464095076940" TEXT="forced to be relative to the world ">
<node CREATED="1464095060681" ID="ID_934152886" MODIFIED="1464095067285" TEXT="using the bAbsoluteLocation, bAbsoluteRotation, &#xa;and bAbsoluteScale properties. "/>
<node CREATED="1464095000505" ID="ID_1330526165" MODIFIED="1464095007048" TEXT="By default, RelativeLocation, RelativeRotation, and &#xa;RelativeScale3D are relative to their AttachParent."/>
</node>
</node>
</node>
<node CREATED="1464095131192" FOLDED="true" ID="ID_953219372" MODIFIED="1467367525359" TEXT="Even when using parent-relative transform values, &#xa;it is still possible to set translation and rotation &#xa;using absolute world values ">
<node CREATED="1464095150240" ID="ID_117604010" MODIFIED="1464095150806" TEXT="SceneComponent::SetWorldLocation()"/>
<node CREATED="1464095154381" ID="ID_79396679" MODIFIED="1464095154858" TEXT="SceneComponent::SetWorldRotation()"/>
</node>
</node>
</node>
<node CREATED="1464080846402" FOLDED="true" ID="ID_1349099830" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#primitivecomponents" MODIFIED="1465824189558" TEXT="Primitive Components">
<node CREATED="1464095193377" FOLDED="true" ID="ID_1257997000" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#primitivecomponents" MODIFIED="1464095379843" TEXT="Primitive Components">
<node CREATED="1464095245289" ID="ID_1972357060" MODIFIED="1464095254020" TEXT="PrimitiveComponents are SceneComponents&#xa;that contain or generate some sort of geometry, &#xa;generally to be rendered or used as collision data. "/>
<node CREATED="1464095290958" FOLDED="true" ID="ID_164948295" MODIFIED="1467367525360" TEXT="There are several subclasses for &#xa;the various types of geometry">
<node CREATED="1464095305258" FOLDED="true" ID="ID_1310333065" MODIFIED="1467367525360" TEXT=" the most common">
<node CREATED="1464095307902" FOLDED="true" ID="ID_306604007" MODIFIED="1467367525360" TEXT="CapsuleComponent">
<node CREATED="1464095326860" ID="ID_687090344" MODIFIED="1464095331676" TEXT="generate geometry that is used for collision&#xa; detection, but not rendered;"/>
</node>
<node CREATED="1464095367274" FOLDED="true" ID="ID_1626724675" MODIFIED="1467367525360" TEXT="pre-built geometry that is rendered, but &#xa;can also be used for collision detection.">
<node CREATED="1464095310794" ID="ID_1424682649" MODIFIED="1464095311118" TEXT="StaticMeshComponent"/>
<node CREATED="1464095314162" ID="ID_1249841456" MODIFIED="1464095314494" TEXT="SkeletalMeshComponent"/>
</node>
</node>
</node>
</node>
<node CREATED="1464095173642" FOLDED="true" ID="ID_1927621460" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/Actors/Components/index.html#sceneproxy" MODIFIED="1467367525361" TEXT="Scene Proxy">
<node CREATED="1464095407659" ID="ID_1661937504" MODIFIED="1464095433476" TEXT="SceneProxy of a PrimitiveComponent is an &#xa;FPrimitiveSceneProxy instance that encapsulates &#xa;the scene data that is mirrored in order to render&#xa; the primitive in parallel to the game thread. "/>
<node CREATED="1464095464034" ID="ID_746520954" MODIFIED="1464095472292" TEXT=" subclass of FPrimitiveSceneProxy is created &#xa;for each primitive type to hold the specific &#xa;render data necessary to render that type &#xa;of primitive."/>
<node CREATED="1464095478593" ID="ID_1155055947" LINK="#ID_937670382" MODIFIED="1464096966396" TEXT="&#x53c2;&#x8003; Rendering System Overview"/>
</node>
</node>
</node>
