<node CREATED="1464042384063" ID="ID_645846416" MODIFIED="1464042384446" TEXT="shared pointers (TSharedPtr)"/>
<node CREATED="1464042389807" ID="ID_502213038" MODIFIED="1464042390238" TEXT="weak pointers (TWeakPtr) "/>
</node>
<node CREATED="1464042423997" FOLDED="true" ID="ID_397014758" MODIFIED="1464042581455" TEXT="This implementation is modeled after ">
<node CREATED="1464042431114" ID="ID_545104067" MODIFIED="1464042432213" TEXT="C++0x standard library&apos;s shared_ptr"/>
<node CREATED="1464042439724" ID="ID_1217181927" MODIFIED="1464042440326" TEXT="Boost smart pointers."/>
</node>
<node CREATED="1464042594126" FOLDED="true" ID="ID_673561006" MODIFIED="1464042912826" TEXT="Type">
<node CREATED="1464042606341" FOLDED="true" ID="ID_1728417001" LINK="#ID_1992593087" MODIFIED="1464042770428" TEXT="Shared Pointers &#xff1a; TSharedPtr&#xa;">
<node CREATED="1464042630371" FOLDED="true" ID="ID_235581924" MODIFIED="1467367525459" TEXT="Reference counted non-intrusive &#xa;authoritative smart pointer.">
<node CREATED="1464042647756" ID="ID_618474715" MODIFIED="1464042688610" TEXT="non-intrusive &#x975e;&#x4fb5;&#x5165;&#x5f0f;"/>
</node>
</node>
