<node CREATED="1464042114071" FOLDED="true" ID="ID_280940023" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/SmartPointerLibrary/index.html#smartpointerimplementationdetails" MODIFIED="1467367525467" TEXT="Smart Pointer Implementation Details">
<node CREATED="1464042114071" FOLDED="true" ID="ID_1520462499" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/SmartPointerLibrary/index.html#smartpointerimplementationdetails" MODIFIED="1464043925639" TEXT="Smart Pointer Implementation Details">
<node CREATED="1464043905466" FOLDED="true" ID="ID_1097230822" MODIFIED="1467367525460" TEXT="The various types of smart pointers &#xa;implemented in the Unreal Smart &#xa;Pointer Library all share some general&#xa; characteristics">
<node CREATED="1464043920900" ID="ID_1421657257" MODIFIED="1464043921123" TEXT="in terms of performance, memory, etc"/>
</node>
</node>
<node CREATED="1464042233963" FOLDED="true" ID="ID_927858830" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/SmartPointerLibrary/index.html#performance" MODIFIED="1464096691499" TEXT="Performance">
<node CREATED="1464043967470" FOLDED="true" ID="ID_1324442253" MODIFIED="1464096691498" TEXT="shared pointers are not meant to be used everywhere">
<node CREATED="1464043989680" ID="ID_312769090" MODIFIED="1464043993982" TEXT=" They are great for certain high level&#xa; systems, or tools programming"/>
<node CREATED="1464043999834" ID="ID_596024292" MODIFIED="1464044002461" TEXT="not well-suited for low level engine/rendering paths."/>
</node>
<node CREATED="1464044010935" FOLDED="true" ID="ID_430381749" MODIFIED="1464044062647" TEXT="Some of the general performance &#xa;benefits of shared pointers are">
<node CREATED="1464044029808" ID="ID_1401965478" MODIFIED="1464044031132" TEXT="All operations are constant time."/>
<node CREATED="1464044037084" ID="ID_220591277" MODIFIED="1464044037611" TEXT="Shared pointer deference is just as fast as C++ pointer"/>
<node CREATED="1464044044292" ID="ID_1920723705" MODIFIED="1464044044731" TEXT="Copying shared pointers never allocates memory."/>
<node CREATED="1464044050023" ID="ID_1730885206" MODIFIED="1464044050651" TEXT="Thread-safe versions are lock-free."/>
<node CREATED="1464044055332" ID="ID_222916999" MODIFIED="1464044055747" TEXT="Fast implementation compared to Boost or STL."/>
</node>
<node CREATED="1464044063474" FOLDED="true" ID="ID_1773568487" MODIFIED="1464044176761" TEXT="Performance drawbacks of shared pointers :  ">
<node CREATED="1464044081963" ID="ID_1200599511" MODIFIED="1464044103168" TEXT="Overhead (&#x5f00;&#x9500;) to creating and copying pointers."/>
<node CREATED="1464044113631" ID="ID_1420900115" MODIFIED="1464044114146" TEXT="Reference count housekeeping."/>
<node CREATED="1464044132166" ID="ID_1170710432" MODIFIED="1464044132770" TEXT="Shared pointers use more memory than C++ pointers"/>
<node CREATED="1464044141043" ID="ID_585958613" MODIFIED="1464044141419" TEXT="Extra heap allocation for reference controller."/>
<node CREATED="1464044154955" ID="ID_1078034566" MODIFIED="1464044164691" TEXT="One for each unique object referenced &#xa;by any number of shared pointers."/>
<node CREATED="1464044170726" ID="ID_1037944371" MODIFIED="1464044174884" TEXT="Weak pointer access is a bit slower&#xa; than shared pointer access."/>
</node>
</node>
<node CREATED="1464042260816" FOLDED="true" ID="ID_24870772" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/SmartPointerLibrary/index.html#memoryusage" MODIFIED="1467367525465" TEXT="Memory Usage">
<node CREATED="1464044206983" FOLDED="true" ID="ID_232986677" MODIFIED="1467367525461" TEXT="All shared pointers (TSharedPtr, TSharedRef, TWeakPtr)&#xa; are 8 bytes (when compiling for 32-bit)">
<node CREATED="1464044269820" FOLDED="true" ID="ID_847079689" MODIFIED="1467367525460" TEXT="consisting of">
<node CREATED="1464044221301" ID="ID_1765965984" MODIFIED="1464044221794" TEXT="C++ pointer (uint32)"/>
<node CREATED="1464044225697" ID="ID_43446921" MODIFIED="1464044226160" TEXT="Reference controller pointer (uint32)"/>
</node>
<node CREATED="1464044247739" ID="ID_1055103957" MODIFIED="1464044254506" TEXT="TSharedFromThis is also 8 bytes &#xa;as it embeds weak pointer."/>
</node>
<node CREATED="1464044291859" FOLDED="true" ID="ID_1584685341" MODIFIED="1467367525463" TEXT="The Reference controller object is 12 bytes &#xa;(when compiling for 32-bit)">
<node CREATED="1464044302382" FOLDED="true" ID="ID_156535951" MODIFIED="1467367525463" TEXT="consisting of:">
<node CREATED="1464044305621" ID="ID_1936130845" MODIFIED="1464044306014" TEXT="C++ pointer (uint32)"/>
<node CREATED="1464044309730" ID="ID_641573033" MODIFIED="1464044313444" TEXT="Shared reference count (uint32)"/>
<node CREATED="1464044317753" ID="ID_1610906579" MODIFIED="1464044318164" TEXT="Weak reference count (uint32)"/>
</node>
<node CREATED="1464044345904" ID="ID_138424201" MODIFIED="1464044351424" TEXT="Only one reference controller is created per object,&#xa; no matter how many shared/weak pointers refer &#xa;to an object."/>
</node>
</node>
<node CREATED="1464042270462" FOLDED="true" ID="ID_1045527676" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/SmartPointerLibrary/index.html#reflection" MODIFIED="1464044699070" TEXT="Reflection">
<node CREATED="1464044364775" FOLDED="true" ID="ID_1838042706" MODIFIED="1464044697255" TEXT="Shared pointers are non-intrusive">
<node CREATED="1464044379195" ID="ID_665525250" MODIFIED="1464044661364" TEXT="which means the class itself does not know &#xa;whether it is owned by a shared pointer &#xa;(or reference) &#x8be5; class &#x4e0d;&#x77e5;&#x9053;&#x662f;&#x5426;&#x81ea;&#x5df1;&#x88ab;&#x67d0;&#x4e2a;&#xa;pointer &#x6307;&#x5411;&#xff0c;&#x6216;&#x8005;&#x88ab;&#x67d0;&#x4e2a; reference &#x5f15;&#x7528;"/>
<node CREATED="1464044433416" FOLDED="true" ID="ID_1508094225" MODIFIED="1464044696398" TEXT="sometimes you really want to access the&#xa; current instance as a shared reference">
<node CREATED="1464044453341" FOLDED="true" ID="ID_1327781217" MODIFIED="1467367525466" TEXT="The solution to this is to derive &#xa;the class from TSharedFromThis&lt;&gt;.">
<node CREATED="1464044535166" ID="ID_1319851648" MODIFIED="1464044541229" TEXT="By deriving from TSharedFromThis&lt;&gt;,&#xa; you can use the AsSharedRef() method &#xa;to convert &apos;this&apos; to a shared reference."/>
<node CREATED="1464044556253" ID="ID_370592407" MODIFIED="1464044560685" TEXT="This can be very useful with class factories&#xa; that always return shared references."/>
<node CREATED="1464044562054" FOLDED="true" ID="ID_228290852" MODIFIED="1464044695022" TEXT="code">
<node CREATED="1464044568104" ID="ID_1378108948" MODIFIED="1464044570724" TEXT="class FAnimation : public TSharedFromThis&lt;FMyClass&gt;&#xa;{&#xa;    void Register()&#xa;    {&#xa;        // Access a shared reference to &apos;this&apos;&#xa;        TSharedRef&lt;FMyClass&gt; SharedThis = AsSharedRef();&#xa;&#xa;        // Class a function that is expecting a shared reference&#xa;        AnimationSystem::RegisterAnimation( SharedThis );&#xa;    }&#xa;}"/>
</node>
</node>
</node>
</node>
</node>
<node CREATED="1464042279943" FOLDED="true" ID="ID_1817491272" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/SmartPointerLibrary/index.html#casting" MODIFIED="1464101533531" TEXT="Casting">
<node CREATED="1464044711289" ID="ID_1260987872" MODIFIED="1464044712200" TEXT="cast shared pointers (and references)"/>
<node CREATED="1464044718460" ID="ID_1505039681" MODIFIED="1464044722800" TEXT=" Up-casting is implicit, just like with C++ pointers."/>
<node CREATED="1464044740106" FOLDED="true" ID="ID_388037376" MODIFIED="1464044773436" TEXT="Cast away const (evil, but sometimes necessary):">
<node CREATED="1464044756716" ID="ID_630610209" MODIFIED="1464044757096" TEXT="ConstCastSharedPtr&lt;T&gt;( ... )"/>
</node>
<node CREATED="1464044767202" FOLDED="true" ID="ID_613897418" MODIFIED="1464101533528" TEXT="Static cast (often used to downcast&#xa; to derived class pointers):">
<node CREATED="1464044778231" ID="ID_1408677650" MODIFIED="1464044778826" TEXT="StaticCastSharedPtr&lt;T&gt;( ... )"/>
</node>
<node CREATED="1464044802570" FOLDED="true" ID="ID_1564258899" MODIFIED="1467367525466" TEXT="Dynamic cast is not supported (no RTTI.)">
<node CREATED="1464044809118" ID="ID_1623484215" MODIFIED="1464044809791" TEXT="Instead, you should use the static cast above."/>
</node>
</node>
<node CREATED="1464042288232" FOLDED="true" ID="ID_1479740489" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/SmartPointerLibrary/index.html#threadsafety" MODIFIED="1464044963795" TEXT="Thread Safety">
<node CREATED="1464044830701" ID="ID_574100176" MODIFIED="1464044836432" TEXT="Regular shared pointers are only safe&#xa; to access on a single thread"/>
<node CREATED="1464044843895" FOLDED="true" ID="ID_1313098732" MODIFIED="1464044962696" TEXT="If you need multiple threads to have access, &#xa;use the thread-safe versions of pointer classes:">
<node CREATED="1464044908030" FOLDED="true" ID="ID_463948584" MODIFIED="1467367525466" TEXT="&#x7c7b;&#x578b;">
<node CREATED="1464044850693" ID="ID_160404476" MODIFIED="1464044851070" TEXT="TThreadSafeSharedPtr&lt;T&gt; "/>
<node CREATED="1464044854641" ID="ID_362207701" MODIFIED="1464044855091" TEXT="TThreadSafeSharedRef&lt;T&gt; "/>
<node CREATED="1464044858641" ID="ID_1998801192" MODIFIED="1464044859003" TEXT="TThreadSafeWeakPtr&lt;T&gt; "/>
<node CREATED="1464044862841" ID="ID_1720646108" MODIFIED="1464044863235" TEXT="TThreadSafeSharedFromThis&lt;&gt; "/>
</node>
<node CREATED="1464044875497" ID="ID_337552316" MODIFIED="1464044880647" TEXT="These versions are a bit slower due &#xa;to atomic reference counting, "/>
<node CREATED="1464044887697" FOLDED="true" ID="ID_727114473" MODIFIED="1464044950368" TEXT="behavior is mostly consistent with regular C++ pointers:">
<node CREATED="1464044933216" ID="ID_300697161" MODIFIED="1464044933884" TEXT="Reads and copies are always thread-safe."/>
<node CREATED="1464044937845" ID="ID_1359553485" MODIFIED="1464044938572" TEXT="Writes/resets must be synchronized to be safe."/>
</node>
</node>
<node CREATED="1464044951661" ID="ID_1180234268" MODIFIED="1464044957246" TEXT="If you know your pointer will never be accessed&#xa; by more than one thread, do not use the thread&#xa; safe version."/>
</node>
</node>
<node CREATED="1464042124683" FOLDED="true" ID="ID_252731284" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/SmartPointerLibrary/index.html#usagedetails" MODIFIED="1467367525470" TEXT="Usage Details">
<node CREATED="1464042124683" FOLDED="true" ID="ID_1166459611" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/SmartPointerLibrary/index.html#usagedetails" MODIFIED="1464045012027" TEXT="Usage Details">
<node CREATED="1464044987720" FOLDED="true" ID="ID_1283471871" MODIFIED="1467367525467" TEXT="SharedPointerTesting.h file contains various &#xa;examples of using shared pointers and&#xa; references.">
<node CREATED="1464045002513" ID="ID_746807808" MODIFIED="1464045005598" TEXT="located in [UE4RootLocation]/Engine/Source/Runtime/Core/Public/Templates/"/>
</node>
</node>
<node CREATED="1464042310344" FOLDED="true" ID="ID_1009159126" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/SmartPointerLibrary/index.html#tips" MODIFIED="1467367525468" TEXT="Tips">
<node CREATED="1464045022269" ID="ID_631129320" MODIFIED="1464045027644" TEXT="Usually you should &quot;operator new&quot; when &#xa;passing a C++ pointer to a new shared pointer."/>
<node CREATED="1464045035869" ID="ID_869195896" MODIFIED="1464045043757" TEXT="Use TSharedRef or TSharedPtr when passing &#xa;smart pointers as function parameters, not &#xa;TWeakPtr."/>
<node CREATED="1464045056606" ID="ID_758473388" MODIFIED="1464045060932" TEXT="The &quot;thread-safe&quot; versions of smart pointers &#xa;are a bit slower -- only use them when needed."/>
<node CREATED="1464045073322" ID="ID_1590031397" MODIFIED="1464045080900" TEXT="You can forward declare shared pointers to &#xa;incomplete types, just how you would expect to!"/>
<node CREATED="1464045090824" ID="ID_198110631" MODIFIED="1464045099836" TEXT="Shared pointers of compatible types &#xa;will be converted implicitly (e.g. upcasting)."/>
<node CREATED="1464045116138" ID="ID_1588748645" MODIFIED="1464045121707" TEXT="You can create a typedef to TSharedRef&lt; MyClass &gt;&#xa; to make it easier to type."/>
<node CREATED="1464045126980" ID="ID_1403678105" MODIFIED="1464045188786" TEXT="For best performance, minimize calls to &#xa;TWeakPtr::Pin (or conversions to TSharedRef/TSharedPtr)."/>
<node CREATED="1464045197371" ID="ID_734142028" MODIFIED="1464045209234" TEXT="Your class can return itself as a shared &#xa;reference if you derive from TSharedFromThis."/>
<node CREATED="1464045220023" ID="ID_1362407424" MODIFIED="1464045224042" TEXT="To downcast a pointer to a derived object&#xa; class, use the StaticCastSharedPtr() function."/>
<node CREATED="1464045229081" ID="ID_525381727" MODIFIED="1464045240585" TEXT="const objects are fully supported with shared pointers!"/>
<node CREATED="1464045254487" ID="ID_695681068" MODIFIED="1464045261816" TEXT="You can make a const shared pointer mutable &#xa;using the ConstCastSharedPtr() function."/>
<node CREATED="1464045272000" FOLDED="true" ID="ID_1524364573" MODIFIED="1464045302817" TEXT="Always convert to C++ references in deep stack frames. ">
<node CREATED="1464045278738" ID="ID_1943828931" MODIFIED="1464045286017" TEXT="Shared pointers are best for member&#xa; references, not stack temporaries."/>
</node>
<node CREATED="1464045321327" ID="ID_88169978" MODIFIED="1464045352359" TEXT="Unlike C++ pointers, shared pointers cannot &#xa;be memcpy&apos;d, so be sure to consider this &#xa;when using arrays of shared pointers."/>
</node>
<node CREATED="1464042318363" FOLDED="true" ID="ID_467980671" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/SmartPointerLibrary/index.html#limitations" MODIFIED="1464045492539" TEXT="Limitations">
<node CREATED="1464045414367" ID="ID_299699179" MODIFIED="1464045419854" TEXT="Shared pointers are not compatible with &#xa;Unreal objects (UObject classes)!"/>
<node CREATED="1464045432342" ID="ID_656209206" MODIFIED="1464045450622" TEXT="Currently only types with that have &#xa;regular destructors (no custom deleters)."/>
<node CREATED="1464045462165" FOLDED="true" ID="ID_1030306202" MODIFIED="1467367525469" TEXT="Dynamically-allocated arrays are not supported yet">
<node CREATED="1464045473295" ID="ID_1615008226" MODIFIED="1464045473707" TEXT="e.g. MakeSharable( new int32[20] ))."/>
</node>
<node CREATED="1464045481869" ID="ID_11430772" MODIFIED="1464045488669" TEXT="Implicit conversion of TSharedPtr/TSharedRef&#xa; to bool is not supported yet."/>
</node>
<node CREATED="1464042326895" FOLDED="true" ID="ID_1560438847" LINK="https://docs.unrealengine.com/latest/INT/Programming/UnrealArchitecture/SmartPointerLibrary/index.html#differencesfromotherimplementations" MODIFIED="1467367525469" TEXT="Differences from Other Implementations">
<node CREATED="1464045511805" ID="ID_363051859" MODIFIED="1464045520469" TEXT="Type names and method names are&#xa; more consistent with Unreal&apos;s codebase."/>
<node CREATED="1464045524996" ID="ID_1737073121" MODIFIED="1464045525658" TEXT="Thread-safety features are optional instead of forced."/>
<node CREATED="1464045529973" ID="ID_614927423" MODIFIED="1464045530499" TEXT="TSharedFromThis returns a shared reference, not a shared pointer."/>
<node CREATED="1464045537400" ID="ID_1246538019" MODIFIED="1464045545844" TEXT="Some features were omitted &#xa;(e.g. use_count(), unique(), etc.)."/>
<node CREATED="1464045550867" ID="ID_1203351499" MODIFIED="1464045558380" TEXT="No exceptions are allowed&#xa; (all related features have been omitted)."/>
<node CREATED="1464045563948" ID="ID_1241046621" MODIFIED="1464045573924" TEXT="Custom allocators and custom delete functions &#xa;are not supported yet."/>
<node CREATED="1464045577945" ID="ID_1996772638" MODIFIED="1464045583092" TEXT="Our implementation supports &#xa;non-nullable smart pointers (TSharedRef)."/>
<node CREATED="1464045589627" ID="ID_1591734860" MODIFIED="1464045593931" TEXT="Several other new features added, such as&#xa; MakeShareable and NULL assignment."/>
</node>
</node>
</node>
