<node CREATED="1466157057196" FOLDED="true" ID="ID_859309210" MODIFIED="1466157070282" TEXT="&#x6ce8;&#x610f;">
<node CREATED="1466157044752" ID="ID_650668549" MODIFIED="1466157045240" TEXT="a Work in Progress feature"/>
<node CREATED="1466157051773" ID="ID_1990622529" MODIFIED="1466157052227" TEXT=" bugs and flaws"/>
</node>
<node CREATED="1466157061840" FOLDED="true" ID="ID_1591684520" LINK="https://wiki.unrealengine.com/Light_Propagation_Volumes_GI#Description" MODIFIED="1466425569525" TEXT="Description">
<node CREATED="1466157465991" ID="ID_705692664" MODIFIED="1466157480282" TEXT="Light propagation volumes is a technique to&#xa;approximately achieve global illumination (GI)&#xa;in Real-time."/>
<node CREATED="1466159010936" ID="ID_317523695" MODIFIED="1466161179512" TEXT="It uses &#xa;- lattices and &#xa;- spherical harmonics (SH) &#xa;to represent the spatial and angular distribution &#xa;of light in the scene.[1]"/>
</node>
<node CREATED="1466157082718" FOLDED="true" ID="ID_1503294548" LINK="https://wiki.unrealengine.com/Light_Propagation_Volumes_GI#How_to_enable_it" MODIFIED="1467367525833" TEXT="How to enable it">
<node CREATED="1466419443918" FOLDED="true" ID="ID_1769735630" MODIFIED="1466428748991" TEXT=" edit the &quot;ConsoleVariables.ini&quot; file">
<node CREATED="1466419453446" FOLDED="true" ID="ID_1804763088" MODIFIED="1466419499517" TEXT="located in your engine config folder">
<node CREATED="1466419458745" ID="ID_206778328" MODIFIED="1466419459179" TEXT="C:\Program Files\Unreal Engine\4.0\Engine\Config\"/>
</node>
<node CREATED="1466419488287" ID="ID_841334005" MODIFIED="1466419497626" TEXT="add r.LightPropagationVolume=1 at the bottom, &#xa;right below the [Startup] category."/>
</node>
<node CREATED="1466419511689" ID="ID_232618467" MODIFIED="1466419512082" TEXT="start/restart your Editor."/>
<node CREATED="1466419523635" FOLDED="true" ID="ID_569327060" MODIFIED="1466430894369" TEXT=" change some settings inside the Editor.">
<node CREATED="1466419530938" FOLDED="true" ID="ID_1035417168" MODIFIED="1467367525832" TEXT="place a &quot;Directional Light&quot; in your scene.">
<node CREATED="1466419641977" FOLDED="true" ID="ID_671508008" MODIFIED="1467367525832" TEXT="properties">
<node CREATED="1466419650673" FOLDED="true" ID="ID_955821783" MODIFIED="1467367525831" TEXT="check the &quot;Affect Dynamic Indirect Lighting&quot; &#xa;to activate the use of LPV.">
<node CREATED="1466419740315" ID="ID_41457572" MODIFIED="1466419747413" TEXT="In case you don&apos;t see this option, make sure&#xa; that the &quot;Show All Advanced Details&quot; option&#xa; is checked inside the Details panel."/>
</node>
<node CREATED="1466419796622" FOLDED="true" ID="ID_428211684" MODIFIED="1467367525832" TEXT="increase the value of &quot;Dynamic Shadow Distance StationaryLight&quot; &#xa;(Dynamic Shadow Distance Movable if it is a Movable light) to 10.000.">
<node CREATED="1466419824939" ID="ID_919586038" MODIFIED="1466419827007" TEXT="in  &quot;Cascaded Shadow Maps&quot; category "/>
</node>
</node>
</node>
</node>
<node CREATED="1466419842995" FOLDED="true" ID="ID_93202291" MODIFIED="1466430895527" TEXT="Now you have to disable the use of Lightmass">
<node CREATED="1466419851710" FOLDED="true" ID="ID_1098224393" MODIFIED="1467367525832" TEXT="World Settings.">
<node CREATED="1466420058668" FOLDED="true" ID="ID_883744963" MODIFIED="1467367525832" TEXT="Force No Precomputed Lighting&#xa;check the checkbox to disable the use of Lightmass.">
<node CREATED="1466420132654" ID="ID_880533459" MODIFIED="1466420133142" TEXT=" located under the Lightmass category, "/>
</node>
</node>
</node>
<node CREATED="1466420165380" ID="ID_1155660665" MODIFIED="1466420173098" TEXT="re/build your lighting to invalidate and &#xa;delete all previous existing baked data.&#xa;"/>
</node>
<node CREATED="1466157090532" FOLDED="true" ID="ID_1240273243" LINK="https://wiki.unrealengine.com/Light_Propagation_Volumes_GI#Tweaking" MODIFIED="1466425944438" TEXT="Tweaking">
<node CREATED="1466424641133" FOLDED="true" ID="ID_1898919051" MODIFIED="1467367525835" TEXT="option for you to tweak">
<node CREATED="1466424674382" FOLDED="true" ID="ID_1471254625" MODIFIED="1467367525835" TEXT="To get these options you have to place&#xa; a &quot;PostProcessVolume&quot; in your scene">
<node CREATED="1466424705598" ID="ID_195374821" MODIFIED="1466424720978" TEXT="drag &amp; drop a &quot;PostProcessVolume&quot; from the &#xa;&quot;Volumes&quot; list under the &quot;Modes&quot; tab into your scene."/>
<node CREATED="1466424731450" FOLDED="true" ID="ID_1676972367" MODIFIED="1467367525835" TEXT="properties">
<node CREATED="1466424778016" FOLDED="true" ID="ID_186290153" MODIFIED="1466424800922" TEXT="to make sure that your PP is used in the &#xa;whole level and not just inside its boundaries.">
<node CREATED="1466424744130" FOLDED="true" ID="ID_1235801125" MODIFIED="1467367525834" TEXT="check the &quot;Unbound&quot;">
<node CREATED="1466424756044" ID="ID_1424542458" MODIFIED="1466424756553" TEXT=" &quot;PostProcessVolume&quot; category "/>
</node>
</node>
<node CREATED="1466424801814" FOLDED="true" ID="ID_1317283572" MODIFIED="1466424837778" TEXT="Intensity">
<node CREATED="1466424809370" ID="ID_75280239" MODIFIED="1466424809800" TEXT="How strong the dynamic GI from the LPV should be."/>
<node CREATED="1466424813015" ID="ID_1904375030" MODIFIED="1466424813457" TEXT="0 is off"/>
<node CREATED="1466424819248" ID="ID_562581605" MODIFIED="1466424819802" TEXT="1.0 is normal value"/>
<node CREATED="1466424824883" ID="ID_1344689443" MODIFIED="1466424830834" TEXT="&gt;1  can be used to boost the effect."/>
</node>
<node CREATED="1466424838531" FOLDED="true" ID="ID_1544833891" MODIFIED="1467367525834" TEXT="(DISABLED) Grid Warp Intensity">
<node CREATED="1466425721030" ID="ID_422624615" MODIFIED="1466425721507" TEXT="The strength of the warp offset for reducing light bleeding."/>
<node CREATED="1466425726002" ID="ID_272496913" MODIFIED="1466425726546" TEXT="0 is off,"/>
<node CREATED="1466425731999" ID="ID_1008373849" MODIFIED="1466425732712" TEXT="1.0 is normal value"/>
<node CREATED="1466425741584" ID="ID_1736157860" MODIFIED="1466425747905" TEXT="&gt; 1  boost the effect."/>
</node>
<node CREATED="1466425752744" FOLDED="true" ID="ID_625349504" MODIFIED="1466425778733" TEXT="Size">
<node CREATED="1466425757117" ID="ID_972734079" MODIFIED="1466425757552" TEXT="The size of the LPV volume, in Unreal Units."/>
</node>
<node CREATED="1466425763533" FOLDED="true" ID="ID_272975387" MODIFIED="1466425782930" TEXT="Secondary Occlusion Intensity">
<node CREATED="1466425770688" ID="ID_1134907810" MODIFIED="1466425771199" TEXT="Intensity&#x9;Secondary occlusion strength (bounce light shadows)."/>
<node CREATED="1466425775907" ID="ID_1941085129" MODIFIED="1466425776487" TEXT=" Set to 0 to disable."/>
</node>
<node CREATED="1466425783829" FOLDED="true" ID="ID_1426231879" MODIFIED="1466425835243" TEXT="Geometry Volume Bias">
<node CREATED="1466425798453" ID="ID_379340656" MODIFIED="1466425798921" TEXT="Bias applied to the geometry volume in cell units (not Unreal Units)."/>
<node CREATED="1466425810615" ID="ID_637751293" MODIFIED="1466425811103" TEXT="Increase to reduce darkening due to secondary occlusion."/>
</node>
<node CREATED="1466425835928" FOLDED="true" ID="ID_915361163" MODIFIED="1466425855291" TEXT="Emissive Injection Intensity">
<node CREATED="1466425840968" ID="ID_1938143110" MODIFIED="1466425841546" TEXT="No information is given at this time."/>
<node CREATED="1466425847786" ID="ID_964855925" MODIFIED="1466425848218" TEXT="But i guess it controls the injection of emissive materials."/>
</node>
<node CREATED="1466425856145" FOLDED="true" ID="ID_672907904" MODIFIED="1467367525834" TEXT="Transmission Intensity">
<node CREATED="1466425863199" ID="ID_293235934" MODIFIED="1466425863946" TEXT="How strong the light transmission from the LPV should be"/>
<node CREATED="1466425886790" ID="ID_243170158" MODIFIED="1466425887255" TEXT="0 is off"/>
<node CREATED="1466425731999" ID="ID_879213500" MODIFIED="1466425732712" TEXT="1.0 is normal value"/>
<node CREATED="1466425741584" ID="ID_380218828" MODIFIED="1466425747905" TEXT="&gt; 1  boost the effect."/>
</node>
</node>
</node>
</node>
